mapper.select:
/* kr.go.bai.eip.aep.arp.dao.AEPARP003PMapper.selectAudRprtDvsnCnt */  

    SELECT COUNT(AUD_RPRT_DVSN_CD) AS CNT
      FROM TBBADDED103M
     WHERE AUD_YR = #{audYr}
       AND AUD_NO = #{audNo}
       AND AUD_STEP_CD = #{audStepCd}
       AND RPRT_CD = 'A10600200'
       AND AUD_RPRT_DVSN_CD = '10'
