mapper.select:
/* kr.go.bai.eip.aep.arp.dao.AEPARP012PMapper.selectDocList */
    SELECT (
            SELECT MAX(DECODE(T2.AUD_DOC_CD, 'A10600100', SUBSTR(T2.DTIL_AUD_DOC_NM, 0, 7), T2.DTIL_AUD_DOC_NM))
              FROM TBFDMADM002M T2
             WHERE T1.RPRT_CD = T2.AUD_DOC_CD
           ) AS DOC_KND_NM
          ,T1.RPRT_NM
          ,F_USR_INFO_BY_ID(T1.DOC_WRT_ID, '2') AS WRT_NM
      FROM TBBADDED103M T1
     WHERE T1.AUD_YR = #{audYr} 
       AND T1.AUD_NO = #{audNo}
       AND T1.RPRT_CD IN ('A10600100','A10601100', 'A10601300','A10601320','A10601330','A10601340', 'A10601500', 'A10601700')
     ORDER BY T1.RPRT_CD
