mapper.select:
/*kr.go.bai.eip.aep.cfr.dao.AEPCFR008PMapper.selectMaxIfStrDh*/
SELECT NVL(TO_CHAR(MAX(IF_STR_DH),'YYYYMMDDHH24MI'), '0')
  FROM EA_INF_FNDT_DATA_INFO
 WHERE 1 = 1
   AND TBL_NM = #{tblNm};

/*kr.go.bai.eip.aep.cfr.dao.AEPCFR008PMapper.selectTBDCMACM013C*/
SELECT CASE WHEN TO_CHAR(FRT_REGI_DH, 'YYYYMMDDHH24MISS') > #{maxIfStrDh} then '등록' WHEN TO_CHAR(FNL_MDF_DH,'YYYYMMDDHH24MISS') > #{maxIfStrDh} then '수정' ELSE '기타'
END IF_CHNG_STAT_DVSN , A.TSK_CAT_CD , A.FUNC_CAT_CD , A.CMM_CD_DVSN_CD , A.CD , A.CD_NM , A.USE_YN , A.USR_DFN_TXT_1 , A.USR_DFN_TXT_2 , A.USR_DFN_TXT_3 , 'TBDCMACM013C' AS TBL_NM FROM TBDCMACM013C A WHERE 1=1 AND TO_CHAR(FNL_MDF_DH, 'YYYYMMDDHH24MI') > #{maxIfStrDh}

/*kr.go.bai.eip.aep.cfr.dao.AEPCFR008PMapper.selectTBDCMACM015M*/
SELECT
CASE WHEN TO_CHAR(FRT_REGI_DH, 'YYYYMMDDHH24MISS') > #{maxIfStrDh} then '등록' WHEN TO_CHAR(FNL_MDF_DH,'YYYYMMDDHH24MISS') > #{maxIfStrDh} then '수정' ELSE '기타' END IF_CHNG_STAT_DVSN , F_CODE_NM('1000119', A.ORG_DVSN_CD) AS ORG_DVSN_NM , F_CODE_NM('1000128', A.PUB_DVSN_CD) AS PUB_DVSN_NM , A.ORG_CD , A.ORG_NM , F_CODE_NM('1000001', A.RGN_CD) AS RGN_NM , F_ORG_HRK_NM(A.COPT_SG_CD) AS COPT_SG_NM , A.USE_DVSN_NO , F_CODE_NM('1000144', A.ENFM_DPT_CD) AS ENFM_DPT_NM , A.ORG_DGE_CD , A.ADMN_ORG_DVSN_CD , A.PASA_USE_YN_CD , 'TBDCMACM015M' AS TBL_NM FROM TBDCMACM015M A WHERE 1=1 AND TO_CHAR(FNL_MDF_DH, 'YYYYMMDDHH24MI') > #{maxIfStrDh}
