mapper.select:
/*kr.go.bai.eip.aep.cfr.dao.AEPCFR011QMapper.selectMainList1*/
SELECT T.* FROM ( SELECT A.CMT_YR ,F_CODE_NM('1000789',A.CMT_DVSN_CD) AS CMT_DVSN_NM ,A.CMT_DVSN_CD ,A.CMT_SQ ,A.IF_SEQ ,A.CMT_DY ,F_CODE_NM('1000848',A.IF_WAY_DVSN) AS IF_WAY_DVSN_NM ,A.IF_WAY_DVSN ,A.SBCN_CNT ,A.SR_DVSN ,A.IF_MSG ,TO_CHAR(A.IF_STR_DH, 'YYYY-MM-DD HH24:MI:SS') AS IF_STR_DH ,TO_CHAR(A.IF_END_DH, 'YYYY-MM-DD HH24:MI:SS') AS IF_END_DH ,A.FRT_USR_ID ,A.FNL_USR_ID ,A.FRT_REGI_DH ,A.FNL_MDF_DH ,TO_CHAR(TO_DATE(A.CMT_DY, 'YYYYMMDD' ), 'YYYY"년"MM"월"DD"일"(DY)') ||' ' ||CASE B.TIME_DVSN_CD WHEN 'AM' THEN '오전' WHEN 'PM' THEN '오후' ELSE '' END || DECODE(B.CMT_TIME,NULL,'',B.CMT_TIME||'시') || DECODE(B.CMT_MIN,NULL,'',B.CMT_MIN||'분') AS CMT_DY_NM ,A.CMT_NM FROM EA_INF_SBCN_INFO A LEFT JOIN TBBADDED120M B ON (A.CMT_DY = B.CMT_DY AND A.CMT_DVSN_CD = B.CMT_DVSN_CD AND A.CMT_SQ = B.CMT_SQ) WHERE A.SR_DVSN = '송신'
AND TO_NUMBER(TO_CHAR(A.IF_STR_DH, 'YYYYMMDD')) >= TO_NUMBER(#{schTrnmStDy})
AND TO_NUMBER(TO_CHAR(A.IF_STR_DH, 'YYYYMMDD')) <= TO_NUMBER(#{schTrnmEdDy})
AND TO_NUMBER(A.CMT_DY) >= TO_NUMBER(#{schCfrStDy})
AND TO_NUMBER(A.CMT_DY) <= TO_NUMBER(#{schCfrEdDy})
AND A.CMT_DVSN_CD = #{schCfrGubun}
AND A.CMT_SQ = #{schCfrSq}
AND A.IF_MSG = #{schIfMsg}
AND A.IF_MSG <> 'Success'
AND A.IF_WAY_DVSN = #{schConMean}
AND A.CMT_YR = #{schYr}
) T
INNER JOIN (SELECT DISTINCT C.CMT_YR,C.CMT_DVSN_CD,C.CMT_SQ ,C.CMT_IF_SEQ FROM EA_INF_CMT_SBJ_INFO C WHERE C.CMT_SBJ_NM LIKE '%'||#{schCfrTitle}||'%' ) D ON (T.CMT_YR = D.CMT_YR AND T.CMT_DVSN_CD = D.CMT_DVSN_CD AND T.CMT_SQ = D.CMT_SQ AND T.IF_SEQ = D.CMT_IF_SEQ)
ORDER BY IF_STR_DH DESC

/*kr.go.bai.eip.aep.cfr.dao.AEPCFR011QMapper.selectMainListExt*/
SELECT A.CMT_YR ,F_CODE_NM('1000789',A.CMT_DVSN_CD) AS CMT_DVSN_NM ,A.CMT_DVSN_CD ,A.CMT_SQ ,A.IF_SEQ ,A.CMT_SBJ_SNO ,A.CMT_SBJ_DVSN_CD ,A.CMT_SBJ_NM ,A.TBL_NM ,A.TBL_DESC ,A.IF_WAY_DVSN ,A.SR_CNT ,A.SR_DVSN ,A.IF_MSG ,TO_CHAR(A.IF_STR_DH, 'YYYY-MM-DD HH24:MI:SS') AS IF_STR_DH ,TO_CHAR(A.IF_END_DH, 'YYYY-MM-DD HH24:MI:SS') AS IF_END_DH ,A.FRT_USR_ID ,A.FNL_USR_ID ,A.FRT_REGI_DH ,A.FNL_MDF_DH ,A.CMT_SBJ_NM ,B.CMT_DY FROM EA_INF_CMT_SBJ_INFO A LEFT JOIN TBBADDED120M B ON (A.CMT_YR = SUBSTR(B.CMT_DY,0,4) AND A.CMT_DVSN_CD = B.CMT_DVSN_CD AND A.CMT_SQ = B.CMT_SQ) WHERE A.SR_DVSN = '송신'
AND A.CMT_YR = #{cmtYr}
AND A.CMT_DVSN_CD = #{cmtDvsnCd}
AND A.CMT_SQ = #{cmtSq}
ORDER BY A.IF_STR_DH DESC

/*kr.go.bai.eip.aep.cfr.dao.AEPCFR011QMapper.selectMainList3*/
SELECT A.CMT_YR ,F_CODE_NM('1000789',A.CMT_DVSN_CD) AS CMT_DVSN_NM ,A.CMT_DVSN_CD ,A.CMT_SQ ,A.IF_SEQ ,A.CMT_SBJ_SNO ,A.DOC_ID ,A.FILE_NM ,A.FILE_SIZE ,A.SR_DVSN ,A.IF_MSG ,A.IF_WAY_DVSN
,TO_CHAR(A.IF_STR_DH, 'YYYY-MM-DD HH24:MI:SS') AS IF_STR_DH ,TO_CHAR(A.IF_END_DH, 'YYYY-MM-DD HH24:MI:SS') AS IF_END_DH ,A.FRT_USR_ID ,A.FNL_USR_ID ,A.FRT_REGI_DH ,A.FNL_MDF_DH ,B.CMT_SBJ_NM ,B.CMT_SBJ_DVSN_CD FROM EA_INF_ECM_FILE A INNER JOIN EA_INF_CMT_SBJ_INFO B ON (A.CMT_YR = B.CMT_YR AND A.CMT_SQ = B.CMT_SQ AND A.CMT_DVSN_CD = B.CMT_DVSN_CD AND A.CMT_SBJ_SNO = B.CMT_SBJ_SNO) WHERE A.SR_DVSN = '송신' AND A.SR_DVSN = B.SR_DVSN AND A.CMT_SBJ_SNO = #{cmtSbjSno}
AND A.CMT_YR = #{cmtYr}
AND A.CMT_DVSN_CD = #{cmtDvsnCd}
AND A.CMT_SQ = #{cmtSq}
ORDER BY A.IF_STR_DH DESC
