AND T3.ENFM_MNG_BRDV_CD IN (SELECT DPT_CD FROM TABLE(F_AUTH_DEPT(#{sCnb}, #{dptAuth}, '', #{upSpvBrdvCd})))
AND T3.ENFM_MNG_BRDV_CD = #{spvBrdvCd}
/* 이행관리과 등 권한이 있는 과*/
AND T3.OD_ENFM_MNG_BRDV_CD IN (SELECT DPT_CD FROM TABLE(F_AUTH_DEPT(#{sCnb}, #{dptAuth}, '', #{odUpSpvBrdvCd})))
AND T3.OD_ENFM_MNG_BRDV_CD = #{odSpvBrdvCd}
AND T3.ENFM_MNG_BRDV_CD IN (SELECT DPT_CD FROM TABLE(F_AUTH_DEPT(#{sCnb}, #{dptAuth}, '', #{upSpvBrdvCd})))
AND T3.ENFM_MNG_BRDV_CD = #{spvBrdvCd}
AND T2.TIT_TXT LIKE '%'||#{titTxt}||'%'
AND T3.COPT_OFI_CD = #{coptOfiCd}
AND T4.RLTN_ORG_CD = #{rltnOrgCd}
AND T4.ENFM_MNG_ORG_CD = #{enfmMngOrgCd}
AND T3.ENF_DT >= #{enfDtFrom}
AND #{enfDtTo} >= T3.ENF_DT
AND T2.DSP_RQ_KND_CD = #{dspRqKndCd}
AND T2.DSP_RQ_KND_CD IN ('310','320','391','396')
AND T2.DSP_RQ_KND_CD IN ('510','520','530')
AND T2.DSP_RQ_KND_CD IN ('810','820','830','840','850')
/* 110 변상판정 210 징계문책 310 시정(금액) 320 시정(기타) 391 현지시정회 396 시정(국장전결)회 810 모범(예산절감) 820 모범(업무개선) 830 모범(성실근무) 840 모범(민원해소) 850 모범(기타) */ AND T2.DSP_RQ_KND_CD IN ('110','210','310','320','391','396','810', '820', '830', '840', '850')
/* 610 통보 611 권고 620 통보(인사) 621 통보(징계) 623 통보(비위) 630 통보개선 */ AND T2.DSP_RQ_KND_CD IN ('610','611','620','621','623','630')
/* 510 법령상개선 520 제도상개선 530 행정상개선 */ AND T2.DSP_RQ_KND_CD IN ('510','520','530')
/* 독촉할사항 */ /* 속도개선으로 주석처리 후 아래 코드로 대체 - 2015.05.12 yyh AND T5.ENFM_PRSU_DT IS NULL */ AND NVL(T5.ENFM_PRSU_DT,'N') = 'N' AND TO_CHAR(SYSDATE, 'YYYYMMDD') > T3.HNDL_DDLN_DT
/* 독촉한사항 */
AND T5.ENFM_PRSU_DT IS NOT NULL
/* 미집행 */
AND NVL(T3.DTM_STA_CD,'0') = '1' AND NVL(T3.DTM_YN,'N') <> 'Y'
/* 미확정 */ AND NVL(T3.DTM_STA_CD,'0') = '1' AND NVL(T3.DTM_YN,'N') = 'Y'
/* 전체 */ AND ( (T7.AUD_YR IS NULL AND TO_CHAR(ADD_MONTHS(T3.ENF_DT, T6.MONTH_DDLN), 'YYYYMMDD') < TO_CHAR(SYSDATE, 'YYYYMMDD')) OR (T7.AUD_YR IS NOT NULL AND T7.ENFM_STEP_CD != '30' AND TO_CHAR(ADD_MONTHS(T7.SLF_SEN_DT, #{overDue}), 'YYYYMMDD') < TO_CHAR(SYSDATE, 'YYYYMMDD') ) )
/* 미회보 */ AND T7.AUD_YR IS NULL AND TO_CHAR(ADD_MONTHS(T3.ENF_DT, T6.MONTH_DDLN), 'YYYYMMDD') < TO_CHAR(SYSDATE, 'YYYYMMDD')
/* 회보 */ AND T7.AUD_YR IS NOT NULL AND T7.ENFM_STEP_CD != '30' /* 이행완료 */ AND TO_CHAR(ADD_MONTHS(T7.SLF_SEN_DT, #{overDue}), 'YYYYMMDD') < TO_CHAR(SYSDATE, 'YYYYMMDD')
AND T7.ENFM_STEP_CD = #{enfmStepCd}
AND NVL(T3.MAIN_ENFM_MNG_TG_YN,'N') = #{mainEnfmMngTgYn}
AND T5.ONR_SEN_DT IS NOT NULL AND TO_CHAR(ADD_MONTHS(T5.ONR_SEN_DT, #{enfmPrsuOverDue}), 'YYYYMMDD') < TO_CHAR(SYSDATE, 'YYYYMMDD')
ORDER BY CASE WHEN T7.AUD_YR IS NULL THEN TO_DATE(TO_CHAR(SYSDATE,'YYYYMMDD')) - ADD_MONTHS(T3.ENF_DT, T6.MONTH_DDLN) ELSE TO_DATE(TO_CHAR(SYSDATE,'YYYYMMDD')) - TO_DATE(T7.SLF_SEN_DT) END DESC
