_ID ORDER BY DECODE(H.APVR_CD, 'A1070', 1, '0010600', 2, '0055700', 3, '0055600' ,4 ,'0045900', 5, '0015800' ,6,'0035100', 7, '0058100', 8, '0013600', 9, 99) ASC ) AS RN , H.APVR_CD , H.DOC_ID , H.AUD_YR , H.AUD_NO FROM TBBADDED145M H WHERE H.APVR_CD <> '0010200' GROUP BY H.DOC_ID, H.APVR_CD, H.AUD_YR, H.AUD_NO) WHERE RN = 1 AND ST1.DOC_ID = DOC_ID AND ST1.AUD_YR = AUD_YR AND ST1.AUD_NO = AUD_NO) ) T3 ON ( T1.WRK_YR = T3.AUD_YR AND T1.WRK_NO = T3.AUD_NO AND T1.AFF_SNO = T3.AFF_SNO) LEFT OUTER JOIN TBBADDED111M T4 ON ( T1.WRK_YR = T4.AUD_YR AND T1.WRK_NO = T4.AUD_NO AND TO_CHAR(T1.LEGAL_REQ_SRNO) = T4.REF_ID AND T4.RPRT_CD = 'A10602300' AND T4.RCPT_DPT_CD = '160100' ) LEFT OUTER JOIN TBBADDED103M T5 ON ( T1.WRK_YR = T5.AUD_YR AND T1.WRK_NO = T5.AUD_NO AND TO_CHAR(T1.LEGAL_REQ_SRNO) = T5.REF_ID AND T5.RPRT_CD = 'A10602300' ) LEFT OUTER JOIN TBBADDED103M T6 ON ( T1.WRK_YR = T6.AUD_YR AND T1.WRK_NO = T6.AUD_NO AND TO_CHAR(T1.LEGAL_REQ_SRNO) = T6.REF_ID AND T6.RPRT_CD = 'A10602100' ) WHERE T1.REQ_STAT_CD IN (40, 50, 60) AND T1.LEGAL_DPT_CD = '160100' AND T1.WRK_DVSN_CD = 'A10'
AND T2.AUD_YR = #{schAudYr}
AND T2.AUD_KND_CD = #{schAudYrNoKndCd}
AND T2.AUD_GRN_NO = #{schAudGrnNo}
AND T2.AUD_SBJ_NM LIKE '%'||#{schAudSbjNm}||'%'
