/* 심사청구(D10), 재심의청구(E10) */ UNION ALL SELECT F_CODE_NM('1000828', T1.LEGAL_REQ_DVSN_CD) AS LEGAL_REQ_DVSN_NM , TO_CHAR(T1.LEGAL_REQ_DH, 'YYYY-MM-DD') AS LEGAL_REQ_DH , F_CODE_NM('1000787', T1.WRK_DVSN_CD) AS WRK_DVSN_NM , CASE WHEN T1.REQ_STAT_CD IN ('20', '30', '90') THEN F_CODE_NM('1000830', T1.REQ_STAT_CD) ELSE F_CODE_NM('1000831', T1.LEGAL_DPT_CD) || DECODE(T1.REQ_STAT_CD, '40', ' 수신', '50', ' 접수', '60', ' 회신완료') END || '(' || TO_CHAR(T1.REQ_STAT_CHG_DH, 'YYYY-MM-DD') || ')' AS REQ_STAT_NM , T2.ACP_YR || '-' || DECODE(T2.ACP_DVSN_CD,'1','심사','2','재심','3','재심판정') || '-' || T2.ACP_SRNO AS AUD_YR_NO , T2.TIT_NM AS AUD_SBJ_NM , '' AS AFF_NO , '' AS TITLE_NM , F_DPT_INFO(T2.DPT_CD, 1) AS REQ_DPT_NM , NVL(F_CODE_NM('1000773', T5.APV_STA_CD), '미작성') AS APV_STA_NM , DECODE(T4.DOC_ID, NULL, '미발신', '발신완료') AS SND_YN , TO_CHAR(T4.SND_DH, 'YYYY-MM-DD') AS SND_DH , T1.WRK_DVSN_CD , T1.WRK_YR , T1.WRK_NO , T1.LEGAL_REQ_DVSN_CD , T1.LEGAL_REQ_SRNO , T1.REQ_STAT_CD , NULL AS AFF_SNO , '' AS ACP_KND_CD , DECODE(T5.DOC_ID, NULL, 'Y', 'N') AS OPIN_DOC_WRT_YN , T2.DPT_CD AS RCPT_DPT_CD , T1.AUD_STEP_CD , T1.APVR_CD , F_USR_INFO_BY_ID(T6.DOC_WRT_ID,'2') AS DOC_WRT_NM , T6.DOC_WRT_ID , F_CODE_NM('1000894', T2.DOC_WRIT_KND_CD) AS DOC_TYPE -- 편집기구분 , '' AS REQ_DVSN_CD FROM TBBADDED126M T1 INNER JOIN TBBADFRE012M T2 ON ( T1.WRK_YR = T2.ACP_YR AND T1.WRK_DVSN_CD = DECODE(T2.ACP_DVSN_CD,'1','D10','2','E10') AND T1.WRK_NO = T2.ACP_SRNO ) LEFT OUTER JOIN TBBADFRE111M T4 ON ( T1.WRK_YR = T4.ACP_YR AND T1.WRK_DVSN_CD = DECODE(T4.ACP_DVSN_CD,'1','D10','2','E10') AND T1.WRK_NO = T4.ACP_SRNO AND TO_CHAR(T1.LEGAL_REQ_SRNO) = T4.REF_ID AND T4.RPRT_CD IN('D10200700','E10400500') AND T4.RCPT_DPT_CD = '160100' ) LEFT OUTER JOIN TBBADFRE103M T5 ON ( T1.WRK_YR = T5.ACP_YR AND T1.WRK_DVSN_CD = DECODE(T5.ACP_DVSN_CD,'1','D10','2','E10') AND T1.WRK_NO = T5.ACP_SRNO AND TO_CHAR(T1.LEGAL_REQ_SRNO) = T5.REF_ID AND T5.RPRT_CD IN('D10200700','E10400500') ) LEFT OUTER JOIN TBBADFRE103M T6 ON ( T1.WRK_YR = T6.ACP_YR AND T1.WRK_DVSN_CD = DECODE(T6.ACP_DVSN_CD,'1','D10','2','E10') AND T1.WRK_NO = T6.ACP_SRNO AND TO_CHAR(T1.LEGAL_REQ_SRNO) = T6.REF_ID AND T6.RPRT_CD IN ('D10200500', 'E10400300') ) WHERE T1.REQ_STAT_CD BETWEEN 40 AND 60 AND T1.LEGAL_DPT_CD = '160100' AND T1.WRK_DVSN_CD IN ( 'D10', 'E10' )
AND T2.ACP_YR = #{schAudYr}
AND T2.ACP_DVSN_CD = #{schAudYrNoKndCd}
AND T2.ACP_SRNO = #{schAudGrnNo}
AND T2.TIT_NM LIKE '%'||#{schAudSbjNm}||'%'
AND T1.WRK_DVSN_CD = #{wrkDvsnCd}
AND T1.REQ_STAT_CD = '40'
AND T1.REQ_STAT_CD = '50'
AND T1.REQ_STAT_CD = '60'
AND T1.LEGAL_REQ_DVSN_CD = #{reqDvsnCd}
AND T1.HIDDEN_YN = 'N'
