mapper.select:
/*kr.go.bai.eip.bad.ded.dao.BADDED008PMapper.selectList*/
SELECT DEP_STAD_STN_NM /*출발기준역명*/ , ARV_STAD_STN_NM /*도착기준역명*/ , CHRG_DVSN_CD /*요금구분코드*/ , F_CODE_NM('1000026',CHRG_DVSN_CD) AS CHRG_DVSN_NM , TFE_KND_CD /*교통편종류코드*/ , F_CODE_NM('1000025', TFE_KND_CD) AS TFE_KND_NM , NVL(CHRG,0) AS CHRG /*요금*/ , CHRG_STAD_RMK /*요금기준비고*/ , TO_CHAR(TO_DATE(MDF_DT),'YYYY-MM-DD') /*수정일*/ , SPE_RM_CHRG /*특별실요금*/ FROM TBBADDED016B WHERE 1=1 AND BZT_DVSN_CD = '1' /* 감사출장 조건 추가 - 2017.02.06 */
AND DEP_STAD_STN_NM LIKE #{schDepStadStnNm}||'%'
AND ARV_STAD_STN_NM LIKE #{schArvStadStnNm}||'%'
AND CHRG_DVSN_CD = #{schChrgDvsnCd}
AND TFE_KND_CD = #{schTfeKndCd}
ORDER BY DEP_STAD_STN_NM
