mapper.select:
/* kr.go.bai.eip.bad.fre.dao.BADFRE615Mapper.selectBadfre615QMain */
SELECT A.ACP_NO, A.ACP_YR, A.ACP_DVSN_CD, A.ACP_SRNO, A.DOC_ID, A.RPRT_NM, A.REQR_NM, A.TIT_NM, A.DPT_CD, A.DCS_SBJ, A.JDG_REQ_DVSN_CD, A.JDG_REQ_DVSN_NM, A.DCS_DT, A.ACP_STEP_CD, A.ISU_TXT, A.OPEN_DOC_NTCE_DH, A.OPEN_YN, A.DPT_NM, A.DOC_TYPE, A.IF_SEQ , A.ACP_DVSN_NM, A.ACP_DT, A.ORG_CD, A.ORG_NM, A.DSP_OFC_ACP_DT, A.AGT_NM, A.AMIT_AM, A.RMK, A.VT_KND_CD, A.VT_KND_NM, A.LWS_YO, A.EXEC_PRCS_ORG_NM, A.EXEC_PRCS_ORG_CD , DECODE((SELECT ST.ATCH_FILE_ID FROM EH_INF_SP_HP_001 ST WHERE ST.IF_SEQ = A.IF_SEQ), NULL, 'N', 'Y') AS SND_FILE_YN FROM ( SELECT A.ACP_YR || '-' || DECODE(A.ACP_DVSN_CD,'1','심사','2','재심','3','재심판정') || '-' || DECODE(A.ORG_ACP_SRNO, null, A.ACP_SRNO, A.ORG_ACP_SRNO) AS ACP_NO , A.ACP_YR /*접수년도*/ , A.ACP_DVSN_CD /*접수구분*/ , A.ACP_SRNO /*접수번호*/ , B.DOC_ID /*공개문 DOC_ID*/ , B.RPRT_NM /*공개문 제목*/ , DECODE(A.AGT_NM, NULL, A.REQR_NM, A.REQR_NM || '(代:' || A.AGT_NM || ')' ) AS REQR_NM /*청구인*/ , DECODE(A.ACP_DVSN_CD, '1', A.TIT_NM, C.TIT_NM) AS TIT_NM /*제목*/ , A.DPT_CD /*부서*/ , A.DCS_SBJ /*결정내용*/ , A.JDG_REQ_DVSN_CD /*구분*/ , F_CODE_NM ('1000043', A.JDG_REQ_DVSN_CD) AS JDG_REQ_DVSN_NM /*구분*/ , TO_CHAR(TO_DATE(A.DCS_DT),'YYYY-MM-DD') AS DCS_DT /*결정일자*/ , B.ACP_STEP_CD , A.ISU_TXT , TO_CHAR(B.OPEN_DOC_NTCE_DH,'YYYY-MM-DD') AS OPEN_DOC_NTCE_DH , DECODE(B.OPEN_DOC_NTCE_DH,'','N','Y') AS OPEN_YN , (SELECT DPT_NM FROM TBDCMACM002M WHERE DPT_CD = A.DPT_CD) AS DPT_NM , F_OPEN_EDIT(B.LINK_URL) AS DOC_TYPE -- 편집기구분 , MAX(D.IF_SEQ) AS IF_SEQ , DECODE(A.ACP_DVSN_CD,'1','심사','2','재심','3','재심판정') AS ACP_DVSN_NM , A.ACP_DT , A.ORG_CD , F_ORG_INFO(A.ORG_CD,'1') AS ORG_NM , A.DSP_OFC_ACP_DT , A.AGT_NM , A.AMIT_AM , A.RMK , A.VT_KND_CD , F_CODE_NM('1000045', A.VT_KND_CD) AS VT_KND_NM , E.LWS_YO , CASE WHEN E.EXEC_PRCS_ORG_CD IS NOT NULL THEN F_ORG_INFO(E.EXEC_PRCS_ORG_CD,'1')||'('||E.EXEC_PRCS_ORG_CD||')' ELSE '' END EXEC_PRCS_ORG_NM , E.EXEC_PRCS_ORG_CD from TBBADFRE012M A INNER JOIN TBBADFRE103M B ON ( A.ACP_YR = B.ACP_YR AND A.ACP_DVSN_CD = B.ACP_DVSN_CD AND A.ACP_SRNO = B.ACP_SRNO AND B.RPRT_CD IN ('D10400600', 'E10600300') AND B.APV_STA_CD = '30' ) LEFT OUTER JOIN TBBADFRE112M C ON (C.ACP_YR = A.ACP_YR AND C.ACP_DVSN_CD = A.ACP_DVSN_CD AND C.ACP_SRNO = A.ACP_SRNO) LEFT OUTER JOIN EH_INF_SP_HP_001 D ON (D.SUB2 = A.ACP_YR AND D.SUB1 = DECODE(A.ACP_DVSN_CD,'1','4','2','5') AND D.CATEGORY_SUB = TO_CHAR(A.ACP_SRNO)) LEFT OUTER JOIN TBBADFRE013D E ON (E.ACP_YR = A.ACP_YR AND E.ACP_DVSN_CD = A.ACP_DVSN_CD AND E.ACP_SRNO = A.ACP_SRNO) WHERE 1 = 1
AND A.ACP_YR = #{acpYr}
AND A.ACP_DVSN_CD = #{acpDvsnCd}
AND A.ACP_SRNO = #{acpSrno}
AND A.DPT_CD = #{dptCd}
AND TO_CHAR(TO_DATE(A.DCS_DT),'YYYYMMDD') >= #{dcsDts}
AND TO_CHAR(TO_DATE(A.DCS_DT),'YYYYMMDD') <= #{dcsDte}
AND TO_CHAR(B.OPEN_DOC_NTCE_DH,'YYYYMMDD') >= #{openDocNtceDhs}
AND TO_CHAR(B.OPEN_DOC_NTCE_DH,'YYYYMMDD') <= #{openDocNtceDhe}
AND DECODE(B.OPEN_DOC_NTCE_DH,'','N','Y') = #{openYn}
AND A.REQR_NM LIKE '%'||#{reqrNm}||'%'
AND A.TIT_NM LIKE '%'||#{titNm}||'%'
GROUP BY A.ACP_YR, A.ACP_DVSN_CD, A.ACP_SRNO, A.ORG_ACP_SRNO, B.DOC_ID, B.RPRT_NM, A.AGT_NM, A.REQR_NM, A.TIT_NM, C.TIT_NM, A.DPT_CD , A.DCS_SBJ, A.JDG_REQ_DVSN_CD, A.DCS_DT, B.ACP_STEP_CD, A.ISU_TXT, B.OPEN_DOC_NTCE_DH, B.LINK_URL, A.ACP_DT, A.ORG_CD, A.DSP_OFC_ACP_DT , A.AMIT_AM, A.RMK, A.VT_KND_CD, E.LWS_YO, E.EXEC_PRCS_ORG_CD ) A ORDER BY A.OPEN_DOC_NTCE_DH DESC, A.ACP_YR DESC, TO_NUMBER(A.ACP_SRNO) DESC

