mapper.select:
/* kr.go.bai.eip.bad.fre.dao.BADFRE615Mapper.getSrnoByKey */
SELECT COUNT(SRNO) AS CNT 
  FROM IS_JDG_REQ_OPEN_DOC
 WHERE ACP_YR = #{acpYr} 
   AND ACP_DVSN_CD = #{acpDvsnCd}
   AND ACP_SRNO = #{acpSrno}
   AND DATA_HNDL_DVSN_CD != 'D'
   AND SRNO = (SELECT MAX(SRNO)
                 FROM IS_JDG_REQ_OPEN_DOC
                WHERE ACP_YR = #{acpYr} 
                  AND ACP_DVSN_CD = #{acpDvsnCd}
                  AND ACP_SRNO = #{acpSrno})

/* kr.go.bai.eip.bad.fre.dao.BADFRE615Mapper.getMaxSrnoByKey */
