Y D.ACP_YR, D.ACP_SRNO, D.ACP_DVSN_CD, D.TIT_NM, D.DPT_CD, A.SBCN_PAGE_CNT, A.SBCN_REGI_REQ_DT , A.ACP_YR, A.ACP_SRNO,A.ACP_DVSN_CD,A.SBCN_PRSS_DPT_NM, D4.CMT_DY , D4.CMT_SQ, A.SBCN_RPTR, C.REF_ID , C.DOC_ID,C.RPT_DOC_ID,C.RPT_RPRT_NM,C.BAICHF_DOC_ID, C.BAICHF_RPRT_NM,C.EDT_AUTH, C.REF_DY, A.DOC_ID, D.MG_DIV_CD, D.DOC_WRIT_KND_CD /* 실지감사 수기 */ UNION ALL SELECT DECODE(A.REL_TSK_NM,NULL,DECODE(A.WORK_DVSN_CD,'D10','('||REL_AUD_YR||'-심사-'||REL_AUD_NO||')'||A.CMT_SBJ_TIT /* 심사청구 */ ,'E10','('||REL_AUD_YR||'-재심-'||REL_AUD_NO||')'||A.CMT_SBJ_TIT /* 재심의청구 */ ,'A10','('||(SELECT F_MNG_KND_AUDYRNO(ST1.AUD_YR, ST1.AUD_NO, ST1.AUD_KND_CD, '-') FROM TBBADDED001M ST1 WHERE ST1.AUD_YR = A.REL_AUD_YR AND ST1.AUD_NO = A.REL_AUD_NO)||')'||A.CMT_SBJ_TIT,A.CMT_SBJ_TIT) ,'('||A.REL_TSK_NM||')'||A.CMT_SBJ_TIT) AS TITLE, A.SBCN_SNO AS SBCN_PAGE_CNT, DECODE(A.BFR_SBCN_CNT,NULL,(SELECT TO_CHAR(COUNT(1)) FROM TBBADDED122M ST1 WHERE ST1.CMT_DY = A.CMT_DY AND ST1.CMT_DVSN_CD = A.CMT_DVSN_CD AND ST1.CMT_SQ = A.CMT_SQ AND ST1.CMT_SBJ_DVSN_CD = A.CMT_SBJ_DVSN_CD AND ST1.CMT_SBJ_SNO = A.CMT_SBJ_SNO),A.BFR_SBCN_CNT)||'건' AS DTL_CNT, F_DPT_INFO(A.REL_DPT_CD,'1') AS MNG_DPT_NM, '안건추가' AS ADD_YN_NM, 'Y' AS ADD_YN, A.CMT_DY AS CMT_DY, A.CMT_DVSN_CD AS CMT_DVSN_CD, TO_CHAR(A.CMT_SQ) AS CMT_SQ, A.CMT_SBJ_DVSN_CD AS CMT_SBJ_DVSN_CD, TO_CHAR(A.CMT_SBJ_SNO) AS CMT_SBJ_SNO, A.REL_AUD_YR AS AUD_YR, A.REL_AUD_NO AS AUD_NO, '' AS AUD_KND_CD, '' AS AUD_GRN_NO, '' AS AUD_MAIN_STEP_SNO, '' AS SBCN_REGI_REQ_DT, A.CMT_ADD_YN AS CMT_ADD_YN, SBCN_RPTR AS SBCN_RPTR, C.REF_ID, C.DOC_ID, C.RPT_DOC_ID, C.RPT_RPRT_NM, C.BAICHF_DOC_ID, C.BAICHF_RPRT_NM, C.EDT_AUTH, DECODE(A.WORK_DVSN_CD,'A10','실지감사' ,'D10','심사청구' ,'E10','재심의청구' ,'Z90','기타안건','기타안건') AS WORK_DVSN_NM, A.WORK_DVSN_CD, A.PRSS_DPT_NM AS PRSS_DPT_NM, A.REL_TSK_NM, 0 AS CAM_CNT, A.REGI_NO, NULL AS REF_DOC_ID , 'Y' AS SBCN_RPRT_SEND_YN , '' AS MG_DIV_CD , NVL((SELECT F_CODE_NM('1000894', AA.DOC_WRIT_KND_CD) FROM TBBADDED001M AA WHERE AA.AUD_YR = A.REL_AUD_YR AND AA.AUD_NO = A.REL_AUD_NO),'hancom') AS DOC_TYPE FROM TBBADDED121M AS A LEFT OUTER JOIN ( SELECT CMT_DY, CMT_DVSN_CD, CMT_SQ, CMT_SBJ_DVSN_CD, CMT_SBJ_SNO, MAX(REF_ID) AS REF_ID, MAX(DOC_ID) AS DOC_ID, MAX(BAICHF_DOC_ID) AS BAICHF_DOC_ID, MAX(BAICHF_RPRT_NM) AS BAICHF_RPRT_NM, MAX(RPT_DOC_ID) AS RPT_DOC_ID, MAX(RPT_RPRT_NM) AS RPT_RPRT_NM, '' AS EDT_AUTH FROM ( SELECT ST1.CMT_DY, ST1.CMT_DVSN_CD, ST1.CMT_SQ, ST1.CMT_SBJ_DVSN_CD, ST1.CMT_SBJ_SNO, /* CMT_SBJ_DTL_SNO - 1 : 부의안, 2 : 부의안요약(보고자용), 3 : 부의안요약(원장용) 기타안건(CMT_SBJ_DVSN_CD:90)일 경우 1 로 대체*/ DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,1), ST2.DOC_ID, NULL) AS REF_ID, DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,3), ST2.DOC_ID, NULL) AS DOC_ID, DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,2), ST2.DOC_ID, NULL) AS RPT_DOC_ID, DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,2), ST2.FILE_NM, NULL) AS RPT_RPRT_NM, DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,3), ST2.DOC_ID, NULL) AS BAICHF_DOC_ID, DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,3), ST2.FILE_NM, NULL) AS BAICHF_RPRT_NM, '' AS EDT_AUTH FROM TBBADDED121M ST1 INNER JOIN TBBADDED122M ST2 ON (ST1.CMT_DY = ST1.CMT_DY AND ST1.CMT_DVSN_CD = ST2.CMT_DVSN_CD AND ST1.CMT_SQ = ST2.CMT_SQ AND ST1.CMT_SBJ_DVSN_CD = ST2.CMT_SBJ_DVSN_CD AND ST1.CMT_SBJ_SNO = ST2.CMT_SBJ_SNO) WHERE ST1.CMT_SBJ_DVSN_CD IN ('10','90') ) GROUP BY CMT_DY, CMT_DVSN_CD, CMT_SQ, CMT_SBJ_DVSN_CD,CMT_SBJ_SNO) C ON (A.CMT_DY = C.CMT_DY AND A.CMT_DVSN_CD = C.CMT_DVSN_CD AND A.CMT_SQ = C.CMT_SQ AND A.CMT_SBJ_DVSN_CD = C.CMT_SBJ_DVSN_CD AND A.CMT_SBJ_SNO = C.CMT_SBJ_SNO) WHERE 1=1 ) T WHERE T.CMT_DVSN_CD = '10' ORDER BY T.SBCN_PAGE_CNT, T.DTL_CNT DESC

SELECT 'TBBADDED120M' AS TBL_NM , (SELECT COMMENTS FROM ALL_TAB_COMMENTS WHERE TABLE_NAME = 'TBBADDED120M' AND OWNER = 'UBCREOWN') AS TBL_DESC , SUBSTR(CMT_DY, 0, 4) AS CMT_YR , CMT_DY , CMT_DVSN_CD , CMT_SQ , TIME_DVSN_CD , CMT_TIME , CMT_MIN , CMT_TIT , CHF_CMTR_ID , CMTR1_ID , CMTR2_ID , CMTR3_ID , CMTR4_ID , CMTR5_ID , CMTR6_ID , CMT_STR_TXT , CMT_END_TXT , CMT_END_YN , TO_CHAR(CMT_END_DH, 'YYYY/MM/DD HH24:MI:SS') AS CMT_END_DH , FRT_USR_ID , FNL_USR_ID , FNL_DEAL_DPT_CD , FNL_MNU_ID , TO_CHAR(FRT_REGI_DH, 'YYYY/MM/DD HH24:MI:SS') AS FRT_REGI_DH , TO_CHAR(FNL_MDF_DH, 'YYYY/MM/DD HH24:MI:SS') AS FNL_MDF_DH , CMT_SBCN_YN , SBMT_DVSN_NO , (SELECT COUNT(AUD_YR) FROM ( SELECT T2.CMT_SQ /*회의 회차*/ ,T2.CMT_TIT /*회의 안건명*/ ,T2.CMT_DY /*회의 일자*/ ,T2.CMT_END_YN /*회의 종료여부*/ ,T2.CMT_DVSN_CD /*회의 구분코드*/ ,REPLACE(T2.CMT_STR_TXT,'|',' ') AS CMT_STR_TXT /*회의 개회선언사*/ ,REPLACE(T2.CMT_END_TXT,'|',' ') AS CMT_END_TXT /*회의 폐회사*/ ,F_USR_INFO(T2.CHF_CMTR_ID, '2') AS CHF_CMTR_NM /*회의 의장*/ ,F_USR_INFO(T2.CMTR1_ID, '2') AS CMTR1_NM /*회의 위원1*/ ,F_USR_INFO(T2.CMTR2_ID, '2') AS CMTR2_NM /*회의 위원2*/ ,F_USR_INFO(T2.CMTR3_ID, '2') AS CMTR3_NM /*회의 위원3*/ ,F_USR_INFO(T2.CMTR4_ID, '2') AS CMTR4_NM /*회의 위원4*/ ,F_USR_INFO(T2.CMTR5_ID, '2') AS CMTR5_NM /*회의 위원5*/ ,F_USR_INFO(T2.CMTR6_ID, '2') AS CMTR6_NM /*회의 위원6*/ ,DECODE(T2.CMT_END_YN, 'Y', '종료', '진행중') AS CMT_END_NM /*회의종료구분*/ ,SUBSTR(CMT_DY,0,4)||'년 '||SUBSTR(CMT_DY, 5, 2)||'월 ' ||SUBSTR(CMT_DY, 7, 2)||'일 ' AS CMT_DATA /*회의 날짜*/ ,T1.AUD_SBJ_NM /*회의 안건명*/ ,T1.CMT_SBJ_DVSN_CD /*안건구분코드*/ ,T1.CMT_SBJ_DVSN_NM /*안건구분코코드*/ ,T1.SBCN_PAGE_CNT /*부의순번*/ ,T1.AUD_YR_NO /*감사연번호*/ ,T1.CNT /*건수*/ ,T1.DPT_NM /*관련부서명*/ ,T1.CMT_ADD_YN /*회의추가여부*/ ,T1.SBCN_DY /*회의일자*/ ,T1.AUD_YR /*감사년도*/ ,T1.AUD_NO /*감사번호*/ ,T1.DOC_ID /*부의안 요약 DOCID*/ ,T1.REF_ID /*부의안 DOCID*/ ,T1.RPT_DOC_ID ,T1.RPT_RPRT_NM ,T1.BAICHF_DOC_ID ,T1.BAICHF_RPRT_NM ,T1.CMT_SBJ_SNO ,T1.DPT_CD ,T1.SBCN_RPTR ,T1.ACP_DVSN_CD ,T1.SBCN_RPRT_SEND_YN ,T1.MG_DIV_CD FROM ( SELECT A.AUD_YR ,A.AUD_NO ,B.AUD_SBJ_NM||(SELECT REL_TEXT1 FROM TBBADDED121T F WHERE F.AUD_YR = A.AUD_YR AND F.AUD_NO = A.AUD_NO AND F.AUD_YR_DY = C.REF_DY) AS AUD_SBJ_NM ,'10' AS CMT_SBJ_DVSN_CD /*안건구분코드*/ ,F_CODE_NM('1000789', '10') AS CMT_SBJ_DVSN_NM /*안건구분코드명*/ ,A.SBCN_PAGE_CNT ,DECODE(REL_TSK_NM,NULL,F_MNG_KND_AUDYRNO(A.AUD_YR, A.AUD_NO, B.AUD_KND_CD, '-'),REL_TSK_NM )AS AUD_YR_NO ,COUNT(A.DSP_RQ_SNO) AS CNT ,NVL(SBCN_PRSS_DPT_NM, F_DPT_INFO(B.MNG_DPT_CD, 1)) AS DPT_NM ,'N' AS CMT_ADD_YN /*N : 하드코딩*/ ,A.SBCN_DY ,'' AS CMT_SBJ_SNO ,'10' as CMT_DVSN_CD ,SBCN_REGI_REQ_DT as SBCN_REGI_REQ_DT ,B.MNG_DPT_CD AS DPT_CD ,A.SBCN_RPTR ,C.REF_ID ,C.DOC_ID ,C.RPT_DOC_ID ,C.RPT_RPRT_NM ,C.BAICHF_DOC_ID ,C.BAICHF_RPRT_NM ,'9' AS ACP_DVSN_CD , (SELECT DECODE(COUNT(1),0,'N','Y') FROM TBBADDED111M ST1 WHERE ST1.AUD_YR = A.AUD_YR AND ST1.AUD_NO = A.AUD_NO AND ST1.DOC_ID = C.DOC_ID) AS SBCN_RPRT_SEND_YN ,'' AS MG_DIV_CD FROM TBBADDED113M A INNER JOIN TBBADDED001M B ON (B.AUD_YR = A.AUD_YR AND B.AUD_NO = A.AUD_NO) LEFT OUTER JOIN (SELECT AUD_YR, AUD_NO, REF_DY, REF_DOC_ID, MAX(REF_ID) AS REF_ID, MAX(DOC_ID) AS DOC_ID, MAX(BAICHF_DOC_ID) AS BAICHF_DOC_ID, MAX(BAICHF_RPRT_NM) AS BAICHF_RPRT_NM, MAX(RPT_DOC_ID) AS RPT_DOC_ID, MAX(RPT_RPRT_NM) AS RPT_RPRT_NM, MAX(EDT_AUTH) AS EDT_AUTH FROM ( SELECT AUD_YR, AUD_NO, REF_DY, REF_DOC_ID, DECODE(ST1.RPRT_CD, 'A10700500', DOC_ID, NULL) AS REF_ID, DECODE(ST1.RPRT_CD, 'A10700600', DOC_ID, NULL) AS DOC_ID, DECODE(ST1.RPRT_CD, 'A10700700', DOC_ID, NULL) AS RPT_DOC_ID, DECODE(ST1.RPRT_CD, 'A10700700', RPRT_NM, NULL) AS RPT_RPRT_NM, DECODE(ST1.RPRT_CD, 'A10700800', DOC_ID, NULL) AS BAICHF_DOC_ID, DECODE(ST1.RPRT_CD, 'A10700800', RPRT_NM, NULL) AS BAICHF_RPRT_NM, DECODE(ST1.RPRT_CD, 'A10700600', EDT_AUTH, NULL) AS EDT_AUTH FROM TBBADDED103M ST1 WHERE RPRT_CD IN ('A10700500', 'A10700600', 'A10700700', 'A10700800') ) GROUP BY AUD_YR, AUD_NO, REF_DY, REF_DOC_ID) C ON (A.AUD_YR = C.AUD_YR AND A.AUD_NO = C.AUD_NO AND C.REF_DY = A.SBCN_REGI_REQ_DT AND C.REF_DOC_ID = A.DOC_ID) WHERE A.SBCN_DY = #{cmtDy} /*회의일자 PARAM*/ AND EXISTS (SELECT 1 FROM TBBADDED120M C WHERE C.CMT_DY = A.SBCN_DY AND C.CMT_DVSN_CD = '10') GROUP BY A.AUD_YR ,A.AUD_NO, A.SBCN_DY, B.SPV_BRDV_CD ,B.AUD_SBJ_NM ,A.SBCN_PAGE_CNT, B.AUD_KND_CD, SBCN_PRSS_DPT_NM, REL_TSK_NM , A.SBCN_REGI_REQ_DT,B.MNG_DPT_CD,A.SBCN_RPTR,C.REF_ID,C.DOC_ID,C.RPT_DOC_ID,C.RPT_RPRT_NM,C.BAICHF_DOC_ID,C.BAICHF_RPRT_NM, C.REF_DY
/*심사,재심의*/ UNION ALL SELECT D.ACP_YR AS AUD_YR, TO_CHAR(D.ACP_SRNO) AS AUD_NO, (SELECT TIT_NM FROM TBBADFRE112M WHERE ACP_YR = D.ACP_YR AND ACP_DVSN_CD = D.ACP_DVSN_CD AND ACP_SRNO = D.ACP_SRNO) AS AUD_SBJ_NM, '' AS CMT_SBJ_DVSN_CD, '' AS CMT_SBJ_DVSN_NM, A.SBCN_PAGE_CNT AS SBCN_PAGE_CNT, D.ACP_YR || '-' || DECODE(D.ACP_DVSN_CD, '1', '심사', '재심') || '-' || TO_CHAR(D.ACP_SRNO) AS AUD_YR_NO, 1 AS CNT, F_DPT_INFO(D.DPT_CD,'1') AS DPT_NM, 'N' AS CMT_ADD_YN, A.SBCN_DY, '' AS CMT_SBJ_SNO, '10' AS CMT_DVSN_CD, A.SBCN_REGI_REQ_DT AS SBCN_REGI_REQ_DT, D.DPT_CD, A.SBCN_RPTR, C.REF_ID, C.DOC_ID, C.RPT_DOC_ID, C.RPT_RPRT_NM, C.BAICHF_DOC_ID, C.BAICHF_RPRT_NM, D.ACP_DVSN_CD, (SELECT DECODE(COUNT(1),0,'N','Y') FROM TBBADFRE111M ST1 WHERE ST1.ACP_YR = A.ACP_YR AND ST1.ACP_SRNO = A.ACP_SRNO AND ST1.ACP_DVSN_CD = A.ACP_DVSN_CD AND ST1.DOC_ID = C.DOC_ID) AS SBCN_RPRT_SEND_YN, D.MG_DIV_CD FROM TBBADFRE113M AS A INNER JOIN (SELECT ACP_YR , ACP_SRNO , ACP_DVSN_CD , DPT_CD , TIT_NM , CASE WHEN (ACP_YR = MG_ACP_YR AND ACP_DVSN_CD = MG_ACP_DVSN_CD AND ACP_SRNO = MG_ACP_SRNO) THEN '대표' WHEN MG_DIV_CD IS NULL THEN '대표' WHEN MG_DIV_CD = '10' THEN '병합' WHEN MG_DIV_CD = '20' THEN '병행' END AS MG_DIV_NM , MG_DIV_CD FROM TBBADFRE012M) AS D ON (A.ACP_YR = D.ACP_YR AND A.ACP_SRNO = D.ACP_SRNO AND A.ACP_DVSN_CD = D.ACP_DVSN_CD) LEFT OUTER JOIN TBBADDED120M D4 ON (D4.CMT_DY = A.SBCN_DY AND D4.CMT_DVSN_CD = '10') LEFT OUTER JOIN (SELECT ACP_YR, ACP_SRNO, ACP_DVSN_CD, REF_DY, REF_ID, MAX(DOC_ID) AS DOC_ID, MAX(BAICHF_DOC_ID) AS BAICHF_DOC_ID, MAX(BAICHF_RPRT_NM) AS BAICHF_RPRT_NM, MAX(RPT_DOC_ID) AS RPT_DOC_ID, MAX(RPT_RPRT_NM) AS RPT_RPRT_NM, MAX(EDT_AUTH) AS EDT_AUTH FROM ( SELECT ACP_YR, ACP_SRNO, ACP_DVSN_CD, REF_DY, REF_ID, DECODE(ST1.RPRT_CD, 'D10301100', DOC_ID,'E10500600', DOC_ID, NULL) AS DOC_ID, DECODE(ST1.RPRT_CD, 'D10301110', DOC_ID,'E10500610', DOC_ID, NULL) AS RPT_DOC_ID, DECODE(ST1.RPRT_CD, 'D10301110', RPRT_NM,'E10500610', RPRT_NM, NULL) AS RPT_RPRT_NM, DECODE(ST1.RPRT_CD, 'D10301120', DOC_ID,'E10500620', DOC_ID, NULL) AS BAICHF_DOC_ID, DECODE(ST1.RPRT_CD, 'D10301120', RPRT_NM,'E10500620', RPRT_NM, NULL) AS BAICHF_RPRT_NM, DECODE(ST1.RPRT_CD, 'D10301100', EDT_AUTH,'E10500600', EDT_AUTH, NULL) AS EDT_AUTH FROM TBBADFRE103M ST1 WHERE RPRT_CD IN ('D10301100', 'E10500600','D10301110','E10500610','D10301120','E10500620') ) GROUP BY ACP_YR, ACP_SRNO, ACP_DVSN_CD, REF_DY, REF_ID) C ON (A.ACP_YR = C.ACP_YR AND A.ACP_DVSN_CD = C.ACP_DVSN_CD AND A.ACP_SRNO = C.ACP_SRNO AND C.REF_DY = A.SBCN_REGI_REQ_DT) WHERE A.SBCN_DY = #{cmtDy} AND D.MG_DIV_NM = '대표' AND EXISTS (SELECT 1 FROM TBBADDED120M C WHERE C.CMT_DY = A.SBCN_DY AND C.CMT_DVSN_CD = '10') GROUP BY D.ACP_YR, D.ACP_SRNO, D.ACP_DVSN_CD, D.TIT_NM, D.DPT_CD, A.SBCN_PAGE_CNT, A.SBCN_REGI_REQ_DT , A.ACP_YR, A.ACP_SRNO,A.ACP_DVSN_CD,A.SBCN_PRSS_DPT_NM, D4.CMT_DY , D4.CMT_SQ, A.SBCN_RPTR, C.REF_ID , C.DOC_ID,C.RPT_DOC_ID,C.RPT_RPRT_NM,C.BAICHF_DOC_ID, C.BAICHF_RPRT_NM,C.REF_DY, A.SBCN_DY, D.MG_DIV_CD
UNION ALL SELECT REL_AUD_YR as AUD_YR ,REL_AUD_NO as AUD_NO ,A.CMT_SBJ_TIT AS AUD_SBJ_NM ,A.CMT_SBJ_DVSN_CD ,F_CODE_NM('1000789', A.CMT_SBJ_DVSN_CD) AS CMT_SBJ_DVSN_NM ,A.SBCN_SNO AS SBCN_PAGE_CNT ,DECODE(A.REL_TSK_NM,NULL,DECODE(WORK_DVSN_CD,'A10',(SELECT F_MNG_KND_AUDYRNO(ST1.AUD_YR, ST1.AUD_NO, ST1.AUD_KND_CD, '-') FROM TBBADDED001M ST1 WHERE ST1.AUD_YR = REL_AUD_YR AND ST1.AUD_NO = REL_AUD_NO) ,'E10',REL_AUD_YR||'-재심-'||REL_AUD_NO ,'D10',REL_AUD_YR||'-심사-'||REL_AUD_NO ,NULL),A.REL_TSK_NM) AS AUD_YR_NO ,(SELECT COUNT(B.CMT_SBJ_DTL_SNO) FROM TBBADDED122M B WHERE A.CMT_DY = B.CMT_DY AND A.CMT_DVSN_CD = B.CMT_DVSN_CD AND A.CMT_SQ = B.CMT_SQ AND A.CMT_SBJ_SNO = B.CMT_SBJ_SNO) AS CNT ,NVL(PRSS_DPT_NM, F_DPT_INFO(REL_DPT_CD, 1)) AS DPT_NM ,A.CMT_ADD_YN AS CMT_ADD_YN /*N : 하드코딩*/ ,A.CMT_DY AS SBCN_DY ,TO_CHAR(A.CMT_SBJ_SNO) AS CMT_SBJ_SNO ,A.CMT_DVSN_CD ,'' as SBCN_REGI_REQ_DT ,REL_DPT_CD AS DPT_CD ,SBCN_RPTR AS SBCN_RPTR ,C.REF_ID ,C.DOC_ID ,C.RPT_DOC_ID ,C.RPT_RPRT_NM ,C.BAICHF_DOC_ID ,C.BAICHF_RPRT_NM ,A.WORK_DVSN_CD AS ACP_DVSN_CD ,'Y' AS SBCN_RPRT_SEND_YN ,'' AS MG_DIV_CD FROM TBBADDED121M A LEFT OUTER JOIN ( SELECT CMT_DY, CMT_DVSN_CD, CMT_SQ, CMT_SBJ_DVSN_CD, CMT_SBJ_SNO, MAX(REF_ID) AS REF_ID, MAX(DOC_ID) AS DOC_ID, MAX(BAICHF_DOC_ID) AS BAICHF_DOC_ID, MAX(BAICHF_RPRT_NM) AS BAICHF_RPRT_NM, MAX(RPT_DOC_ID) AS RPT_DOC_ID, MAX(RPT_RPRT_NM) AS RPT_RPRT_NM, MAX(EDT_AUTH) AS EDT_AUTH FROM ( SELECT ST1.CMT_DY, ST1.CMT_DVSN_CD, ST1.CMT_SQ, ST1.CMT_SBJ_DVSN_CD, ST1.CMT_SBJ_SNO, /* CMT_SBJ_DTL_SNO - 1 : 부의안, 2 : 부의안요약(보고자용), 3 : 부의안요약(원장용) 기타안건(CMT_SBJ_DVSN_CD:90)일 경우 1 로 대체*/ DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,1), ST2.DOC_ID, NULL) AS REF_ID, DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,3), ST2.DOC_ID, NULL) AS DOC_ID, DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,2), ST2.DOC_ID, NULL) AS RPT_DOC_ID, DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,2), ST2.FILE_NM, NULL) AS RPT_RPRT_NM, DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,3), ST2.DOC_ID, NULL) AS BAICHF_DOC_ID, DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,3), ST2.FILE_NM, NULL) AS BAICHF_RPRT_NM, '' AS EDT_AUTH FROM TBBADDED121M ST1 INNER JOIN TBBADDED122M ST2 ON (ST1.CMT_DY = ST1.CMT_DY AND ST1.CMT_DVSN_CD = ST2.CMT_DVSN_CD AND ST1.CMT_SQ = ST2.CMT_SQ AND ST1.CMT_SBJ_DVSN_CD = ST2.CMT_SBJ_DVSN_CD AND ST1.CMT_SBJ_SNO = ST2.CMT_SBJ_SNO) WHERE ST1.CMT_SBJ_DVSN_CD IN ('10','90') ) GROUP BY CMT_DY, CMT_DVSN_CD, CMT_SQ, CMT_SBJ_DVSN_CD,CMT_SBJ_SNO) C ON (A.CMT_DY = C.CMT_DY AND A.CMT_DVSN_CD = C.CMT_DVSN_CD AND A.CMT_SQ = C.CMT_SQ AND A.CMT_SBJ_DVSN_CD = C.CMT_SBJ_DVSN_CD AND A.CMT_SBJ_SNO = C.CMT_SBJ_SNO) WHERE A.CMT_DY = #{cmtDy}/*회의일자 PARAM*/ AND A.CMT_DVSN_CD = '10' /*회의 구분코드*/ ) T1 INNER JOIN TBBADDED120M T2 ON T1.SBCN_DY = T2.CMT_DY AND T1.CMT_DVSN_CD = T2.CMT_DVSN_CD WHERE T2.CMT_DY = #{cmtDy} /*회의일자 PARAM*/ AND T1.CMT_DVSN_CD = '10' /*회의 구분코드*/ ORDER BY T1.SBCN_PAGE_CNT , T1.CNT DESC )) AS SBCN_CNT FROM TBBADDED120M WHERE CMT_DY = #{cmtDy} AND CMT_END_YN = 'N' AND CMT_DVSN_CD = '10'

/*회의상세안건*/ SELECT CMT_DY AS SBCN_DT, CMT_SQ, ROWNUM AS CMT_SBJ_SNO, AUD_YR,AUD_NO, ACP_DVSN_CD, AUD_YR AS ACP_YR, AUD_NO AS ACP_SRNO, SUBSTR(CMT_DY,0,4) AS CMT_YR, CMT_DVSN_CD FROM ( SELECT T2.CMT_SQ /*회의 회차*/ ,T2.CMT_TIT /*회의 안건명*/ ,T2.CMT_DY /*회의 일자*/ ,T2.CMT_END_YN /*회의 종료여부*/ ,T2.CMT_DVSN_CD /*회의 구분코드*/ ,REPLACE(T2.CMT_STR_TXT,'|',' ') AS CMT_STR_TXT /*회의 개회선언사*/ ,REPLACE(T2.CMT_END_TXT,'|',' ') AS CMT_END_TXT /*회의 폐회사*/ ,F_USR_INFO(T2.CHF_CMTR_ID, '2') AS CHF_CMTR_NM /*회의 의장*/ ,F_USR_INFO(T2.CMTR1_ID, '2') AS CMTR1_NM /*회의 위원1*/ ,F_USR_INFO(T2.CMTR2_ID, '2') AS CMTR2_NM /*회의 위원2*/ ,F_USR_INFO(T2.CMTR3_ID, '2') AS CMTR3_NM /*회의 위원3*/ ,F_USR_INFO(T2.CMTR4_ID, '2') AS CMTR4_NM /*회의 위원4*/ ,F_USR_INFO(T2.CMTR5_ID, '2') AS CMTR5_NM /*회의 위원5*/ ,F_USR_INFO(T2.CMTR6_ID, '2') AS CMTR6_NM /*회의 위원6*/ ,DECODE(T2.CMT_END_YN, 'Y', '종료', '진행중') AS CMT_END_NM /*회의종료구분*/ ,SUBSTR(CMT_DY,0,4)||'년 '||SUBSTR(CMT_DY, 5, 2)||'월 ' ||SUBSTR(CMT_DY, 7, 2)||'일 ' AS CMT_DATA /*회의 날짜*/ ,T1.AUD_SBJ_NM /*회의 안건명*/ ,T1.CMT_SBJ_DVSN_CD /*안건구분코드*/ ,T1.CMT_SBJ_DVSN_NM /*안건구분코코드*/ ,T1.SBCN_PAGE_CNT /*부의순번*/ ,T1.AUD_YR_NO /*감사연번호*/ ,T1.CNT /*건수*/ ,T1.DPT_NM /*관련부서명*/ ,T1.CMT_ADD_YN /*회의추가여부*/ ,T1.SBCN_DY /*회의일자*/ ,T1.AUD_YR /*감사년도*/ ,T1.AUD_NO /*감사번호*/ ,T1.DOC_ID /*부의안 요약 DOCID*/ ,T1.REF_ID /*부의안 DOCID*/ ,T1.RPT_DOC_ID ,T1.RPT_RPRT_NM ,T1.BAICHF_DOC_ID ,T1.BAICHF_RPRT_NM ,T1.CMT_SBJ_SNO ,T1.DPT_CD ,T1.SBCN_RPTR ,T1.ACP_DVSN_CD ,T1.SBCN_RPRT_SEND_YN ,T1.MG_DIV_CD FROM ( SELECT A.AUD_YR ,A.AUD_NO ,B.AUD_SBJ_NM||(SELECT REL_TEXT1 FROM TBBADDED121T F WHERE F.AUD_YR = A.AUD_YR AND F.AUD_NO = A.AUD_NO AND F.AUD_YR_DY = C.REF_DY) AS AUD_SBJ_NM ,'10' AS CMT_SBJ_DVSN_CD /*안건구분코드*/ ,F_CODE_NM('1000789', '10') AS CMT_SBJ_DVSN_NM /*안건구분코드명*/ ,A.SBCN_PAGE_CNT ,DECODE(REL_TSK_NM,NULL,F_MNG_KND_AUDYRNO(A.AUD_YR, A.AUD_NO, B.AUD_KND_CD, '-'),REL_TSK_NM )AS AUD_YR_NO ,COUNT(A.DSP_RQ_SNO) AS CNT ,NVL(SBCN_PRSS_DPT_NM, F_DPT_INFO(B.MNG_DPT_CD, 1)) AS DPT_NM ,'N' AS CMT_ADD_YN /*N : 하드코딩*/ ,A.SBCN_DY ,'' AS CMT_SBJ_SNO ,'10' as CMT_DVSN_CD ,SBCN_REGI_REQ_DT as SBCN_REGI_REQ_DT ,B.MNG_DPT_CD AS DPT_CD ,A.SBCN_RPTR ,C.REF_ID ,C.DOC_ID ,C.RPT_DOC_ID ,C.RPT_RPRT_NM ,C.BAICHF_DOC_ID ,C.BAICHF_RPRT_NM ,'9' AS ACP_DVSN_CD , (SELECT DECODE(COUNT(1),0,'N','Y') FROM TBBADDED111M ST1 WHERE ST1.AUD_YR = A.AUD_YR AND ST1.AUD_NO = A.AUD_NO AND ST1.DOC_ID = C.DOC_ID) AS SBCN_RPRT_SEND_YN ,'' AS MG_DIV_CD FROM TBBADDED113M A INNER JOIN TBBADDED001M B ON (B.AUD_YR = A.AUD_YR AND B.AUD_NO = A.AUD_NO) LEFT OUTER JOIN (SELECT AUD_YR, AUD_NO, REF_DY, REF_DOC_ID, MAX(REF_ID) AS REF_ID, MAX(DOC_ID) AS DOC_ID, MAX(BAICHF_DOC_ID) AS BAICHF_DOC_ID, MAX(BAICHF_RPRT_NM) AS BAICHF_RPRT_NM, MAX(RPT_DOC_ID) AS RPT_DOC_ID, MAX(RPT_RPRT_NM) AS RPT_RPRT_NM, MAX(EDT_AUTH) AS EDT_AUTH FROM ( SELECT AUD_YR, AUD_NO, REF_DY, REF_DOC_ID, DECODE(ST1.RPRT_CD, 'A10700500', DOC_ID, NULL) AS REF_ID, DECODE(ST1.RPRT_CD, 'A10700600', DOC_ID, NULL) AS DOC_ID, DECODE(ST1.RPRT_CD, 'A10700700', DOC_ID, NULL) AS RPT_DOC_ID, DECODE(ST1.RPRT_CD, 'A10700700', RPRT_NM, NULL) AS RPT_RPRT_NM, DECODE(ST1.RPRT_CD, 'A10700800', DOC_ID, NULL) AS BAICHF_DOC_ID, DECODE(ST1.RPRT_CD, 'A10700800', RPRT_NM, NULL) AS BAICHF_RPRT_NM, DECODE(ST1.RPRT_CD, 'A10700600', EDT_AUTH, NULL) AS EDT_AUTH FROM TBBADDED103M ST1 WHERE RPRT_CD IN ('A10700500', 'A10700600', 'A10700700', 'A10700800') ) GROUP BY AUD_YR, AUD_NO, REF_DY, REF_DOC_ID) C ON (A.AUD_YR = C.AUD_YR AND A.AUD_NO = C.AUD_NO AND C.REF_DY = A.SBCN_REGI_REQ_DT AND C.REF_DOC_ID = A.DOC_ID) WHERE A.SBCN_DY = #{cmtDy} /*회의일자 PARAM*/ AND EXISTS (SELECT 1 FROM TBBADDED120M C WHERE C.CMT_DY = A.SBCN_DY AND C.CMT_DVSN_CD = '10') GROUP BY A.AUD_YR ,A.AUD_NO, A.SBCN_DY, B.SPV_BRDV_CD ,B.AUD_SBJ_NM ,A.SBCN_PAGE_CNT, B.AUD_KND_CD, SBCN_PRSS_DPT_NM, REL_TSK_NM , A.SBCN_REGI_REQ_DT,B.MNG_DPT_CD,A.SBCN_RPTR,C.REF_ID,C.DOC_ID,C.RPT_DOC_ID,C.RPT_RPRT_NM,C.BAICHF_DOC_ID,C.BAICHF_RPRT_NM, C.REF_DY
/*심사,재심의*/ UNION ALL SELECT D.ACP_YR AS AUD_YR, TO_CHAR(D.ACP_SRNO) AS AUD_NO, (SELECT TIT_NM FROM TBBADFRE112M WHERE ACP_YR = D.ACP_YR AND ACP_DVSN_CD = D.ACP_DVSN_CD AND ACP_SRNO = D.ACP_SRNO) AS AUD_SBJ_NM, '' AS CMT_SBJ_DVSN_CD, '' AS CMT_SBJ_DVSN_NM, A.SBCN_PAGE_CNT AS SBCN_PAGE_CNT, D.ACP_YR || '-' || DECODE(D.ACP_DVSN_CD, '1', '심사', '재심') || '-' || TO_CHAR(D.ACP_SRNO) AS AUD_YR_NO, 1 AS CNT, F_DPT_INFO(D.DPT_CD,'1') AS DPT_NM, 'N' AS CMT_ADD_YN, A.SBCN_DY, '' AS CMT_SBJ_SNO, '10' AS CMT_DVSN_CD, A.SBCN_REGI_REQ_DT AS SBCN_REGI_REQ_DT, D.DPT_CD, A.SBCN_RPTR, C.REF_ID, C.DOC_ID, C.RPT_DOC_ID, C.RPT_RPRT_NM, C.BAICHF_DOC_ID, C.BAICHF_RPRT_NM, D.ACP_DVSN_CD, (SELECT DECODE(COUNT(1),0,'N','Y') FROM TBBADFRE111M ST1 WHERE ST1.ACP_YR = A.ACP_YR AND ST1.ACP_SRNO = A.ACP_SRNO AND ST1.ACP_DVSN_CD = A.ACP_DVSN_CD AND ST1.DOC_ID = C.DOC_ID) AS SBCN_RPRT_SEND_YN, D.MG_DIV_CD FROM TBBADFRE113M AS A INNER JOIN (SELECT ACP_YR , ACP_SRNO , ACP_DVSN_CD , DPT_CD , TIT_NM , CASE WHEN (ACP_YR = MG_ACP_YR AND ACP_DVSN_CD = MG_ACP_DVSN_CD AND ACP_SRNO = MG_ACP_SRNO) THEN '대표' WHEN MG_DIV_CD IS NULL THEN '대표' WHEN MG_DIV_CD = '10' THEN '병합' WHEN MG_DIV_CD = '20' THEN '병행' END AS MG_DIV_NM , MG_DIV_CD FROM TBBADFRE012M) AS D ON (A.ACP_YR = D.ACP_YR AND A.ACP_SRNO = D.ACP_SRNO AND A.ACP_DVSN_CD = D.ACP_DVSN_CD) LEFT OUTER JOIN TBBADDED120M D4 ON (D4.CMT_DY = A.SBCN_DY AND D4.CMT_DVSN_CD = '10') LEFT OUTER JOIN (SELECT ACP_YR, ACP_SRNO, ACP_DVSN_CD, REF_DY, REF_ID, MAX(DOC_ID) AS DOC_ID, MAX(BAICHF_DOC_ID) AS BAICHF_DOC_ID, MAX(BAICHF_RPRT_NM) AS BAICHF_RPRT_NM, MAX(RPT_DOC_ID) AS RPT_DOC_ID, MAX(RPT_RPRT_NM) AS RPT_RPRT_NM, MAX(EDT_AUTH) AS EDT_AUTH FROM ( SELECT ACP_YR, ACP_SRNO, ACP_DVSN_CD, REF_DY, REF_ID, DECODE(ST1.RPRT_CD, 'D10301100', DOC_ID,'E10500600', DOC_ID, NULL) AS DOC_ID, DECODE(ST1.RPRT_CD, 'D10301110', DOC_ID,'E10500610', DOC_ID, NULL) AS RPT_DOC_ID, DECODE(ST1.RPRT_CD, 'D10301110', RPRT_NM,'E10500610', RPRT_NM, NULL) AS RPT_RPRT_NM, DECODE(ST1.RPRT_CD, 'D10301120', DOC_ID,'E10500620', DOC_ID, NULL) AS BAICHF_DOC_ID, DECODE(ST1.RPRT_CD, 'D10301120', RPRT_NM,'E10500620', RPRT_NM, NULL) AS BAICHF_RPRT_NM, DECODE(ST1.RPRT_CD, 'D10301100', EDT_AUTH,'E10500600', EDT_AUTH, NULL) AS EDT_AUTH FROM TBBADFRE103M ST1 WHERE RPRT_CD IN ('D10301100', 'E10500600','D10301110','E10500610','D10301120','E10500620') ) GROUP BY ACP_YR, ACP_SRNO, ACP_DVSN_CD, REF_DY, REF_ID) C ON (A.ACP_YR = C.ACP_YR AND A.ACP_DVSN_CD = C.ACP_DVSN_CD AND A.ACP_SRNO = C.ACP_SRNO AND C.REF_DY = A.SBCN_REGI_REQ_DT) WHERE A.SBCN_DY = #{cmtDy} AND D.MG_DIV_NM = '대표' AND EXISTS (SELECT 1 FROM TBBADDED120M C WHERE C.CMT_DY = A.SBCN_DY AND C.CMT_DVSN_CD = '10') GROUP BY D.ACP_YR, D.ACP_SRNO, D.ACP_DVSN_CD, D.TIT_NM, D.DPT_CD, A.SBCN_PAGE_CNT, A.SBCN_REGI_REQ_DT , A.ACP_YR, A.ACP_SRNO,A.ACP_DVSN_CD,A.SBCN_PRSS_DPT_NM, D4.CMT_DY , D4.CMT_SQ, A.SBCN_RPTR, C.REF_ID , C.DOC_ID,C.RPT_DOC_ID,C.RPT_RPRT_NM,C.BAICHF_DOC_ID, C.BAICHF_RPRT_NM,C.REF_DY, A.SBCN_DY, D.MG_DIV_CD
UNION ALL SELECT REL_AUD_YR as AUD_YR ,REL_AUD_NO as AUD_NO ,A.CMT_SBJ_TIT AS AUD_SBJ_NM ,A.CMT_SBJ_DVSN_CD ,F_CODE_NM('1000789', A.CMT_SBJ_DVSN_CD) AS CMT_SBJ_DVSN_NM ,A.SBCN_SNO AS SBCN_PAGE_CNT ,DECODE(A.REL_TSK_NM,NULL,DECODE(WORK_DVSN_CD,'A10',(SELECT F_MNG_KND_AUDYRNO(ST1.AUD_YR, ST1.AUD_NO, ST1.AUD_KND_CD, '-') FROM TBBADDED001M ST1 WHERE ST1.AUD_YR = REL_AUD_YR AND ST1.AUD_NO = REL_AUD_NO) ,'E10',REL_AUD_YR||'-재심-'||REL_AUD_NO ,'D10',REL_AUD_YR||'-심사-'||REL_AUD_NO ,NULL),A.REL_TSK_NM) AS AUD_YR_NO ,(SELECT COUNT(B.CMT_SBJ_DTL_SNO) FROM TBBADDED122M B WHERE A.CMT_DY = B.CMT_DY AND A.CMT_DVSN_CD = B.CMT_DVSN_CD AND A.CMT_SQ = B.CMT_SQ AND A.CMT_SBJ_SNO = B.CMT_SBJ_SNO) AS CNT ,NVL(PRSS_DPT_NM, F_DPT_INFO(REL_DPT_CD, 1)) AS DPT_NM ,A.CMT_ADD_YN AS CMT_ADD_YN /*N : 하드코딩*/ ,A.CMT_DY AS SBCN_DY ,TO_CHAR(A.CMT_SBJ_SNO) AS CMT_SBJ_SNO ,A.CMT_DVSN_CD ,'' as SBCN_REGI_REQ_DT ,REL_DPT_CD AS DPT_CD ,SBCN_RPTR AS SBCN_RPTR ,C.REF_ID ,C.DOC_ID ,C.RPT_DOC_ID ,C.RPT_RPRT_NM ,C.BAICHF_DOC_ID ,C.BAICHF_RPRT_NM ,A.WORK_DVSN_CD AS ACP_DVSN_CD ,'Y' AS SBCN_RPRT_SEND_YN ,'' AS MG_DIV_CD FROM TBBADDED121M A LEFT OUTER JOIN ( SELECT CMT_DY, CMT_DVSN_CD, CMT_SQ, CMT_SBJ_DVSN_CD, CMT_SBJ_SNO, MAX(REF_ID) AS REF_ID, MAX(DOC_ID) AS DOC_ID, MAX(BAICHF_DOC_ID) AS BAICHF_DOC_ID, MAX(BAICHF_RPRT_NM) AS BAICHF_RPRT_NM, MAX(RPT_DOC_ID) AS RPT_DOC_ID, MAX(RPT_RPRT_NM) AS RPT_RPRT_NM, MAX(EDT_AUTH) AS EDT_AUTH FROM ( SELECT ST1.CMT_DY, ST1.CMT_DVSN_CD, ST1.CMT_SQ, ST1.CMT_SBJ_DVSN_CD, ST1.CMT_SBJ_SNO, /* CMT_SBJ_DTL_SNO - 1 : 부의안, 2 : 부의안요약(보고자용), 3 : 부의안요약(원장용) 기타안건(CMT_SBJ_DVSN_CD:90)일 경우 1 로 대체*/ DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,1), ST2.DOC_ID, NULL) AS REF_ID, DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,3), ST2.DOC_ID, NULL) AS DOC_ID, DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,2), ST2.DOC_ID, NULL) AS RPT_DOC_ID, DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,2), ST2.FILE_NM, NULL) AS RPT_RPRT_NM, DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,3), ST2.DOC_ID, NULL) AS BAICHF_DOC_ID, DECODE(ST2.CMT_SBJ_DTL_SNO, DECODE(ST2.CMT_SBJ_DVSN_CD,'90',1,3), ST2.FILE_NM, NULL) AS BAICHF_RPRT_NM, '' AS EDT_AUTH FROM TBBADDED121M ST1 INNER JOIN TBBADDED122M ST2 ON (ST1.CMT_DY = ST1.CMT_DY AND ST1.CMT_DVSN_CD = ST2.CMT_DVSN_CD AND ST1.CMT_SQ = ST2.CMT_SQ AND ST1.CMT_SBJ_DVSN_CD = ST2.CMT_SBJ_DVSN_CD AND ST1.CMT_SBJ_SNO = ST2.CMT_SBJ_SNO) WHERE ST1.CMT_SBJ_DVSN_CD IN ('10','90') ) GROUP BY CMT_DY, CMT_DVSN_CD, CMT_SQ, CMT_SBJ_DVSN_CD,CMT_SBJ_SNO) C ON (A.CMT_DY = C.CMT_DY AND A.CMT_DVSN_CD = C.CMT_DVSN_CD AND A.CMT_SQ = C.CMT_SQ AND A.CMT_SBJ_DVSN_CD = C.CMT_SBJ_DVSN_CD AND A.CMT_SBJ_SNO = C.CMT_SBJ_SNO) WHERE A.CMT_DY = #{cmtDy}/*회의일자 PARAM*/ AND A.CMT_DVSN_CD = '10' /*회의 구분코드*/ ) T1 INNER JOIN TBBADDED120M T2 ON T1.SBCN_DY = T2.CMT_DY AND T1.CMT_DVSN_CD = T2.CMT_DVSN_CD WHERE T2.CMT_DY = #{cmtDy} /*회의일자 PARAM*/ AND T1.CMT_DVSN_CD = '10' /*회의 구분코드*/ ORDER BY T1.SBCN_PAGE_CNT , T1.CNT DESC )

SELECT 'TBBADDED121M' AS TBL_NM , (SELECT COMMENTS FROM ALL_TAB_COMMENTS WHERE TABLE_NAME = 'TBBADDED121M' AND OWNER = 'UBCREOWN') AS TBL_DESC , #{cmtYr} AS CMT_YR , #{cmtDvsnCd} AS CMT_DVSN_CD , #{cmtSq} AS CMT_SQ , #{cmtSbjSno} AS CMT_SBJ_SNO , CMT_DY , CMT_DVSN_CD AS CMT_DVSN_CD_REAL , CMT_SQ AS CMT_SQ_REAL , CMT_SBJ_DVSN_CD , CMT_SBJ_SNO AS CMT_SBJ_SNO_REAL , CMT_SBJ_TIT , CMT_ADD_YN , SBCN_SNO , DOC_ID , FILE_NM , WORK_DVSN_CD , REL_AUD_YR , REL_AUD_NO , REL_DPT_CD , REL_TSK_NM , REGI_NO , BFR_SBCN_CNT , PRSS_DPT_NM , SBCN_RPTR , FRT_USR_ID , FNL_USR_ID , FNL_DEAL_DPT_CD , FNL_MNU_ID , TO_CHAR(FRT_REGI_DH, 'YYYY/MM/DD HH24:MI:SS') AS FRT_REGI_DH , TO_CHAR(FNL_MDF_DH, 'YYYY/MM/DD HH24:MI:SS') AS FNL_MDF_DH , SBMT_MNG_DPT_NM , SBMT_CMTR_NM , SBMT_REL_ORG_NM , SBMT_DVSN_NO FROM TBBADDED121M WHERE CMT_DY = #{cmtDy} AND CMT_DVSN_CD = '10' /*10:부의, 20:소위*/
AND (REL_AUD_YR, WORK_DVSN_CD, REL_AUD_NO) IN (SELECT ACP_YR, DECODE(ACP_DVSN_CD, '1', 'D10','E10') , ACP_SRNO FROM TBBADFRE012M WHERE NVL(MG_ACP_YR, ACP_YR) = #{audYr} AND NVL(MG_ACP_DVSN_CD, ACP_DVSN_CD) = DECODE(#{acpDvsnCd}, 'D10', '1', '2') AND NVL(MG_ACP_SRNO, ACP_SRNO) = #{audNo})
AND WORK_DVSN_CD = #{acpDvsnCd} AND REL_AUD_YR = #{audYr} AND REL_AUD_NO = #{audNo}
/* AND WORK_DVSN_CD IN ('D10','E10') D10:심사, E10:재심 */

SELECT 'TBBADDED122M' AS TBL_NM , (SELECT COMMENTS FROM ALL_TAB_COMMENTS WHERE TABLE_NAME = 'TBBADDED122M' AND OWNER = 'UBCREOWN') AS TBL_DESC , #{cmtYr} AS CMT_YR , #{cmtDvsnCd} AS CMT_DVSN_CD , #{cmtSq} AS CMT_SQ , #{cmtSbjSno} AS CMT_SBJ_SNO , CMT_DY , CMT_DVSN_CD AS CMT_DVSN_CD_REAL , CMT_SQ AS CMT_SQ_REAL , CMT_SBJ_DVSN_CD , CMT_SBJ_SNO AS CMT_SBJ_SNO_REAL , CMT_SBJ_DTL_SNO , CMT_SBJ_DTL_TIT , DOC_ID , SBCN_VT_CD , SBCN_VT_TXT , FILE_NM , FRT_USR_ID , FNL_USR_ID , FNL_DEAL_DPT_CD , FNL_MNU_ID , TO_CHAR(FRT_REGI_DH, 'YYYY/MM/DD HH24:MI:SS') AS FRT_REGI_DH , TO_CHAR(FNL_MDF_DH, 'YYYY/MM/DD HH24:MI:SS') AS FNL_MDF_DH FROM TBBADDED122M WHERE (CMT_DY, CMT_DVSN_CD, CMT_SQ, CMT_SBJ_DVSN_CD, CMT_SBJ_SNO) IN (SELECT CMT_DY, CMT_DVSN_CD, CMT_SQ, CMT_SBJ_DVSN_CD, CMT_SBJ_SNO FROM TBBADDED121M WHERE CMT_DY = #{cmtDy} AND CMT_DVSN_CD = '10'
AND (REL_AUD_YR, WORK_DVSN_CD, REL_AUD_NO) IN (SELECT ACP_YR, DECODE(ACP_DVSN_CD, '1', 'D10','E10') , ACP_SRNO FROM TBBADFRE012M WHERE NVL(MG_ACP_YR, ACP_YR) = #{audYr} AND NVL(MG_ACP_DVSN_CD, ACP_DVSN_CD) = DECODE(#{acpDvsnCd}, 'D10', '1', '2') AND NVL(MG_ACP_SRNO, ACP_SRNO) = #{audNo})
AND WORK_DVSN_CD = #{acpDvsnCd} AND REL_AUD_YR = #{audYr} AND REL_AUD_NO = #{audNo}
)

