mapper.select:
         SELECT DOC_ID
          FROM TBBADFRE103M 
         WHERE ACP_YR = #{acpYr}
           AND ACP_DVSN_CD = #{acpDvsnCd}
           AND ACP_SRNO = #{acpSrno}
           AND (/*부의안 요약문, 결정문*/
               RPRT_CD IN ('D10300100', 'E10500100','D10301100', 'E10500600', 'D10301110', 'E10500610', 'D10301120', 'E10500620')
               OR 
               /*원처분문서*/
               ((ACP_YR, ACP_DVSN_CD, ACP_SRNO) IN (SELECT ACP_YR, ACP_DVSN_CD, ACP_SRNO 
                                                      FROM TBBADFRE111M 
                                                     WHERE ACP_YR = #{acpYr}
                                                       AND ACP_DVSN_CD = #{acpDvsnCd}
                                                       AND ACP_SRNO = #{acpSrno}
                                                       AND ACP_STEP_CD IN ('D1010','E1010'))
               AND ACP_STEP_CD IN ('D1010','E1010')
               AND REF_ID IS NOT NULL)
              )
         UNION ALL
         SELECT DOC_ID
		    FROM TBBADFRE110M
          WHERE ACP_YR = #{acpYr}
            AND ACP_DVSN_CD = #{acpDvsnCd}
            AND ACP_SRNO = #{acpSrno}
         UNION ALL
         SELECT ACP_DCM_DOC_ID AS DOC_ID
           FROM TBBADFRE108D
          WHERE ACP_YR = #{acpYr}
            AND ACP_DVSN_CD = #{acpDvsnCd}
            AND ACP_SRNO = #{acpSrno}
         UNION ALL
        SELECT DOC_ID
          FROM TBBADFRE115M
         WHERE ACP_YR = #{acpYr}
           AND ACP_DVSN_CD = #{acpDvsnCd}
           AND ACP_SRNO = #{acpSrno}

         SELECT DOC_ID
          FROM TBBADFRE103M 
         WHERE ACP_YR = #{acpYr}
           AND ACP_DVSN_CD = DECODE(#{acpDvsnCd},'D10','1','E10','2','')
           AND ACP_SRNO = #{acpSrno}
           AND (/*부의안 요약문, 결정문*/
               RPRT_CD IN ('D10300100', 'E10500100','D10301100', 'E10500600', 'D10301110', 'E10500610', 'D10301120', 'E10500620')
               OR 
               /*원처분문서*/
               ((ACP_YR, ACP_DVSN_CD, ACP_SRNO) IN (SELECT ACP_YR, ACP_DVSN_CD, ACP_SRNO 
                                                      FROM TBBADFRE111M 
                                                     WHERE ACP_YR = #{acpYr}
                                                       AND ACP_DVSN_CD = DECODE(#{acpDvsnCd},'D10','1','E10','2','')
                                                       AND ACP_SRNO = #{acpSrno}
                                                       AND ACP_STEP_CD IN ('D1010','E1010'))
               AND ACP_STEP_CD IN ('D1010','E1010')
               AND REF_ID IS NOT NULL)
              )
         UNION ALL
         SELECT DOC_ID
		    FROM TBBADFRE110M
          WHERE ACP_YR = #{acpYr}
            AND ACP_DVSN_CD = DECODE(#{acpDvsnCd},'D10','1','E10','2','')
            AND ACP_SRNO = #{acpSrno}
         UNION ALL
         SELECT ACP_DCM_DOC_ID AS DOC_ID
           FROM TBBADFRE108D
          WHERE ACP_YR = #{acpYr}
            AND ACP_DVSN_CD = DECODE(#{acpDvsnCd},'D10','1','E10','2','')
            AND ACP_SRNO = #{acpSrno}
         UNION ALL
        SELECT DOC_ID
          FROM TBBADFRE115M
         WHERE ACP_YR = #{acpYr}
           AND ACP_DVSN_CD = DECODE(#{acpDvsnCd},'D10','1','E10','2','')
           AND ACP_SRNO = #{acpSrno}
         UNION ALL
         SELECT DOC_ID
           FROM TBBADDED122M
          WHERE (CMT_DY, CMT_DVSN_CD, CMT_SQ, CMT_SBJ_DVSN_CD, CMT_SBJ_SNO) IN (SELECT CMT_DY, CMT_DVSN_CD, CMT_SQ, CMT_SBJ_DVSN_CD, CMT_SBJ_SNO
                                                                               FROM TBBADDED121M
                                                                              WHERE CMT_DY = #{cmtDy}
                                                                                AND CMT_DVSN_CD = '10' 

                                                                                AND (REL_AUD_YR, WORK_DVSN_CD, REL_AUD_NO) IN (SELECT ACP_YR, DECODE(ACP_DVSN_CD, '1', 'D10','E10') , ACP_SRNO FROM TBBADFRE012M WHERE NVL(MG_ACP_YR, ACP_YR) = #{audYr} AND NVL(MG_ACP_DVSN_CD, ACP_DVSN_CD) = DECODE(#{acpDvsnCd}, 'D10', '1', '2') AND NVL(MG_ACP_SRNO, ACP_SRNO) = #{audNo})


                                                                                AND WORK_DVSN_CD = #{acpDvsnCd}
                                                                                AND REL_AUD_YR = #{audYr}
                                                                                AND REL_AUD_NO = #{audNo}

                                                                                )

