mapper.select:
/* kr.go.bai.eip.cor.cor.dao.COR002007EMapper.COR003005PMainSelect */
SELECT A.REQ_DVSN_CD ,A.ACP_YR ,A.CVPT_ACP_NO ,A.ACP_YR||'-부감-'||A.RM_CVPT_ACP_NO AS ACP_NO ,A.CVPT_KND_CD ,F_CODE_NM('1000939',A.CVPT_KND_CD) AS CVPT_KND_NM ,A.TIT_NM ,DECODE(A.REQR_KND_CD,'1',A.CVPTR_NM,'2',A.RPST_PEN_NM,'3',A.RPST_PEN_NM,'4',A.CVPTR_NM) AS CVPTR_NM ,TO_CHAR(A.ACP_DH,'YYYY.MM.DD HH24:MI') AS ACP_DH ,A.CVPT_HNDL_STA_CD ,A.ACP_RUT_CD ,A.HPN ,A.RM_CVPT_ACP_NO FROM TBCORCOR101M A ,TBCORCOR102M B ,TBCORCOR201M C WHERE 1=1 AND A.REQ_DVSN_CD = '5' AND A.REQ_DVSN_CD = B.REQ_DVSN_CD AND A.ACP_YR = B.ACP_YR AND A.CVPT_ACP_NO = B.CVPT_ACP_NO AND A.REQ_DVSN_CD = C.REQ_DVSN_CD AND A.ACP_YR = C.ACP_YR AND A.CVPT_ACP_NO = C.CVPT_ACP_NO AND C.HNDL_DPT_CD = #{dptCd} AND C.HNDL_CHGR_ID = #{usrId} AND (A.REQ_DVSN_CD, A.ACP_YR, A.CVPT_ACP_NO) NOT IN ( SELECT REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO FROM TBCORCOR203M) AND A.CVPT_HNDL_STA_CD IN ('30','40') AND (A.REQ_DVSN_CD, A.ACP_YR, A.CVPT_ACP_NO) NOT IN ( SELECT #{reqDvsnCd},#{acpYr},#{cvptAcpNo} FROM DUAL ) AND ((A.REQ_DVSN_CD, A.ACP_YR, A.CVPT_ACP_NO) NOT IN ( SELECT REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO FROM TBCORCOR105L) AND (A.REQ_DVSN_CD, A.ACP_YR, A.CVPT_ACP_NO) NOT IN ( SELECT SUC_REQ_DVSN_CD, SUC_ACP_YR, SUC_CVPT_ACP_NO FROM TBCORCOR105L) )
AND A.ACP_DH >= #{acpDhs}
AND TO_CHAR(A.ACP_DH,'YYYYMMDD') <= #{acpDhe}
AND A.TIT_NM LIKE '%'||#{titNm}||'%'
AND B.CVPT_SBT LIKE '%'||#{titNm}||'%'
AND A.ACP_YR = #{schAcpYr}
AND A.RM_CVPT_ACP_NO = #{rmCvptAcpNo}
AND (A.CVPTR_NM LIKE '%'||#{cvptrNm}||'%' OR A.RPST_PEN_NM LIKE '%'||#{cvptrNm}||'%')

mapper.insert:
   		/* kr.go.bai.eip.cor.cor.dao.COR002007EMapper.COR003005P105SucInsert */

   			INSERT INTO	TBCORCOR105L
	   					(	REQ_DVSN_CD
	   						,ACP_YR
	   						,CVPT_ACP_NO
	   						,SUC_REQ_DVSN_CD
	   						,SUC_ACP_YR
	   						,SUC_CVPT_ACP_NO
	   						,FRT_USR_ID
	       					,FNL_USR_ID
	       					,FNL_DEAL_DPT_CD
	       					,FNL_MNU_ID
	       					,FRT_REGI_DH
	       					,FNL_MDF_DH
	       				)
       			VALUES	(	#{reqDvsnCd}
	   						,#{acpYr}
	   						,#{cvptAcpNo}
	   						,#{sucReqDvsnCd}
	   						,#{sucAcpYr}
	   						,#{sucCvptAcpNo}
	   						,#{frtUsrId}
	       					,#{fnlUsrId}
	       					,#{fnlDealDptCd}
	       					,#{fnlMnuId}
	       					,SYSDATE
	       					,SYSDATE
	       				)

mapper.update:
/* kr.go.bai.eip.cor.cor.dao.COR002007EMapper.COR003005P201SucUpdate */

	UPDATE	TBCORCOR201M A
	SET		A.CVPT_KND_DVSN_CD = (SELECT 	Z.CVPT_KND_DVSN_CD
								FROM 	TBCORCOR201M Z
								WHERE 	Z.REQ_DVSN_CD = #{reqDvsnCd}
								AND		Z.ACP_YR = #{acpYr}
								AND		Z.CVPT_ACP_NO = #{cvptAcpNo})
			,A.PASV_ADMN_YN = (SELECT 	Z.PASV_ADMN_YN
								FROM 	TBCORCOR201M Z
								WHERE 	Z.REQ_DVSN_CD = #{reqDvsnCd}
								AND		Z.ACP_YR = #{acpYr}
								AND		Z.CVPT_ACP_NO = #{cvptAcpNo})
			,A.REF_YN = (SELECT 	Z.REF_YN
								FROM 	TBCORCOR201M Z
								WHERE 	Z.REQ_DVSN_CD = #{reqDvsnCd}
								AND		Z.ACP_YR = #{acpYr}
								AND		Z.CVPT_ACP_NO = #{cvptAcpNo})
			,A.DOC_ID = (SELECT 	Z.DOC_ID
								FROM 	TBCORCOR201M Z
								WHERE 	Z.REQ_DVSN_CD = #{reqDvsnCd}
								AND		Z.ACP_YR = #{acpYr}
								AND		Z.CVPT_ACP_NO = #{cvptAcpNo})
			,A.DOC_TYP_CD = (SELECT 	Z.DOC_TYP_CD
								FROM 	TBCORCOR201M Z
								WHERE 	Z.REQ_DVSN_CD = #{reqDvsnCd}
								AND		Z.ACP_YR = #{acpYr}
								AND		Z.CVPT_ACP_NO = #{cvptAcpNo})
	WHERE	A.REQ_DVSN_CD = #{sucReqDvsnCd}
	AND		A.ACP_YR = #{sucAcpYr}
	AND		A.CVPT_ACP_NO = #{sucCvptAcpNo}

