mapper.select:
/* kr.go.bai.eip.cor.cor.dao.COR002007EMapper.CSPDCP418PMainSelect */
SELECT A.REQ_DVSN_CD , A.ACP_YR , A.CVPT_ACP_NO , A.ACP_YR||'-부감-'||C.RM_CVPT_ACP_NO AS ACP_NO , DECODE(C.REQR_KND_CD,'1',C.CVPTR_NM,'2',C.RPST_PEN_NM,'3',C.RPST_PEN_NM,'4',C.CVPTR_NM) AS CVPTR_NM , DECODE(C.ZCD,NULL,NULL,C.AD||' '||C.DTL_AD) AS AD , C.ZCD , B.CVPT_OPM_SRNO , CASE WHEN D.OPM_TXT IS NULL AND #{dge} IS NULL THEN (SELECT CVPT_DOC_TXT FROM TBCORCOR203M Z WHERE A.REQ_DVSN_CD = Z.REQ_DVSN_CD AND A.ACP_YR = Z.ACP_YR AND A.CVPT_ACP_NO = Z.CVPT_ACP_NO AND Z.CVPT_DOC_DVSN_CD = 'A3') WHEN D.OPM_TXT IS NULL AND #{dge} IS NOT NULL THEN (SELECT CVPT_DOC_TXT FROM TBCORCOR203M Z WHERE A.REQ_DVSN_CD = Z.REQ_DVSN_CD AND A.ACP_YR = Z.ACP_YR AND A.CVPT_ACP_NO = Z.CVPT_ACP_NO AND Z.CVPT_DOC_DVSN_CD = 'B4' AND Z.DGE = #{dge}) ELSE D.OPM_TXT END AS OPM_TXT , B.ONR_DOC_MNG_CRD_ID , NVL(B.CVPT_IFM_DVSN_CD,#{cvptIfmDvsnCd}) AS CVPT_IFM_DVSN_CD , F_CODE_NM('1000370',NVL(B.CVPT_IFM_DVSN_CD,#{cvptIfmDvsnCd})) AS TITLE , #{dge} AS DGE , C.TIT_NM , DECODE(C.TNB,NULL,C.HPN,C.TNB) AS TEL , E.FL_RUT_NM , E.SYS_FL_NM , DECODE(C.REQR_KND_CD,'1',C.CVPTR_NM,'2',C.RPST_PEN_NM,'3',C.RPST_PEN_NM,'4',C.CVPTR_NM) || ' 귀하 (우'||C.ZCD || ' ' || C.AD || ' ' || C.DTL_AD || ')' AS RECEIVER FROM TBCORCOR201M A , TBCORCOR301M B , TBCORCOR101M C , TBCORCOR302M D , TBDCMACM103L E WHERE 1=1 AND A.REQ_DVSN_CD = #{reqDvsnCd} AND A.ACP_YR = #{acpYr} AND A.CVPT_ACP_NO = #{cvptAcpNo} AND A.REQ_DVSN_CD = C.REQ_DVSN_CD AND A.ACP_YR = C.ACP_YR AND A.CVPT_ACP_NO = C.CVPT_ACP_NO AND A.REQ_DVSN_CD = B.REQ_DVSN_CD(+) AND A.ACP_YR = B.ACP_YR(+) AND A.CVPT_ACP_NO = B.CVPT_ACP_NO(+) AND B.CVPT_IFM_DVSN_CD(+) = #{cvptIfmDvsnCd} AND B.REQ_DVSN_CD = D.REQ_DVSN_CD(+) AND B.CVPT_ACP_NO = D.CVPT_ACP_NO(+) AND B.ACP_YR = D.ACP_YR(+) AND B.CVPT_OPM_SRNO = D.CVPT_OPM_SRNO(+) AND B.ONR_DOC_MNG_CRD_ID = E.ONR_DOC_MNG_CRD_ID(+)
AND B.DGE(+) = #{dge}

/* kr.go.bai.eip.cor.cor.dao.COR002007EMapper.CSPDCP418P301KeySelect */

	SELECT 	TO_CHAR(NVL(MAX(TO_NUMBER(CVPT_OPM_SRNO)),0)+1) AS CVPT_OPM_SRNO
	FROM 	TBCORCOR301M
	WHERE 	1=1
	AND 	REQ_DVSN_CD = #{reqDvsnCd}
	AND 	ACP_YR = #{acpYr}
	AND 	CVPT_ACP_NO = #{cvptAcpNo}

