        					,FNL_DEAL_DPT_CD = #{fnlDealDptCd}
        					,FNL_MNU_ID = #{fnlMnuId}
        					,FNL_MDF_DH = SYSDATE
        	WHEN NOT MATCHED THEN
				INSERT 	(	REQ_DVSN_CD
							,ACP_YR
							,CVPT_ACP_NO
							,CVPT_OPM_SRNO
	       					,OPM_TXT
	       					,FRT_USR_ID
	       					,FNL_USR_ID
	       					,FNL_DEAL_DPT_CD
	       					,FNL_MNU_ID
	       					,FRT_REGI_DH
	       					,FNL_MDF_DH
		       				)
		       		VALUES	(	#{reqDvsnCd}
								,#{acpYr}
								,#{cvptAcpNo}
								,#{cvptOpmSrno}
								,#{opmTxt}
		       					,#{frtUsrId}
		       					,#{fnlUsrId}
		       					,#{fnlDealDptCd}
		       					,#{fnlMnuId}
		       					,SYSDATE
		       					,SYSDATE
		       				)

