/* kr.go.bai.eip.cor.cor.dao.COR002007EMapper.COR003003E301OnnaraSelect */
SELECT A.REQ_DVSN_CD , A.ACP_YR , A.CVPT_ACP_NO , A.ACP_YR||'-부감-'||C.RM_CVPT_ACP_NO AS ACP_NO , DECODE(C.REQR_KND_CD,'1',C.CVPTR_NM,'2',C.RPST_PEN_NM,'3',C.RPST_PEN_NM,'4',C.CVPTR_NM) AS CVPTR_NM , DECODE(C.ZCD,NULL,NULL,C.AD||' '||C.DTL_AD) AS AD , C.ZCD , B.CVPT_OPM_SRNO , CASE WHEN #{dge} IS NULL THEN A.ANS_TXT WHEN #{dge} IS NOT NULL THEN (SELECT Z.ANS_TXT FROM TBCORCOR401H Z WHERE A.REQ_DVSN_CD = Z.REQ_DVSN_CD AND A.ACP_YR = Z.ACP_YR AND A.CVPT_ACP_NO = Z.CVPT_ACP_NO AND Z.DGE = #{dge}) ELSE NULL END AS OPM_TXT , B.ONR_DOC_MNG_CRD_ID , NVL(B.CVPT_IFM_DVSN_CD,#{cvptIfmDvsnCd}) AS CVPT_IFM_DVSN_CD , F_CODE_NM('1000967',NVL(B.CVPT_IFM_DVSN_CD,#{cvptIfmDvsnCd}))||' (제'||A.ACP_YR||'-부감-'||C.RM_CVPT_ACP_NO||'호)' AS TITLE , #{dge} AS DGE , C.TIT_NM , DECODE(C.TNB,NULL,C.HPN,C.TNB) AS TEL , DECODE(C.REQR_KND_CD,'1',C.CVPTR_NM,'2',C.RPST_PEN_NM,'3',C.RPST_PEN_NM,'4',C.CVPTR_NM) || ' 귀하 (우'||C.ZCD || ' ' || C.AD || ' ' || C.DTL_AD || ')' AS RECEIVER FROM TBCORCOR201M A , TBCORCOR301M B , TBCORCOR101M C , TBCORCOR302M D WHERE 1=1 AND A.REQ_DVSN_CD = #{reqDvsnCd} AND A.ACP_YR = #{acpYr} AND A.CVPT_ACP_NO = #{cvptAcpNo} AND A.REQ_DVSN_CD = C.REQ_DVSN_CD AND A.ACP_YR = C.ACP_YR AND A.CVPT_ACP_NO = C.CVPT_ACP_NO AND A.REQ_DVSN_CD = B.REQ_DVSN_CD(+) AND A.ACP_YR = B.ACP_YR(+) AND A.CVPT_ACP_NO = B.CVPT_ACP_NO(+) AND B.CVPT_IFM_DVSN_CD(+) = #{cvptIfmDvsnCd} AND B.REQ_DVSN_CD = D.REQ_DVSN_CD(+) AND B.CVPT_ACP_NO = D.CVPT_ACP_NO(+) AND B.ACP_YR = D.ACP_YR(+) AND B.CVPT_OPM_SRNO = D.CVPT_OPM_SRNO(+)
AND B.DGE(+) = #{dge}

mapper.insert:
		/* kr.go.bai.eip.cor.cor.dao.COR002007EMapper.COR003003E301OnnaraMerge */

        	MERGE INTO 	TBCORCOR301M A
        	USING 	(	SELECT 	#{reqDvsnCd} AS REQ_DVSN_CD
        						,#{acpYr} AS ACP_YR
        						,#{cvptAcpNo} AS CVPT_ACP_NO
        						,#{cvptOpmSrno} AS CVPT_OPM_SRNO
        				FROM DUAL
        			) B
        	ON		(	A.REQ_DVSN_CD = B.REQ_DVSN_CD
        				AND	A.ACP_YR = B.ACP_YR
        				AND A.CVPT_ACP_NO = B.CVPT_ACP_NO
        				AND A.CVPT_OPM_SRNO = B.CVPT_OPM_SRNO
        			)
        	WHEN MATCHED THEN
        			UPDATE
        			SET		ONR_DOC_MNG_CRD_ID = #{onrDocMngCrdId}
        					,ADMN_INF_HNDL_NO = #{admnInfHndlNo}
        					,FNL_USR_ID = #{fnlUsrId}
        					,FNL_DEAL_DPT_CD = #{fnlDealDptCd}
        					,FNL_MNU_ID = #{fnlMnuId}
        					,FNL_MDF_DH = SYSDATE
        	WHEN NOT MATCHED THEN
		        	INSERT 		(	REQ_DVSN_CD
		        					,ACP_YR
		        					,CVPT_ACP_NO
		        					,CVPT_OPM_SRNO
		        					,DGE
		        					,CVPT_IFM_DVSN_CD
		        					,CVPT_OPM_APV_HNDL_STA_CD
		        					,ADMN_INF_HNDL_NO
		        					,ONR_DOC_MNG_CRD_ID
		        					,FRT_USR_ID
		        					,FNL_USR_ID
		        					,FNL_DEAL_DPT_CD
		        					,FNL_MNU_ID
		        					,FRT_REGI_DH
		        					,FNL_MDF_DH
		        				)
		        		VALUES	(	#{reqDvsnCd}
									,#{acpYr}
									,#{cvptAcpNo}
		        					,#{cvptOpmSrno}
		        					,#{dge}
		        					,#{cvptIfmDvsnCd}
		        					,'10'
		        					,#{admnInfHndlNo}
		        					,#{onrDocMngCrdId}
									,#{frtUsrId}
									,#{fnlUsrId}
									,#{fnlDealDptCd}
									,#{fnlMnuId}
									,SYSDATE
									,SYSDATE
								)

