	/* kr.go.bai.eip.cor.cor.dao.COR002007EMapper.COR003003ECvptDocUpdate */
		MERGE INTO 	TBCORCOR301M A
        	USING 	(	SELECT 	#{reqDvsnCd} AS REQ_DVSN_CD
        						,#{acpYr} AS ACP_YR
        						,#{cvptAcpNo} AS CVPT_ACP_NO
