        				AND	A.ACP_YR = B.ACP_YR
        				AND A.CVPT_ACP_NO = B.CVPT_ACP_NO
        				AND A.CVPT_OPM_SRNO = B.CVPT_OPM_SRNO
        			)
       	WHEN MATCHED THEN

		UPDATE
			SET		ONR_DOC_MNG_CRD_ID = 'testDocId1'
	    			,CVPT_OPM_APV_HNDL_STA_CD = '30'
	    			,CVPT_IFM_DVSN_CD = 'C'
					,FNL_MDF_DH = SYSDATE
