					,FNL_DEAL_DPT_CD = #{fnlDealDptCd}
					,FNL_MNU_ID = #{fnlMnuId}
			WHERE	REQ_DVSN_CD = #{reqDvsnCd}
	 		AND		ACP_YR = #{acpYr}
	 		AND		CVPT_ACP_NO = #{cvptAcpNo}

 		WHEN NOT MATCHED THEN
	 		INSERT
				(	REQ_DVSN_CD
					,ACP_YR
					,CVPT_ACP_NO
					,CVPT_OPM_SRNO
					,ONR_DOC_MNG_CRD_ID
					,CVPT_OPM_APV_HNDL_STA_CD
					,FNL_MDF_DH
					,FNL_USR_ID
					,FNL_DEAL_DPT_CD
					,FNL_MNU_ID
					,FRT_USR_ID
					,FRT_REGI_DH
					,CVPT_IFM_DVSN_CD
				)
	       	VALUES	(	#{reqDvsnCd}
	       			,#{acpYr}
	       			,#{cvptAcpNo}
	       			,'1'
	       			,'testDocId1'
	       			,'30'
