/* kr.go.bai.eip.csp.dcp.dao.CSPDCP111Mapper.CSPDCP111EMainSelect */
SELECT A.REQ_DVSN_CD ,A.ACP_YR ,A.CVPT_ACP_NO ,DECODE(A.RM_CVPT_ACP_NO,NULL,'-',A.ACP_YR||'-'|| F_CODE_NM('1000949', A.RM_ACP_KND_CD) ||'-'||A.RM_CVPT_ACP_NO) AS ACP_NO ,A.RM_CVPT_ACP_NO ,A.CVPT_KND_CD ,A.REQR_KND_CD ,A.ACP_RUT_CD ,A.ACP_RUT_DTL_CD ,A.CVPTR_NM ,A.BTDT ,A.JB_NM ,A.RPST_PEN_NM ,A.BZN ,A.CPNM_NM ,A.ZCD ,A.AD ,A.DTL_AD ,DECODE(A.TNB,NULL,'',SUBSTR(A.TNB,1,INSTR(A.TNB,'-',1,1)-1)) TNB1 ,DECODE(A.TNB,NULL,'',SUBSTR(A.TNB,INSTR(A.TNB,'-',1,1)+1,INSTR(A.TNB,'-',1,2)-INSTR(A.TNB,'-',1,1)-1)) AS TNB2 ,DECODE(A.TNB,NULL,'',SUBSTR(A.TNB,INSTR(A.TNB,'-',1,2)+1)) TNB3 ,DECODE(A.HPN,NULL,'',SUBSTR(HPN,1,INSTR(HPN,'-',1,1)-1)) HPN1 ,DECODE(A.HPN,NULL,'',SUBSTR(A.HPN,INSTR(A.HPN,'-',1,1)+1,INSTR(A.HPN,'-',1,2)-INSTR(A.HPN,'-',1,1)-1)) AS HPN2 ,DECODE(A.HPN,NULL,'',SUBSTR(A.HPN,INSTR(A.HPN,'-',1,2)+1)) HPN3 ,A.HPN ,A.EML_ID ,A.EML_DMI_NM ,A.OPEN_YN ,A.MNG_TYP_CD ,A.RSLT_NTF_WAY_CD ,A.TIT_NM ,A.ACP_DPT_CD ,NVL2(A.RAL_ACP_DPT_CD, (SELECT S1.CD_NM FROM TBDCMACM013C S1 WHERE S1.CMM_CD_DVSN_CD = '1000144' AND S1.CD = A.RAL_ACP_DPT_CD) ,(SELECT S1.DPT_NM FROM TBDCMACM002M S1 WHERE S1.DPT_CD = A.ACP_DPT_CD) ) AS ACP_DPT_NM ,( SELECT S1.CD_NM FROM TBDCMACM013C S1 WHERE S1.CMM_CD_DVSN_CD = '1000144' AND S1.CD = A.RAL_ACP_DPT_CD ) AS RAL_ACP_DPT_NM ,A.RCEP_ID ,F_USR_INFO_BY_ID(A.RCEP_ID,'2') AS RCEP_NM ,TO_CHAR(A.ACP_DH,'YYYY-MM-DD HH24:MI') AS ACP_DH ,A.CVPT_HNDL_STA_CD ,A.AUD_INF_TG_YN ,A.SMG_CVPT_APL_NO ,A.SMG_CVPT_ACP_NO ,A.SMG_FNL_HNDL_STA_CD ,A.SMG_CVPT_APL_DH ,A.SMG_FRT_CVPT_APL_GCC_ORG_CD ,A.SMG_CAT_HST_SRNO ,TO_CHAR(A.CVPT_WDW_DH,'YYYY-MM-DD HH24:MI') AS CVPT_WDW_DH ,A.CVPT_WDW_RSN ,B.CVPT_TXT ,B.CVPT_SBT ,C.CLM_NM ,C.WRK_PLAC_NM ,DECODE(C.TNB,NULL,'',SUBSTR(C.TNB,1,INSTR(C.TNB,'-',1,1)-1)) CLM_TNB1 ,DECODE(C.TNB,NULL,'',SUBSTR(C.TNB,INSTR(C.TNB,'-',1,1)+1,INSTR(C.TNB,'-',1,2)-INSTR(C.TNB,'-',1,1)-1)) AS CLM_TNB2 ,DECODE(C.TNB,NULL,'',SUBSTR(C.TNB,INSTR(C.TNB,'-',1,2)+1)) CLM_TNB3 ,C.ZCD AS CLM_ZCD ,C.AD AS CLM_AD ,C.DTL_AD AS CLM_DTL_AD ,A.DOC_ID ,F_CODE_NM('1000361',A.CVPT_HNDL_STA_CD) AS CVPT_HNDL_STA_NM ,A.HMP_APL_NO ,NVL(A.HMP_APL_NO,A.SMG_CVPT_APL_NO) AS HMP_SMG_APL_NO ,TO_CHAR(A.CVPT_WDW_DH,'YYYY-MM-DD HH24:MI') AS CVPT_WDW_DH ,NVL(A.INNER_YN,'N') AS INNER_YN ,A.INNER_ORG_NM ,A.CVPT_KND_DVSN_CD ,A.MAIN_FUNC_CAT_CD ,(SELECT Z.USR_DFN_TXT_1 FROM TBDCMACM013C Z WHERE Z.CMM_CD_DVSN_CD ='1000948' AND Z.CD = A.TG_ORG_CAT_CD) AS TG_ORG_CAT_CD1 ,(SELECT Z.USR_DFN_TXT_2 FROM TBDCMACM013C Z WHERE Z.CMM_CD_DVSN_CD ='1000948' AND Z.CD = A.TG_ORG_CAT_CD) AS TG_ORG_CAT_CD2 ,A.TG_ORG_CAT_CD AS TG_ORG_CAT_CD3 ,A.MAIN_FUNC_CAT_CD FROM TBCSPDCP101M A ,TBCSPDCP102M B ,TBCSPDCP103M C WHERE 1=1 AND A.REQ_DVSN_CD = #{reqDvsnCd} AND A.ACP_YR = #{acpYr} AND A.REQ_DVSN_CD = B.REQ_DVSN_CD AND A.ACP_YR = B.ACP_YR AND A.CVPT_ACP_NO = B.CVPT_ACP_NO AND A.REQ_DVSN_CD = C.REQ_DVSN_CD(+) AND A.ACP_YR = C.ACP_YR(+) AND A.CVPT_ACP_NO = C.CVPT_ACP_NO(+)
AND A.CVPT_ACP_NO = #{cvptAcpNo}
AND A.RM_CVPT_ACP_NO = #{rmCvptAcpNo}
AND A.RM_ACP_KND_CD = #{rmAcpKndCd}

/* kr.go.bai.eip.csp.dcp.dao.CSPDCP111Mapper.CSPDCP111EOrgSelect */
SELECT A.REQ_DVSN_CD ,A.ACP_YR ,A.CVPT_ACP_NO ,A.AUD_TG_ORG_CD ,B.ORG_NM ,( SELECT Z.USR_DFN_TXT_1 FROM TBDCMACM013C Z WHERE Z.CMM_CD_DVSN_CD ='1000128' AND Z.CD = B.PUB_DVSN_CD) AS PUB_DVSN_CD ,F_ORG_HRK_NM(B.COPT_SG_CD) AS COPT_SG_NM ,( SELECT F_CODE_NM('1000447',Z.USR_DFN_TXT_1) FROM TBDCMACM013C Z WHERE Z.CMM_CD_DVSN_CD ='1000128' AND Z.CD = B.PUB_DVSN_CD) AS PUB_DVSN_NM FROM TBCSPDCP104L A ,TBDCMACM015M B ,TBCSPDCP101M C WHERE 1=1 AND C.REQ_DVSN_CD = #{reqDvsnCd} AND C.ACP_YR = #{acpYr} AND C.REQ_DVSN_CD = A.REQ_DVSN_CD AND C.ACP_YR = A.ACP_YR AND C.CVPT_ACP_NO = A.CVPT_ACP_NO AND A.AUD_TG_ORG_CD = B.ORG_CD
AND C.CVPT_ACP_NO = #{cvptAcpNo}
AND C.CVPT_ACP_NO = #{cvptAcpNo}
AND C.RM_CVPT_ACP_NO = #{rmCvptAcpNo}
AND C.REQ_DVSN_CD = #{rmAcpKndCd}

mapper.insert:
		/* kr.go.bai.eip.csp.dcp.dao.CSPDCP111Mapper.CSPDCP111EDCP103Insert */

        	MERGE INTO 	TBCSPDCP103M A
        	USING 	(	SELECT 	#{reqDvsnCd} AS REQ_DVSN_CD
        						,#{acpYr} AS ACP_YR
        						,#{cvptAcpNo} AS CVPT_ACP_NO
        				FROM DUAL
        			) B
        	ON		(	A.REQ_DVSN_CD = B.REQ_DVSN_CD
        				AND	A.ACP_YR = B.ACP_YR
        				AND A.CVPT_ACP_NO = B.CVPT_ACP_NO
        			)
        	WHEN MATCHED THEN
        			UPDATE
        			SET		CLM_NM = #{clmNm}
        					,WRK_PLAC_NM = #{wrkPlacNm}
        					,TNB = DECODE(#{clmTnb1},NULL,'',#{clmTnb1}||'-'||#{clmTnb2}||'-'||#{clmTnb3})
        					,ZCD = #{clmZcd}
        					,AD = #{clmAd}
        					,DTL_AD = #{clmDtlAd}
        					,FNL_USR_ID = #{fnlUsrId}
        					,FNL_DEAL_DPT_CD = #{fnlDealDptCd}
        					,FNL_MNU_ID = #{fnlMnuId}
        					,FNL_MDF_DH = SYSDATE
        	WHEN NOT MATCHED THEN
		        	INSERT 		(	REQ_DVSN_CD
		        					,ACP_YR
		        					,CVPT_ACP_NO
		        					,CLM_NM
		        					,WRK_PLAC_NM
		        					,TNB
		        					,ZCD
		        					,AD
		        					,DTL_AD
		        					,FRT_USR_ID
		        					,FNL_USR_ID
		        					,FNL_DEAL_DPT_CD
		        					,FNL_MNU_ID
		        					,FRT_REGI_DH
		        					,FNL_MDF_DH
		        				)
		        		VALUES	(	#{reqDvsnCd}
									,#{acpYr}
									,#{cvptAcpNo}
									,#{clmNm}
		        					,#{wrkPlacNm}
		        					,DECODE(#{clmTnb1},NULL,'',#{clmTnb1}||'-'||#{clmTnb2}||'-'||#{clmTnb3})
		        					,#{clmZcd}
		        					,#{clmAd}
		        					,#{clmDtlAd}
									,NVL(#{frtUsrId},#{fnlUsrId})
									,#{fnlUsrId}
									,#{fnlDealDptCd}
									,#{fnlMnuId}
									,SYSDATE
									,SYSDATE
								)

