mapper.select:
/* kr.go.bai.eip.csp.dcp.dao.CSPDCP112Mapper.CSPDCP112PSmgKeySelect */

	SELECT	DT.INTERFACE_KEY
	FROM 
			(SELECT A.INTERFACE_KEY
			FROM	T_IC_TARGET_SRC_INTERFACE A
			        ,TBCSPDCP101M B
			WHERE	B.REQ_DVSN_CD = #{reqDvsnCd}
			AND		B.ACP_YR	= #{acpYr}
			AND		B.CVPT_ACP_NO = #{cvptAcpNo}
			AND		A.PETI_NO = B.SMG_CVPT_APL_NO
			AND		A.PETI_GUBUN = B.SMG_FRT_HNDL_STA_CD
			ORDER BY INTERFACE_KEY DESC
			)DT
	WHERE ROWNUM =1

/* kr.go.bai.eip.csp.dcp.dao.CSPDCP112Mapper.CSPDCP112PSmgKeySelect2 */

	SELECT	SMG_FRT_CON_NO
	FROM	TBCSPDCP101M
	WHERE	REQ_DVSN_CD = #{reqDvsnCd}
	AND		ACP_YR	= #{acpYr}
	AND		CVPT_ACP_NO = #{cvptAcpNo}

mapper.update:
		/* kr.go.bai.eip.csp.dcp.dao.CSPDCP112Mapper.CSPDCP112P101Update */

			UPDATE	TBCSPDCP101M
			SET		CVPT_HNDL_STA_CD = '80'
					,FNL_USR_ID = #{fnlUsrId}
   					,FNL_DEAL_DPT_CD = #{fnlDealDptCd}
   					,FNL_MNU_ID = #{fnlMnuId}
   					,FNL_MDF_DH = SYSDATE
			WHERE	REQ_DVSN_CD = #{reqDvsnCd}
			AND		ACP_YR	= #{acpYr}
			AND		CVPT_ACP_NO = #{cvptAcpNo}

mapper.insert:
		/* kr.go.bai.eip.csp.dcp.dao.CSPDCP112Mapper.CSPDCP112P107Insert */

			INSERT INTO	TBCSPDCP107H
        				(	REQ_DVSN_CD
        					,ACP_YR
        					,CVPT_ACP_NO
        					,SRNO
        					,BACK_RSN
        					,BACK_GCC_ORG_CD
        					,FRT_USR_ID
        					,FNL_USR_ID
        					,FNL_DEAL_DPT_CD
        					,FNL_MNU_ID
        					,FRT_REGI_DH
        					,FNL_MDF_DH
        				)
        		VALUES	(	#{reqDvsnCd}
							,#{acpYr}
							,#{cvptAcpNo}
							,(	SELECT NVL(MAX(SRNO),0)+1
								  FROM TBCSPDCP107H
								 WHERE 1=1
							       AND REQ_DVSN_CD = #{reqDvsnCd}
							       AND ACP_YR = #{acpYr}
							       AND CVPT_ACP_NO = #{cvptAcpNo}
							 )
							,#{backRsn}
							,'1140100'
							,F_USR_INFO(#{sCnb},'1')
							,#{fnlUsrId}
							,#{fnlDealDptCd}
							,#{fnlMnuId}
							,SYSDATE
							,SYSDATE
						)

		/* kr.go.bai.eip.csp.dcp.dao.CSPDCP112Mapper.CSPDCP112P202Insert */

			INSERT INTO	TBCSPDCP202H
        				(	REQ_DVSN_CD
        					,ACP_YR
        					,CVPT_ACP_NO
        					,CVPT_DTB_HST_SRNO
        					,DTB_DPT_CD
        					,DTB_PEN_ID
        					,CVPT_HNDL_STA_CD
        					,FRT_USR_ID
        					,FNL_USR_ID
        					,FNL_DEAL_DPT_CD
        					,FNL_MNU_ID
        					,FRT_REGI_DH
        					,FNL_MDF_DH
        				)
        		VALUES	(	#{reqDvsnCd}
							,#{acpYr}
							,#{cvptAcpNo}
							,(	SELECT NVL(MAX(CVPT_DTB_HST_SRNO),0)+1
								  FROM TBCSPDCP202H
								 WHERE 1=1
							       AND REQ_DVSN_CD = #{reqDvsnCd}
							       AND ACP_YR = #{acpYr}
							       AND CVPT_ACP_NO = #{cvptAcpNo}
							 )
							,#{fnlDealDptCd}
							,F_USR_INFO(#{sCnb},'1')
							,'80'
							,F_USR_INFO(#{sCnb},'1')
							,#{fnlUsrId}
							,#{fnlDealDptCd}
							,#{fnlMnuId}
							,SYSDATE
							,SYSDATE
						)
