mapper.select:
/* kr.go.bai.eip.csp.dcp.dao.CSPDCP123Mapper.CSPDCP123PTxtACheck */
SELECT CASE WHEN COUNT(1) = 0 THEN 'I' ELSE 'U' END AS TXTAIUCHECK
  FROM TBCSPDCP505M
 WHERE 1=1
   AND YM = #{ym}
   AND INF_RPRT_NO = #{infRprtNo}
   AND INF_RPRT_DOC_DVSN_CD = 'A'

/* kr.go.bai.eip.csp.dcp.dao.CSPDCP123Mapper.CSPDCP123PTxtBCheck */
SELECT CASE WHEN COUNT(1) = 0 THEN 'I' ELSE 'U' END AS TXTAIUCHECK
  FROM TBCSPDCP505M
 WHERE 1=1
   AND YM = #{ym}
   AND INF_RPRT_NO = #{infRprtNo}
   AND INF_RPRT_DOC_DVSN_CD = 'B'

mapper.insert:
/* kr.go.bai.eip.csp.dcp.dao.CSPDCP123Mapper.CSPDCP123PTxtInsert */

 INSERT INTO TBCSPDCP505M(
		             YM
		            ,INF_RPRT_NO
		            ,INF_RPRT_DOC_DVSN_CD
		            ,DOC_TXT
		            ,FRT_USR_ID
		            ,FNL_USR_ID
		            ,FNL_DEAL_DPT_CD
		            ,FNL_MNU_ID
		            ,FRT_REGI_DH
		            ,FNL_MDF_DH 
		            )
		            VALUES(
		            #{ym}
		            ,#{infRprtNo}
		            ,#{infRprtDocDvsnCd}
		            ,(CASE WHEN #{infRprtDocDvsnCd} = 'A' THEN #{textA} ELSE #{textB} END) 
		            ,#{frtUsrId}
		            ,#{fnlUsrId}
		            ,#{fnlDealDptCd}
		            ,#{fnlMnuId}
		            ,SYSDATE
		            ,SYSDATE
		            )

mapper.update:
/* kr.go.bai.eip.csp.dcp.dao.CSPDCP123Mapper.CSPDCP123PTxtUpdate */
 UPDATE TBCSPDCP505M 
    SET FNL_USR_ID         = #{fnlUsrId}
       ,FNL_DEAL_DPT_CD    = #{fnlDealDptCd}
       ,FNL_MNU_ID         = #{fnlMnuId}
       ,FNL_MDF_DH         = SYSDATE
       ,DOC_TXT            = (CASE WHEN #{infRprtDocDvsnCd} = 'A' THEN #{textA} ELSE #{textB} END)
  WHERE  1=1
    AND YM = #{ym}
    AND INF_RPRT_NO = #{infRprtNo}
    AND INF_RPRT_DOC_DVSN_CD = #{infRprtDocDvsnCd}
