mapper.select:
/* kr.go.bai.eip.csp.dcp.dao.CSPDCP141Mapper.CSPDCP141QSelect2 */
WITH DATA AS ( SELECT D1.CVPT_KND_CD , D1.ACP_RUT_CD , D1.CVPT_HNDL_STA_CD , D4.CVPT_HNDL_DVSN_CD FROM TBCSPDCP101M D1 , TBCSPDCP201M D2 ,( SELECT S1.REQ_DVSN_CD , S1.CVPT_ACP_NO , S1.ACP_YR , S1.CVPT_HNDL_DVSN_CD FROM TBCSPDCP205L S1 WHERE ( S1.REQ_DVSN_CD, S1.ACP_YR, S1.CVPT_ACP_NO, S1.SRNO ) IN ( SELECT REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO, MIN(SRNO) FROM TBCSPDCP205L GROUP BY REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO ) ) D4 WHERE 1=1 AND D1.REQ_DVSN_CD = D2.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D2.CVPT_ACP_NO(+) AND D1.ACP_YR = D2.ACP_YR(+) AND D1.REQ_DVSN_CD = '1' AND D1.REQ_DVSN_CD = D4.REQ_DVSN_CD(+) AND D1.ACP_YR = D4.ACP_YR(+) AND D1.CVPT_ACP_NO = D4.CVPT_ACP_NO(+) AND D1.RM_CVPT_ACP_NO IS NOT NULL /* AND D1.CVPT_HNDL_STA_CD = '60'*/
AND TO_CHAR(D1.ACP_DH, 'YYYYMMDD') BETWEEN TO_CHAR(TO_DATE(#{acpDhs}, 'YYYYMMDD'), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{acpDhe}, 'YYYYMMDD'), 'YYYYMMDD')
AND TO_CHAR(D2.FNL_HNDL_DH, 'YYYYMMDD') BETWEEN TO_CHAR(TO_DATE(#{acpDhs}, 'YYYYMMDD'), 'YYYYMMDD') AND TO_CHAR(TO_DATE(#{acpDhe}, 'YYYYMMDD'), 'YYYYMMDD')
), PRE_DATA AS ( SELECT D1.CVPT_KND_CD , D1.ACP_RUT_CD , D1.CVPT_HNDL_STA_CD , D4.CVPT_HNDL_DVSN_CD FROM TBCSPDCP101M D1 , TBCSPDCP201M D2 ,( SELECT S1.REQ_DVSN_CD , S1.CVPT_ACP_NO , S1.ACP_YR , S1.CVPT_HNDL_DVSN_CD FROM TBCSPDCP205L S1 WHERE ( S1.REQ_DVSN_CD, S1.ACP_YR, S1.CVPT_ACP_NO, S1.SRNO ) IN ( SELECT REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO, MIN(SRNO) FROM TBCSPDCP205L GROUP BY REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO ) ) D4 WHERE 1=1 AND D1.REQ_DVSN_CD = D2.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D2.CVPT_ACP_NO(+) AND D1.ACP_YR = D2.ACP_YR(+) AND D1.REQ_DVSN_CD = D4.REQ_DVSN_CD(+) AND D1.ACP_YR = D4.ACP_YR(+) AND D1.CVPT_ACP_NO = D4.CVPT_ACP_NO(+) AND D1.REQ_DVSN_CD = '1' /*AND D1.CVPT_HNDL_STA_CD = '60'*/ AND D1.RM_CVPT_ACP_NO IS NOT NULL
AND D1.ACP_DH BETWEEN TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{acpDhs}, 'YYYYMMDD'), -12),'dd'), 'YYYYMMDD') AND TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{acpDhe}, 'YYYYMMDD'), -12),'dd'), 'YYYYMMDD')
AND D2.FNL_HNDL_DH BETWEEN TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{acpDhs}, 'YYYYMMDD'), -12),'dd'), 'YYYYMMDD') AND TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{acpDhe}, 'YYYYMMDD'), -12),'dd'), 'YYYYMMDD')
) SELECT NULL AS CD , '합계' AS CD_NM , COUNT(1) AS CNT , CASE WHEN COUNT(1) = 0 THEN 0 ELSE (COUNT(1)/COUNT(1)) * 100 END AS PER , NVL(SUM(DECODE(ACP_RUT_CD, '1', 1, 0 )), 0) AS ACP_RUT_CD_1 /*서신*/, NVL(SUM(DECODE(ACP_RUT_CD, '2', 1, 0 )), 0) AS ACP_RUT_CD_2 /*방문*/, NVL(SUM(DECODE(ACP_RUT_CD, '3', 1, 0 )), 0) AS ACP_RUT_CD_3 /*이첩*/, NVL(SUM(DECODE(ACP_RUT_CD, '4', 1, 0 )), 0) AS ACP_RUT_CD_4 /*전화*/, NVL(SUM(DECODE(ACP_RUT_CD, '5', 1, 0 )), 0) AS ACP_RUT_CD_5 /*Fax*/, NVL(SUM(DECODE(ACP_RUT_CD, '7', 1, 0 )), 0) AS ACP_RUT_CD_7 /*홈페이지*/, NVL(SUM(DECODE(ACP_RUT_CD, '8', 1, 0 )), 0) AS ACP_RUT_CD_8 /*신문고*/ FROM DATA UNION ALL SELECT NULL AS CD , '감사민원' AS CD_NM , SUM(CASE WHEN (CVPT_HNDL_DVSN_CD IN ('10','20','30','40','50','60','80') OR CVPT_HNDL_DVSN_CD IS NULL) THEN 1 ELSE 0 END) AS CNT , CASE WHEN COUNT(1) = 0 THEN 0 ELSE (NVL(SUM( CASE WHEN (CVPT_HNDL_DVSN_CD IN ('10','20','30','40','50','60','80') OR CVPT_HNDL_DVSN_CD IS NULL) THEN 1 ELSE 0 END),0)/COUNT(1)) * 100 END AS PER , NVL(SUM(CASE WHEN ACP_RUT_CD = '1' AND (CVPT_HNDL_DVSN_CD IN ('10','20','30','40','50','60','80') OR CVPT_HNDL_DVSN_CD IS NULL) THEN 1 ELSE 0 END ), 0) AS ACP_RUT_CD_1 /*서신*/, NVL(SUM(CASE WHEN ACP_RUT_CD = '2' AND (CVPT_HNDL_DVSN_CD IN ('10','20','30','40','50','60','80') OR CVPT_HNDL_DVSN_CD IS NULL) THEN 1 ELSE 0 END ), 0) AS ACP_RUT_CD_2 /*방문*/, NVL(SUM(CASE WHEN ACP_RUT_CD = '3' AND (CVPT_HNDL_DVSN_CD IN ('10','20','30','40','50','60','80') OR CVPT_HNDL_DVSN_CD IS NULL) THEN 1 ELSE 0 END ), 0) AS ACP_RUT_CD_3 /*이첩*/, NVL(SUM(CASE WHEN ACP_RUT_CD = '4' AND (CVPT_HNDL_DVSN_CD IN ('10','20','30','40','50','60','80') OR CVPT_HNDL_DVSN_CD IS NULL) THEN 1 ELSE 0 END ), 0) AS ACP_RUT_CD_4 /*전화*/, NVL(SUM(CASE WHEN ACP_RUT_CD = '5' AND (CVPT_HNDL_DVSN_CD IN ('10','20','30','40','50','60','80') OR CVPT_HNDL_DVSN_CD IS NULL) THEN 1 ELSE 0 END ), 0) AS ACP_RUT_CD_5 /*Fax*/, NVL(SUM(CASE WHEN ACP_RUT_CD = '7' AND (CVPT_HNDL_DVSN_CD IN ('10','20','30','40','50','60','80') OR CVPT_HNDL_DVSN_CD IS NULL) THEN 1 ELSE 0 END ), 0) AS ACP_RUT_CD_7 /*홈페이지*/, NVL(SUM(CASE WHEN ACP_RUT_CD = '8' AND (CVPT_HNDL_DVSN_CD IN ('10','20','30','40','50','60','80') OR CVPT_HNDL_DVSN_CD IS NULL) THEN 1 ELSE 0 END ), 0) AS ACP_RUT_CD_8 /*신문고*/ FROM DATA UNION ALL SELECT * FROM ( SELECT D3.CD , D3.CD_NM , NVL(D4.CNT, 0) AS CNT , CASE WHEN NVL(D4.CNTALL, 0) = 0 THEN 0 ELSE ROUND((D4.CNT/D4.CNTALL) * 100, 2) END AS PER , NVL(D4.ACP_RUT_CD_1, 0 ) AS ACP_RUT_CD_1 , NVL(D4.ACP_RUT_CD_2, 0 ) AS ACP_RUT_CD_2 , NVL(D4.ACP_RUT_CD_3, 0 ) AS ACP_RUT_CD_3 , NVL(D4.ACP_RUT_CD_4, 0 ) AS ACP_RUT_CD_4 , NVL(D4.ACP_RUT_CD_5, 0 ) AS ACP_RUT_CD_5 , NVL(D4.ACP_RUT_CD_7, 0 ) AS ACP_RUT_CD_7 , NVL(D4.ACP_RUT_CD_8, 0 ) AS ACP_RUT_CD_8 FROM (SELECT CD , CD_NM FROM TBDCMACM013C D2 WHERE D2.CMM_CD_DVSN_CD = '1000047' AND D2.CD <> '000000000' ORDER BY OTPT_SEQ ) D3, (SELECT D1.CVPT_KND_CD , COUNT(1) AS CNT , (SELECT COUNT(1) FROM DATA S1 WHERE 1=1) AS CNTALL , NVL(SUM(DECODE(ACP_RUT_CD, '1', 1, 0 )), 0) AS ACP_RUT_CD_1 , NVL(SUM(DECODE(ACP_RUT_CD, '2', 1, 0 )), 0) AS ACP_RUT_CD_2 , NVL(SUM(DECODE(ACP_RUT_CD, '3', 1, 0 )), 0) AS ACP_RUT_CD_3 , NVL(SUM(DECODE(ACP_RUT_CD, '4', 1, 0 )), 0) AS ACP_RUT_CD_4 , NVL(SUM(DECODE(ACP_RUT_CD, '5', 1, 0 )), 0) AS ACP_RUT_CD_5 , NVL(SUM(DECODE(ACP_RUT_CD, '7', 1, 0 )), 0) AS ACP_RUT_CD_7 , NVL(SUM(DECODE(ACP_RUT_CD, '8', 1, 0 )), 0) AS ACP_RUT_CD_8 FROM DATA D1 where (CVPT_HNDL_DVSN_CD IN ('10','20','30','40','50','60','80') OR CVPT_HNDL_DVSN_CD IS NULL) GROUP BY D1.CVPT_KND_CD ) D4 WHERE D3.CD = D4.CVPT_KND_CD(+) order by D3.CD) UNION ALL SELECT NULL AS CD , '일반민원' AS CD_NM , SUM(CASE WHEN CVPT_HNDL_DVSN_CD = '70' THEN 1 ELSE 0 END) AS CNT , CASE WHEN COUNT(1) = 0 THEN 0 ELSE (NVL(SUM( CASE WHEN (CVPT_HNDL_DVSN_CD = '70') THEN 1 ELSE 0 END),0)/COUNT(1)) * 100 END AS PER , NVL(SUM(CASE WHEN ACP_RUT_CD = '1' AND CVPT_HNDL_DVSN_CD = '70' THEN 1 ELSE 0 END ), 0) AS ACP_RUT_CD_1 /*서신*/, NVL(SUM(CASE WHEN ACP_RUT_CD = '2' AND CVPT_HNDL_DVSN_CD = '70' THEN 1 ELSE 0 END ), 0) AS ACP_RUT_CD_2 /*방문*/, NVL(SUM(CASE WHEN ACP_RUT_CD = '3' AND CVPT_HNDL_DVSN_CD = '70' THEN 1 ELSE 0 END ), 0) AS ACP_RUT_CD_3 /*이첩*/, NVL(SUM(CASE WHEN ACP_RUT_CD = '4' AND CVPT_HNDL_DVSN_CD = '70' THEN 1 ELSE 0 END ), 0) AS ACP_RUT_CD_4 /*전화*/, NVL(SUM(CASE WHEN ACP_RUT_CD = '5' AND CVPT_HNDL_DVSN_CD = '70' THEN 1 ELSE 0 END ), 0) AS ACP_RUT_CD_5 /*Fax*/, NVL(SUM(CASE WHEN ACP_RUT_CD = '7' AND CVPT_HNDL_DVSN_CD = '70' THEN 1 ELSE 0 END ), 0) AS ACP_RUT_CD_7 /*홈페이지*/, NVL(SUM(CASE WHEN ACP_RUT_CD = '8' AND CVPT_HNDL_DVSN_CD = '70' THEN 1 ELSE 0 END ), 0) AS ACP_RUT_CD_8 /*신문고*/ FROM DATA UNION ALL SELECT D3.CD , D3.CD_NM , NVL(D4.CNT, 0) AS CNT , CASE WHEN NVL(D4.CNTALL, 0) = 0 THEN 0 ELSE ROUND((D4.CNT/D4.CNTALL) * 100, 2) END AS PER , NVL(D4.ACP_RUT_CD_1, 0 ) AS ACP_RUT_CD_1 , NVL(D4.ACP_RUT_CD_2, 0 ) AS ACP_RUT_CD_2 , NVL(D4.ACP_RUT_CD_3, 0 ) AS ACP_RUT_CD_3 , NVL(D4.ACP_RUT_CD_4, 0 ) AS ACP_RUT_CD_4 , NVL(D4.ACP_RUT_CD_5, 0 ) AS ACP_RUT_CD_5 , NVL(D4.ACP_RUT_CD_7, 0 ) AS ACP_RUT_CD_7 , NVL(D4.ACP_RUT_CD_8, 0 ) AS ACP_RUT_CD_8 FROM (SELECT CD , CD_NM FROM TBDCMACM013C D2 WHERE D2.CMM_CD_DVSN_CD = '1000047' AND D2.CD <> '000000000' ORDER BY OTPT_SEQ ) D3, (SELECT D1.CVPT_KND_CD , COUNT(1) AS CNT , (SELECT COUNT(1) FROM DATA S1 WHERE 1=1) AS CNTALL , NVL(SUM(DECODE(ACP_RUT_CD, '1', 1, 0 )), 0) AS ACP_RUT_CD_1 , NVL(SUM(DECODE(ACP_RUT_CD, '2', 1, 0 )), 0) AS ACP_RUT_CD_2 , NVL(SUM(DECODE(ACP_RUT_CD, '3', 1, 0 )), 0) AS ACP_RUT_CD_3 , NVL(SUM(DECODE(ACP_RUT_CD, '4', 1, 0 )), 0) AS ACP_RUT_CD_4 , NVL(SUM(DECODE(ACP_RUT_CD, '5', 1, 0 )), 0) AS ACP_RUT_CD_5 , NVL(SUM(DECODE(ACP_RUT_CD, '7', 1, 0 )), 0) AS ACP_RUT_CD_7 , NVL(SUM(DECODE(ACP_RUT_CD, '8', 1, 0 )), 0) AS ACP_RUT_CD_8 FROM DATA D1 where CVPT_HNDL_DVSN_CD = '70' GROUP BY D1.CVPT_KND_CD ) D4 WHERE D3.CD = D4.CVPT_KND_CD(+) UNION ALL SELECT NULL AS CD , '전년 동기간 증감' AS CD_NM , D5.CNT-D6.CNT AS CNT , NVL(D5.PER-D6.PER, 0) AS PER , D5.ACP_RUT_CD_1 - D6.ACP_RUT_CD_1 AS ACP_RUT_CD_1 , D5.ACP_RUT_CD_2 - D6.ACP_RUT_CD_2 AS ACP_RUT_CD_2 , D5.ACP_RUT_CD_3 - D6.ACP_RUT_CD_3 AS ACP_RUT_CD_3 , D5.ACP_RUT_CD_4 - D6.ACP_RUT_CD_4 AS ACP_RUT_CD_4 , D5.ACP_RUT_CD_5 - D6.ACP_RUT_CD_5 AS ACP_RUT_CD_5 , D5.ACP_RUT_CD_7 - D6.ACP_RUT_CD_7 AS ACP_RUT_CD_7 , D5.ACP_RUT_CD_8 - D6.ACP_RUT_CD_8 AS ACP_RUT_CD_8 FROM (SELECT 'A' AS PK , COUNT(1) AS CNT , CASE WHEN COUNT(1) = 0 THEN 0 ELSE (COUNT(1) / COUNT(1)) * 100 END AS PER , NVL(SUM(DECODE(ACP_RUT_CD, '1', 1, 0 )), 0) AS ACP_RUT_CD_1 , NVL(SUM(DECODE(ACP_RUT_CD, '2', 1, 0 )), 0) AS ACP_RUT_CD_2 , NVL(SUM(DECODE(ACP_RUT_CD, '3', 1, 0 )), 0) AS ACP_RUT_CD_3 , NVL(SUM(DECODE(ACP_RUT_CD, '4', 1, 0 )), 0) AS ACP_RUT_CD_4 , NVL(SUM(DECODE(ACP_RUT_CD, '5', 1, 0 )), 0) AS ACP_RUT_CD_5 , NVL(SUM(DECODE(ACP_RUT_CD, '7', 1, 0 )), 0) AS ACP_RUT_CD_7 , NVL(SUM(DECODE(ACP_RUT_CD, '8', 1, 0 )), 0) AS ACP_RUT_CD_8 FROM DATA ) D5 , (SELECT 'A' AS PK , COUNT(1) AS CNT , CASE WHEN COUNT(1) = 0 THEN 0 ELSE (COUNT(1) / COUNT(1)) * 100 END AS PER , NVL(SUM(DECODE(ACP_RUT_CD, '1', 1, 0 )), 0) AS ACP_RUT_CD_1 , NVL(SUM(DECODE(ACP_RUT_CD, '2', 1, 0 )), 0) AS ACP_RUT_CD_2 , NVL(SUM(DECODE(ACP_RUT_CD, '3', 1, 0 )), 0) AS ACP_RUT_CD_3 , NVL(SUM(DECODE(ACP_RUT_CD, '4', 1, 0 )), 0) AS ACP_RUT_CD_4 , NVL(SUM(DECODE(ACP_RUT_CD, '5', 1, 0 )), 0) AS ACP_RUT_CD_5 , NVL(SUM(DECODE(ACP_RUT_CD, '7', 1, 0 )), 0) AS ACP_RUT_CD_7 , NVL(SUM(DECODE(ACP_RUT_CD, '8', 1, 0 )), 0) AS ACP_RUT_CD_8 FROM PRE_DATA ) D6 WHERE D5.PK = D6.PK(+)

/* kr.go.bai.eip.csp.dcp.dao.CSPDCP141Mapper.CSPDCP141QSelect3_1 */
WITH DATA AS ( SELECT NVL(D3.HNDL_DPT_CD,D1.ACP_DPT_CD) AS HNDL_DPT_CD ,D2.CVPT_HNDL_DVSN_CD ,D1.CVPT_HNDL_STA_CD FROM TBCSPDCP101M D1 ,( SELECT D4.REQ_DVSN_CD , D4.CVPT_ACP_NO , D4.ACP_YR , D4.CVPT_HNDL_DVSN_CD FROM TBCSPDCP205L D4 WHERE ( D4.REQ_DVSN_CD, D4.ACP_YR, D4.CVPT_ACP_NO, D4.SRNO ) IN ( SELECT REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO, MIN(SRNO) FROM TBCSPDCP205L GROUP BY REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO ) ) D2 ,TBCSPDCP201M D3 WHERE 1=1 AND D1.REQ_DVSN_CD = D2.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D2.CVPT_ACP_NO(+) AND D1.ACP_YR = D2.ACP_YR(+) AND D1.REQ_DVSN_CD = D3.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D3.CVPT_ACP_NO(+) AND D1.ACP_YR = D3.ACP_YR(+) AND D1.REQ_DVSN_CD = 1 AND D1.RM_CVPT_ACP_NO IS NOT NULL
AND TO_CHAR(D1.ACP_DH, 'YYYYMMDD') BETWEEN #{acpDhs} AND #{acpDhe}
AND TO_CHAR(D3.FNL_HNDL_DH, 'YYYYMMDD') BETWEEN #{acpDhs} AND #{acpDhe}
), PRE_DATA AS ( SELECT NVL(D3.HNDL_DPT_CD,D1.ACP_DPT_CD) AS HNDL_DPT_CD ,D2.CVPT_HNDL_DVSN_CD ,D1.CVPT_HNDL_STA_CD FROM TBCSPDCP101M D1 ,( SELECT D4.REQ_DVSN_CD , D4.CVPT_ACP_NO , D4.ACP_YR , D4.CVPT_HNDL_DVSN_CD FROM TBCSPDCP205L D4 WHERE ( D4.REQ_DVSN_CD, D4.ACP_YR, D4.CVPT_ACP_NO, D4.SRNO ) IN ( SELECT REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO, MIN(SRNO) FROM TBCSPDCP205L GROUP BY REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO ) ) D2 ,TBCSPDCP201M D3 WHERE 1=1 AND D1.REQ_DVSN_CD = D2.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D2.CVPT_ACP_NO(+) AND D1.ACP_YR = D2.ACP_YR(+) AND D1.REQ_DVSN_CD = D3.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D3.CVPT_ACP_NO(+) AND D1.ACP_YR = D3.ACP_YR(+) AND D1.REQ_DVSN_CD = 1 AND D1.RM_CVPT_ACP_NO IS NOT NULL
AND TO_CHAR(D1.ACP_DH, 'YYYYMMDD') BETWEEN TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{acpDhs}, 'YYYYMMDD'), -12),'dd'), 'YYYYMMDD') AND TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{acpDhe}, 'YYYYMMDD'), -12),'dd'), 'YYYYMMDD')
AND TO_CHAR(D3.FNL_HNDL_DH, 'YYYYMMDD') BETWEEN TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{acpDhs}, 'YYYYMMDD'), -12),'dd'), 'YYYYMMDD') AND TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{acpDhe}, 'YYYYMMDD'), -12),'dd'), 'YYYYMMDD')
) SELECT NULL AS DPT_CD , '합계' AS DPT_NM , COUNT(1) AS CNT , CASE WHEN COUNT(1) = 0 THEN 0 ELSE (COUNT(1)/COUNT(1)) * 100 END AS PER , NVL(SUM(CASE WHEN ((CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD ='10') OR CVPT_HNDL_STA_CD = '70') THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT1 /*단순종결*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '20' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT2 /*조사종결*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '30' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT3 /*즉시해소*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '40' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT4 /*즉시시정*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '50' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT5 /*감사실시*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '60' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT6 /*위탁조사*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '80' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT7 /*이첩*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '70' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT8 /*대내이송*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '80' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT9 /*재분류*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD IN ('20','30','31','40', '41') THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT10 /*처리중*/ FROM DATA
UNION ALL
SELECT D4.DPT_CD , D4.DPT_NM , NVL(D4.CNT, 0) AS CNT , CASE WHEN NVL(D4.CNTALL, 0) = 0 THEN 0 ELSE NVL(ROUND((D4.CNT/D4.CNTALL) * 100,2), 0) END AS PER , NVL(D4.CVPT_HNDL_DVSN_CNT1, 0) AS CVPT_HNDL_DVSN_CNT1 , NVL(D4.CVPT_HNDL_DVSN_CNT2, 0) AS CVPT_HNDL_DVSN_CNT2 , NVL(D4.CVPT_HNDL_DVSN_CNT3, 0) AS CVPT_HNDL_DVSN_CNT3 , NVL(D4.CVPT_HNDL_DVSN_CNT4, 0) AS CVPT_HNDL_DVSN_CNT4 , NVL(D4.CVPT_HNDL_DVSN_CNT5, 0) AS CVPT_HNDL_DVSN_CNT5 , NVL(D4.CVPT_HNDL_DVSN_CNT6, 0) AS CVPT_HNDL_DVSN_CNT6 , NVL(D4.CVPT_HNDL_DVSN_CNT7, 0) AS CVPT_HNDL_DVSN_CNT7 , NVL(D4.CVPT_HNDL_DVSN_CNT8, 0) AS CVPT_HNDL_DVSN_CNT8 , NVL(D4.CVPT_HNDL_DVSN_CNT9, 0) AS CVPT_HNDL_DVSN_CNT9 , NVL(D4.CVPT_HNDL_DVSN_CNT10, 0) AS CVPT_HNDL_DVSN_CNT10 FROM ( SELECT D1.HNDL_DPT_CD AS DPT_CD , F_DPT_INFO(D1.HNDL_DPT_CD,'4') AS DPT_NM , COUNT(1) AS CNT , (SELECT COUNT(1) FROM DATA S1 WHERE 1=1) AS CNTALL , NVL(SUM(CASE WHEN ((CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD ='10') OR CVPT_HNDL_STA_CD = '70') THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT1 /*단순종결*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '20' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT2 /*조사종결*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '30' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT3 /*즉시해소*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '40' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT4 /*즉시시정*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '50' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT5 /*감사실시*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '60' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT6 /*위탁조사*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '80' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT7 /*이첩*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '70' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT8 /*대내이송*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '80' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT9 /*재분류*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD IN ('20','30','31','40', '41') THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT10 /*처리중*/ FROM DATA D1 GROUP BY D1.HNDL_DPT_CD ) D4
UNION ALL
