/* kr.go.bai.eip.csp.dcp.dao.CSPDCP141Mapper.CSPDCP141QSelect4_1 */
WITH DATA AS ( SELECT SUBSTR(D1.TG_ORG_CAT_CD, '0', '1') AS TG_ORG_CAT_CD ,D2.CVPT_HNDL_DVSN_CD ,D1.CVPT_HNDL_STA_CD ,D1.RM_ACP_KND_CD FROM TBCSPDCP101M D1 ,( SELECT D4.REQ_DVSN_CD , D4.CVPT_ACP_NO , D4.ACP_YR , D4.CVPT_HNDL_DVSN_CD FROM TBCSPDCP205L D4 WHERE ( D4.REQ_DVSN_CD, D4.ACP_YR, D4.CVPT_ACP_NO, D4.SRNO ) IN ( SELECT REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO, MIN(SRNO) FROM TBCSPDCP205L GROUP BY REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO ) ) D2 ,TBCSPDCP201M D3 WHERE 1=1 AND D1.REQ_DVSN_CD = D2.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D2.CVPT_ACP_NO(+) AND D1.ACP_YR = D2.ACP_YR(+) AND D1.REQ_DVSN_CD = D3.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D3.CVPT_ACP_NO(+) AND D1.ACP_YR = D3.ACP_YR(+) AND D1.RM_CVPT_ACP_NO IS NOT NULL AND D1.REQ_DVSN_CD = 1
AND TO_CHAR(D1.ACP_DH, 'YYYYMMDD') BETWEEN #{acpDhs} AND #{acpDhe}
AND TO_CHAR(D3.FNL_HNDL_DH, 'YYYYMMDD') BETWEEN #{acpDhs} AND #{acpDhe}
), PRE_DATA AS ( SELECT SUBSTR(D1.TG_ORG_CAT_CD, '0', '1') AS TG_ORG_CAT_CD ,D2.CVPT_HNDL_DVSN_CD ,D1.CVPT_HNDL_STA_CD FROM TBCSPDCP101M D1 ,( SELECT D4.REQ_DVSN_CD , D4.CVPT_ACP_NO , D4.ACP_YR , D4.CVPT_HNDL_DVSN_CD FROM TBCSPDCP205L D4 WHERE ( D4.REQ_DVSN_CD, D4.ACP_YR, D4.CVPT_ACP_NO, D4.SRNO ) IN ( SELECT REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO, MIN(SRNO) FROM TBCSPDCP205L GROUP BY REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO ) ) D2 ,TBCSPDCP201M D3 WHERE 1=1 AND D1.REQ_DVSN_CD = D2.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D2.CVPT_ACP_NO(+) AND D1.ACP_YR = D2.ACP_YR(+) AND D1.REQ_DVSN_CD = D3.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D3.CVPT_ACP_NO(+) AND D1.ACP_YR = D3.ACP_YR(+) AND D1.REQ_DVSN_CD = 1 AND D1.RM_CVPT_ACP_NO IS NOT NULL
AND TO_CHAR(D1.ACP_DH, 'YYYYMMDD') BETWEEN TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{acpDhs}, 'YYYYMMDD'), -12),'dd'), 'YYYYMMDD') AND TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{acpDhe}, 'YYYYMMDD'), -12),'dd'), 'YYYYMMDD')
AND TO_CHAR(D3.FNL_HNDL_DH, 'YYYYMMDD') BETWEEN TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{acpDhs}, 'YYYYMMDD'), -12),'dd'), 'YYYYMMDD') AND TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{acpDhe}, 'YYYYMMDD'), -12),'dd'), 'YYYYMMDD')
) SELECT NULL AS DPT_CD , '합계' AS DPT_NM , COUNT(1) AS CNT , CASE WHEN COUNT(1) = 0 THEN 0 ELSE (COUNT(1)/COUNT(1)) * 100 END AS PER , NVL(SUM(CASE WHEN ((CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD ='10') OR CVPT_HNDL_STA_CD = '70') THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT1 /*단순종결*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '20' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT2 /*조사종결*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '30' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT3 /*즉시해소*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '40' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT4 /*즉시시정*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '50' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT5 /*감사실시*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '60' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT6 /*위탁조사*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '80' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT7 /*이첩*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '70' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT8 /*대내이송*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '80' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT9 /*재분류*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD IN ('20','30','31','40', '41') THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT10 /*처리중*/ FROM DATA
UNION ALL
SELECT * FROM ( SELECT CASE WHEN D4.CD IS NOT NULL THEN D4.CD WHEN D4.CD IS NULL THEN CASE WHEN D4.RM_ACP_KND_CD = '1' THEN 'Q' ELSE 'P' END ELSE D4.CD END AS CD , CASE WHEN D4.CD IS NOT NULL THEN F_CODE_NM('1000946',D4.CD) WHEN D4.CD IS NULL THEN CASE WHEN D4.RM_ACP_KND_CD = '1' THEN '미분류' ELSE '일반민원' END ELSE F_CODE_NM('1000946',D4.CD) END AS CD_NM , NVL(D4.CNT, 0) AS CNT , CASE WHEN D4.CNTALL = 0 THEN 0 ELSE NVL(ROUND((D4.CNT/D4.CNTALL) * 100,2), 0) END AS PER , NVL(D4.CVPT_HNDL_DVSN_CNT1, 0) AS CVPT_HNDL_DVSN_CNT1 , NVL(D4.CVPT_HNDL_DVSN_CNT2, 0) AS CVPT_HNDL_DVSN_CNT2 , NVL(D4.CVPT_HNDL_DVSN_CNT3, 0) AS CVPT_HNDL_DVSN_CNT3 , NVL(D4.CVPT_HNDL_DVSN_CNT4, 0) AS CVPT_HNDL_DVSN_CNT4 , NVL(D4.CVPT_HNDL_DVSN_CNT5, 0) AS CVPT_HNDL_DVSN_CNT5 , NVL(D4.CVPT_HNDL_DVSN_CNT6, 0) AS CVPT_HNDL_DVSN_CNT6 , NVL(D4.CVPT_HNDL_DVSN_CNT7, 0) AS CVPT_HNDL_DVSN_CNT7 , NVL(D4.CVPT_HNDL_DVSN_CNT8, 0) AS CVPT_HNDL_DVSN_CNT8 , NVL(D4.CVPT_HNDL_DVSN_CNT9, 0) AS CVPT_HNDL_DVSN_CNT9 , NVL(D4.CVPT_HNDL_DVSN_CNT10, 0) AS CVPT_HNDL_DVSN_CNT10 FROM ( SELECT SUBSTR(D1.TG_ORG_CAT_CD, '0', '1') AS CD , COUNT(1) AS CNT , (SELECT COUNT(1) FROM DATA S1 WHERE 1=1) AS CNTALL , D1.RM_ACP_KND_CD , NVL(SUM(CASE WHEN ((CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD ='10') OR CVPT_HNDL_STA_CD = '70') THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT1 /*단순종결*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '20' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT2 /*조사종결*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '30' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT3 /*즉시해소*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '40' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT4 /*즉시시정*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '50' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT5 /*감사실시*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '60' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT6 /*위탁조사*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA