ORDER BY CD ) D10
UNION ALL
SELECT NULL AS CD_CD , '전년 동기간 증감' AS DPT_NM , D5.CNT-D6.CNT AS CNT , NVL(D5.PER-D6.PER, 0) AS PER , D5.CVPT_HNDL_DVSN_CNT1-D6.CVPT_HNDL_DVSN_CNT1 AS CVPT_HNDL_DVSN_CNT1 , D5.CVPT_HNDL_DVSN_CNT2-D6.CVPT_HNDL_DVSN_CNT2 AS CVPT_HNDL_DVSN_CNT2 , D5.CVPT_HNDL_DVSN_CNT3-D6.CVPT_HNDL_DVSN_CNT3 AS CVPT_HNDL_DVSN_CNT3 , D5.CVPT_HNDL_DVSN_CNT4-D6.CVPT_HNDL_DVSN_CNT4 AS CVPT_HNDL_DVSN_CNT4 , D5.CVPT_HNDL_DVSN_CNT5-D6.CVPT_HNDL_DVSN_CNT5 AS CVPT_HNDL_DVSN_CNT5 , D5.CVPT_HNDL_DVSN_CNT6-D6.CVPT_HNDL_DVSN_CNT6 AS CVPT_HNDL_DVSN_CNT6 , D5.CVPT_HNDL_DVSN_CNT7-D6.CVPT_HNDL_DVSN_CNT7 AS CVPT_HNDL_DVSN_CNT7 , D5.CVPT_HNDL_DVSN_CNT8-D6.CVPT_HNDL_DVSN_CNT8 AS CVPT_HNDL_DVSN_CNT8 , D5.CVPT_HNDL_DVSN_CNT9-D6.CVPT_HNDL_DVSN_CNT9 AS CVPT_HNDL_DVSN_CNT9 , D5.CVPT_HNDL_DVSN_CNT10-D6.CVPT_HNDL_DVSN_CNT10 AS CVPT_HNDL_DVSN_CNT10 FROM ( SELECT 'A' AS PK , COUNT(1) AS CNT , (CASE WHEN COUNT(1) = 0 THEN 0 ELSE (COUNT(1) / COUNT(1)) * 100 END) AS PER , NVL(SUM(CASE WHEN ((CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD ='10') OR CVPT_HNDL_STA_CD = '70') THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT1 /*단순종결*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '20' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT2 /*조사종결*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '30' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT3 /*즉시해소*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '40' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT4 /*즉시시정*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '50' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT5 /*감사실시*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '60' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT6 /*위탁조사*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '80' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT7 /*이첩*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '70' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT8 /*대내이송*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '80' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT9 /*재분류*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD IN ('20','30','31','40', '41') THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT10 /*처리중*/
FROM DATA ) D5 , ( SELECT 'A' AS PK , COUNT(1) AS CNT , (CASE WHEN COUNT(1) = 0 THEN 0 ELSE (COUNT(1) / COUNT(1)) * 100 END) AS PER , NVL(SUM(CASE WHEN ((CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD ='10') OR CVPT_HNDL_STA_CD = '70') THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT1 /*단순종결*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '20' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT2 /*조사종결*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '30' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT3 /*즉시해소*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '40' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT4 /*즉시시정*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '50' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT5 /*감사실시*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '60' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT6 /*위탁조사*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '80' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT7 /*이첩*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '70' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT8 /*대내이송*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '80' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT9 /*재분류*/ , NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD IN ('20','30','31','40', '41') THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT10 /*처리중*/ FROM PRE_DATA ) D6 WHERE D5.PK = D6.PK(+)

WITH DATA AS ( SELECT D1.CVPT_KND_CD ,D2.CVPT_HNDL_DVSN_CD ,D1.CVPT_HNDL_STA_CD ,D4.CVPT_OPM_APV_HNDL_STA_CD FROM TBCSPDCP101M D1 ,( SELECT D4.REQ_DVSN_CD , D4.CVPT_ACP_NO , D4.ACP_YR , D4.CVPT_HNDL_DVSN_CD FROM TBCSPDCP205L D4 WHERE ( D4.REQ_DVSN_CD, D4.ACP_YR, D4.CVPT_ACP_NO, D4.SRNO ) IN ( SELECT REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO, MIN(SRNO) FROM TBCSPDCP205L GROUP BY REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO ) ) D2 ,TBCSPDCP201M D3 ,TBCSPDCP301M D4 WHERE 1=1 AND D1.REQ_DVSN_CD = D2.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D2.CVPT_ACP_NO(+) AND D1.ACP_YR = D2.ACP_YR(+) AND D1.REQ_DVSN_CD = D3.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D3.CVPT_ACP_NO(+) AND D1.ACP_YR = D3.ACP_YR(+) AND D1.REQ_DVSN_CD = D4.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D4.CVPT_ACP_NO(+) AND D1.ACP_YR = D4.ACP_YR(+) AND D4.CVPT_IFM_DVSN_CD(+) = 'C' AND D1.REQ_DVSN_CD = 1 AND D1.CVPT_HNDL_STA_CD NOT IN ('10') AND D1.RM_CVPT_ACP_NO IS NOT NULL AND D1.CVPT_KND_CD IN ( SELECT CD FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD = '1000047' AND CD <> '000000000' AND USR_DFN_TXT_1 = '1' )
AND TO_CHAR(D1.ACP_DH, 'YYYYMMDD') BETWEEN #{acpDhs} AND #{acpDhe}
AND TO_CHAR(D3.FNL_HNDL_DH, 'YYYYMMDD') BETWEEN #{acpDhs} AND #{acpDhe}
), PRE_DATA AS ( SELECT D1.CVPT_KND_CD ,D2.CVPT_HNDL_DVSN_CD ,D1.CVPT_HNDL_STA_CD ,D4.CVPT_OPM_APV_HNDL_STA_CD FROM TBCSPDCP101M D1 ,( SELECT D4.REQ_DVSN_CD , D4.CVPT_ACP_NO , D4.ACP_YR , D4.CVPT_HNDL_DVSN_CD FROM TBCSPDCP205L D4 WHERE ( D4.REQ_DVSN_CD, D4.ACP_YR, D4.CVPT_ACP_NO, D4.SRNO ) IN ( SELECT REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO, MIN(SRNO) FROM TBCSPDCP205L GROUP BY REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO ) ) D2 ,TBCSPDCP201M D3 ,TBCSPDCP301M D4 WHERE 1=1 AND D1.REQ_DVSN_CD = D2.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D2.CVPT_ACP_NO(+) AND D1.ACP_YR = D2.ACP_YR(+) AND D1.REQ_DVSN_CD = D3.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D3.CVPT_ACP_NO(+) AND D1.ACP_YR = D3.ACP_YR(+) AND D1.REQ_DVSN_CD = D4.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D4.CVPT_ACP_NO(+) AND D1.ACP_YR = D4.ACP_YR(+) AND D4.CVPT_IFM_DVSN_CD(+) = 'C' AND D1.REQ_DVSN_CD = 1 AND D1.CVPT_HNDL_STA_CD NOT IN ('10') AND D1.RM_CVPT_ACP_NO IS NOT NULL AND D1.CVPT_KND_CD IN ( SELECT CD FROM TBDCMACM013C WHERE CMM_CD_DVSN_CD = '1000047' AND CD <> '000000000' AND USR_DFN_TXT_1 = '1' )
AND TO_CHAR(D1.ACP_DH, 'YYYYMMDD') BETWEEN TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{acpDhs}, 'YYYYMMDD'), -12),'dd'), 'YYYYMMDD') AND TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{acpDhe}, 'YYYYMMDD'), -12),'dd'), 'YYYYMMDD')
AND TO_CHAR(D3.FNL_HNDL_DH, 'YYYYMMDD') BETWEEN TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{acpDhs}, 'YYYYMMDD'), -12),'dd'), 'YYYYMMDD') AND TO_CHAR(TRUNC(ADD_MONTHS(TO_DATE(#{acpDhe}, 'YYYYMMDD'), -12),'dd'), 'YYYYMMDD')
) SELECT NULL AS DPT_CD , '합계' AS DPT_NM , COUNT(1) AS CNT , (CASE WHEN COUNT(1) = 0 THEN 0 ELSE (COUNT(1)/COUNT(1)) * 100 END ) AS PER, NVL(SUM(CASE WHEN (CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD IN ( '11', '12','13', '14' )) OR (CVPT_HNDL_STA_CD = '70' AND CVPT_OPM_APV_HNDL_STA_CD = '30' AND CVPT_HNDL_DVSN_CD IN( '11', '12','13', '14')) THEN 1 ELSE 0 END), 0) AS CVPT_HNDL_DVSN_CNT1 /*직접조사-소계*/, NVL(SUM(CASE WHEN (CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '11') OR (CVPT_HNDL_STA_CD = '70' AND CVPT_OPM_APV_HNDL_STA_CD = '30' AND CVPT_HNDL_DVSN_CD = '11') THEN 1 ELSE 0 END), 0) AS CVPT_HNDL_DVSN_CNT1_1 /*직접조사-즉시시정*/, NVL(SUM(CASE WHEN (CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '12') OR (CVPT_HNDL_STA_CD = '70' AND CVPT_OPM_APV_HNDL_STA_CD = '30' AND CVPT_HNDL_DVSN_CD = '12') THEN 1 ELSE 0 END), 0) AS CVPT_HNDL_DVSN_CNT1_2 /*직접조사-즉시해소*/, NVL(SUM(CASE WHEN (CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '13') OR (CVPT_HNDL_STA_CD = '70' AND CVPT_OPM_APV_HNDL_STA_CD = '30' AND CVPT_HNDL_DVSN_CD = '13') OR (CVPT_HNDL_STA_CD = '70' AND (CVPT_OPM_APV_HNDL_STA_CD IS NULL OR CVPT_OPM_APV_HNDL_STA_CD <> '30')) THEN 1 ELSE 0 END), 0) AS CVPT_HNDL_DVSN_CNT1_3 /*직접조사-즉시종결*/, NVL(SUM(CASE WHEN (CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '14') OR (CVPT_HNDL_STA_CD = '70' AND CVPT_OPM_APV_HNDL_STA_CD = '30' AND CVPT_HNDL_DVSN_CD = '14') THEN 1 ELSE 0 END), 0) AS CVPT_HNDL_DVSN_CNT1_4 /*직접조사-실지감사*/, NVL(SUM(CASE WHEN (CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '20') OR (CVPT_HNDL_STA_CD = '70' AND CVPT_OPM_APV_HNDL_STA_CD = '30' AND CVPT_HNDL_DVSN_CD = '20') THEN 1 ELSE 0 END), 0) AS CVPT_HNDL_DVSN_CNT2 /*위탁조사*/, NVL(SUM(CASE WHEN (CVPT_HNDL_STA_CD = '60' AND CVPT_HNDL_DVSN_CD = '30') OR (CVPT_HNDL_STA_CD = '70' AND CVPT_OPM_APV_HNDL_STA_CD = '30' AND CVPT_HNDL_DVSN_CD = '30') THEN 1 ELSE 0 END), 0) AS CVPT_HNDL_DVSN_CNT3 /*감사참고자료*/, NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD = '80' THEN 1 ELSE 0 END),0) AS CVPT_HNDL_DVSN_CNT5, /*재분류*/ NVL(SUM(CASE WHEN CVPT_HNDL_STA_CD NOT IN ('60','70','10','80') THEN 1 ELSE 0 END), 0) AS CVPT_HNDL_DVSN_CNT4 /*처리중*/ FROM DATA
UNION ALL
