WITH /* CSPDCP411ReportCSPDCP1417RNoChgrTotalSelect */ RCP_DATA AS ( SELECT D1.REQ_DVSN_CD , D1.ACP_YR , D1.CVPT_ACP_NO , D1.CVPT_HNDL_STA_CD , ROUND(SYSDATE - D1.ACP_DH) AS TERM , ( SELECT USR_DFN_TXT_1 FROM TBDCMACM013C D3 WHERE D3.CMM_CD_DVSN_CD = '1000047' AND D3.CD = D1.CVPT_KND_CD ) AS CVPT_KND_DVSN_CD , D2.HNDL_CHGR_ID FROM TBCSPDCP101M D1 , TBCSPDCP201M D2 WHERE D1.REQ_DVSN_CD = D2.REQ_DVSN_CD(+) AND D1.ACP_YR = D2.ACP_YR(+) AND D1.CVPT_ACP_NO = D2.CVPT_ACP_NO(+) AND D1.REQ_DVSN_CD = '1' AND D1.CVPT_HNDL_STA_CD IN ( '20', '30' ) /* 신청, 접수, 배부중(담당자 없는것) 인 것만 계산 */ AND D2.HNDL_CHGR_ID IS NULL
AND D1.ACP_DH BETWEEN TO_DATE(#{acpDhs} || '000000', 'YYYYMMDDHH24MISS') AND TO_DATE(#{acpDhe} || '235959', 'YYYYMMDDHH24MISS')
AND D2.FNL_HNDL_DH BETWEEN TO_DATE(#{acpDhs} || '000000', 'YYYYMMDDHH24MISS') AND TO_DATE(#{acpDhe} || '235959', 'YYYYMMDDHH24MISS')
) SELECT COUNT(D5.CVPT_ACP_NO) AS TOT_CNT , NVL(SUM(DECODE(D5.CVPT_KND_DVSN_CD, '1', 1, 0)),0) AS TOT_AUD_CNT , NVL(SUM(DECODE(D5.CVPT_KND_DVSN_CD, '2', 1, 0)),0) AS TOT_GE_CNT , NVL(SUM(CASE WHEN D5.TERM <= 3 THEN 1 ELSE 0 END),0) AS TOT_3_CNT , NVL(SUM(CASE WHEN D5.TERM <= 3 AND D5.CVPT_KND_DVSN_CD = '1' THEN 1 ELSE 0 END),0) AS AUD_3_CNT , NVL(SUM(CASE WHEN D5.TERM <= 3 AND D5.CVPT_KND_DVSN_CD = '2' THEN 1 ELSE 0 END),0) AS GE_3_CNT , NVL(SUM(CASE WHEN D5.TERM >= 4 THEN 1 ELSE 0 END),0) AS TOT_4_CNT , NVL(SUM(CASE WHEN D5.TERM >= 4 AND D5.CVPT_KND_DVSN_CD = '1' THEN 1 ELSE 0 END),0) AS AUD_4_CNT , NVL(SUM(CASE WHEN D5.TERM >= 4 AND D5.CVPT_KND_DVSN_CD = '2' THEN 1 ELSE 0 END),0) AS GE_4_CNT FROM RCP_DATA D5

