WITH CSPDCP_DATA AS ( SELECT SUBSTR(T1.FNL_HNDL_DH,1,4) AS ACP_YR /* 처리년도 */ , T1.HNDL_DPT_CD /* 처리부서코드 */ , T1.DPT_FLAG /* 부서구분 */ , DECODE(T1.DPT_FLAG,'2','지행','민원') AS DPT_FLAG_NM /* 부서구분명 */ , T1.HNDL_DPT_NM AS DPT_NM/* 처리부서명 */ , T1.HNDL_DDLN_DT /* 처리기한일 */ , T1.HNDL_CHGR_ID /* 처리담당자ID */ , T1.FNL_HNDL_DH /* 최종처리일시 */ , T1.FNL_DTB_DH /* 배부일시 */ , TO_CHAR(TO_DATE(T1.FNL_HNDL_DH,'YYYYMMDD'),'Q') AS FNL_HNDL_Q , (SELECT COUNT(AA.YE) FROM TBBADDED013B AA WHERE AA.YE || AA.MD BETWEEN T1.FNL_DTB_DH AND T1.FNL_HNDL_DH ) AS HLDY_CNT /* 공휴일CNT */ , T1.CVPT_HNDL_DVSN_CD /* 민원처리구분코드 */ , T1.CVPT_HNDL_DVSN_NM , T1.CVPT_HNDL_STA_CD /* 민원처리상태코드 */ , T1.HNDL_YN FROM (SELECT D1.ACP_YR , (CASE WHEN SUBSTR(NVL(D2.HNDL_DPT_CD,'0L0100'),2,2) = 'L0' THEN '1' ELSE '2' END) AS DPT_FLAG , NVL(D2.HNDL_DPT_CD,'0L0100') AS HNDL_DPT_CD /* 처리부서코드 */ ,( SELECT S1.DPT_NM FROM TBDCMACM002M S1 WHERE S1.DPT_CD = NVL(D2.HNDL_DPT_CD,'0L0100') ) AS HNDL_DPT_NM /* 처리부서코드 */ ,D1.HNDL_DDLN_DT /* 처리기한일 */ ,D2.HNDL_CHGR_ID /* 처리담당자ID */ ,(CASE WHEN D1.CVPT_HNDL_STA_CD = '70' THEN NVL(TO_CHAR(D1.CVPT_WDW_DH, 'YYYYMMDD'),TO_CHAR(D2.FNL_HNDL_DH, 'YYYYMMDD')) WHEN D1.CVPT_HNDL_STA_CD = '80' THEN NVL(TO_CHAR(D1.ACP_DH, 'YYYYMMDD'),TO_CHAR(D2.FNL_HNDL_DH, 'YYYYMMDD')) ELSE TO_CHAR(D2.FNL_HNDL_DH , 'YYYYMMDD') END) AS FNL_HNDL_DH /* 최종처리일시 */
, (CASE WHEN D1.CVPT_HNDL_STA_CD = '60' AND D3.CVPT_HNDL_DVSN_CD IN ('30','40','90') THEN '90' WHEN D1.CVPT_HNDL_STA_CD = '60' AND D3.CVPT_HNDL_DVSN_CD IN ('20','110') THEN '20' WHEN D1.CVPT_HNDL_STA_CD = '60' AND D3.CVPT_HNDL_DVSN_CD NOT IN ('10','20','30','40','50','70','80','90','110') THEN '9999' WHEN D1.CVPT_HNDL_STA_CD = '80' THEN '9999' WHEN D1.CVPT_HNDL_STA_CD = '70' THEN '999' ELSE D3.CVPT_HNDL_DVSN_CD END) CVPT_HNDL_DVSN_CD , (CASE WHEN D1.CVPT_HNDL_STA_CD = '60' AND D3.CVPT_HNDL_DVSN_CD IN ('30','40','90') THEN F_CODE_NM('1000754', '90') WHEN D1.CVPT_HNDL_STA_CD = '60' AND D3.CVPT_HNDL_DVSN_CD IN ('20','110') THEN F_CODE_NM('1000754', '20') WHEN D1.CVPT_HNDL_STA_CD = '60' AND D3.CVPT_HNDL_DVSN_CD NOT IN ('10','20','30','40','50','70','80','90','110') THEN '기타' WHEN D1.CVPT_HNDL_STA_CD = '80' THEN '기타' WHEN D1.CVPT_HNDL_STA_CD = '70' THEN '취하' ELSE F_CODE_NM('1000754', D3.CVPT_HNDL_DVSN_CD) END) CVPT_HNDL_DVSN_NM , D1.CVPT_HNDL_STA_CD /* 민원처리상태코드 */ ,F_CODE_NM('1000361',D1.CVPT_HNDL_STA_CD) AS CVPT_HNDL_STA_NM
,TO_CHAR(D2.FNL_DTB_DH, 'YYYYMMDD') AS FNL_DTB_DH ,CASE WHEN D6.CNT > 0 THEN 'O' WHEN D9.DOC_ID IS NOT NULL THEN 'O' ELSE 'X' END AS HNDL_YN FROM TBCSPDCP101M D1 ,TBCSPDCP201M D2 ,TBCSPDCP205L D3 ,(SELECT S5.REQ_DVSN_CD , S5.ACP_YR , S5.CVPT_ACP_NO , COUNT(*) AS CNT FROM TBCSPDCP203M S5 WHERE S5.DGE IS NULL AND S5.CVPT_DOC_DVSN_CD = 'A1' GROUP BY S5.REQ_DVSN_CD, S5.ACP_YR, S5.CVPT_ACP_NO ) D6 ,(SELECT REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO, MIN(DOC_ID) AS DOC_ID FROM TBCSPDCP603M WHERE RPRT_CD IN ('B10200400','B10200600') GROUP BY REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO ) D9 WHERE D1.REQ_DVSN_CD = '1' AND D1.REQ_DVSN_CD = D2.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D2.CVPT_ACP_NO(+) AND D1.ACP_YR = D2.ACP_YR(+) AND D1.REQ_DVSN_CD = D3.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D3.CVPT_ACP_NO(+) AND D1.ACP_YR = D3.ACP_YR(+) AND D1.REQ_DVSN_CD = D6.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D6.CVPT_ACP_NO(+) AND D1.ACP_YR = D6.ACP_YR(+) AND D1.REQ_DVSN_CD = D9.REQ_DVSN_CD(+) AND D1.CVPT_ACP_NO = D9.CVPT_ACP_NO(+) AND D1.ACP_YR = D9.ACP_YR(+) AND D3.SRNO(+) = '1'
AND (D1.REQ_DVSN_CD, D1.ACP_YR, D1.CVPT_ACP_NO) NOT IN ( SELECT ST.REQ_DVSN_CD, ST.ACP_YR, ST.CVPT_ACP_NO FROM TBCSPDCP105L ST, TBCSPDCP205L TT WHERE 1=1 AND TT.REQ_DVSN_CD = '1' AND ST.SUC_REQ_DVSN_CD =TT.REQ_DVSN_CD AND ST.SUC_ACP_YR = TT.ACP_YR AND ST.SUC_CVPT_ACP_NO = TT.CVPT_ACP_NO AND (TT.AUD_RFR_MTRL_TRNF_DPT_CD IN ('100000','100100') OR TT.TRNF_DPT_CD IN ('100000','100100')) )
AND (D3.AUD_RFR_MTRL_TRNF_DPT_CD NOT IN ('100000','100100') OR D3.AUD_RFR_MTRL_TRNF_DPT_CD IS NULL) AND (D3.TRNF_DPT_CD NOT IN ('100000','100100') OR D3.TRNF_DPT_CD IS NULL)
AND NVL(D1.CVPT_KND_DVSN_CD,'1') = #{cvptKndDvsnCd}
) T1 WHERE T1.FNL_HNDL_DH <= REPLACE(#{shcDt},'-','') AND SUBSTR(T1.FNL_HNDL_DH,1,4) BETWEEN SUBSTR(#{shcDt},1,4) -2 AND SUBSTR(#{shcDt},1,4) AND T1.CVPT_HNDL_STA_CD IN ('60','70','80') ) SELECT B.ACP_YR , SUM(DECODE(A.FNL_HNDL_Q,'1',1,0)) AS Q1_CNT , SUM(DECODE(A.FNL_HNDL_Q,'2',1,0)) AS Q2_CNT , SUM(DECODE(A.FNL_HNDL_Q,'3',1,0)) AS Q3_CNT , SUM(DECODE(A.FNL_HNDL_Q,'4',1,0)) AS Q4_CNT , SUM(DECODE(A.FNL_HNDL_Q,NULL,0,1)) AS TOT_CNT FROM CSPDCP_DATA A RIGHT JOIN (SELECT SUBSTR(#{shcDt},1,4) - (level-1) AS ACP_YR FROM DUAL connect by (level-1) < 3) B ON A.ACP_YR = B.ACP_YR WHERE ((A.CVPT_HNDL_STA_CD = '60' AND A.CVPT_HNDL_DVSN_CD IS NOT NULL) OR (A.CVPT_HNDL_STA_CD IN ('70','80'))) GROUP BY B.ACP_YR ORDER BY B.ACP_YR

