mapper.select:
/* kr.go.bai.eip.csp.dcp.dao.CSPDCP513Mapper.CSPDCP513EDspSelect */

	SELECT	REQ_DVSN_CD
			,ACP_YR
			,CVPT_ACP_NO
			,DGE
			,SRNO
			,DSP_ORG_CD
			,F_ORG_INFO(DSP_ORG_CD,'1') AS DSP_ORG_NM
			,DSP_RQ_KND_CD
			,NOP_CNT
			,PNL_DRIN_PSVT_AM
	FROM	TBCSPDCP405L
	WHERE	REQ_DVSN_CD = #{reqDvsnCd}
	AND		ACP_YR = #{acpYr}
	AND		CVPT_ACP_NO = #{cvptAcpNo}
	AND		DGE = #{dge}

mapper.insert:
/* kr.go.bai.eip.csp.dcp.dao.CSPDCP513Mapper.CSPDCP513ESucInsertTBCSPDCP401H */

INSERT INTO TBCSPDCP401H
           (	REQ_DVSN_CD
	           ,ACP_YR
	           ,CVPT_ACP_NO
	           ,DGE
	           ,HNDL_DPT_CD
	           ,HNDL_PEN_ID
	           ,INSPC_RSLT_UNQ_TXT
	           ,DOC_ID
	           ,FRT_USR_ID
	           ,FNL_USR_ID
	           ,FNL_DEAL_DPT_CD
	           ,FNL_MNU_ID
	           ,FRT_REGI_DH
	           ,FNL_MDF_DH
           )
	SELECT	SUC_REQ_DVSN_CD
			,SUC_ACP_YR
			,SUC_CVPT_ACP_NO
			,#{dge}
			,#{fnlDealDptCd}
			,#{fnlUsrId}
			,#{inspcRsltUnqTxt}
			,#{docId}
           	,#{frtUsrId}
           	,#{fnlUsrId}
           	,#{fnlDealDptCd}
           	,#{fnlMnuId}
           	,SYSDATE
           	,SYSDATE
	FROM	TBCSPDCP105L
	WHERE	REQ_DVSN_CD = #{reqDvsnCd}
	AND		ACP_YR = #{acpYr}
	AND		CVPT_ACP_NO = #{cvptAcpNo}

/* kr.go.bai.eip.csp.dcp.dao.CSPDCP513Mapper.CSPDCP513ESucInsertTBCSPDCP402L */

	INSERT INTO TBCSPDCP402L
        (REQ_DVSN_CD
        ,ACP_YR
        ,CVPT_ACP_NO
        ,DGE
        ,RSLT_RPRT_HNDL_RSLT_CD   
        ,FRT_USR_ID
        ,FNL_USR_ID
        ,FNL_DEAL_DPT_CD
        ,FNL_MNU_ID
        ,FRT_REGI_DH
        ,FNL_MDF_DH 
        )
	SELECT	SUC_REQ_DVSN_CD
			,SUC_ACP_YR
			,SUC_CVPT_ACP_NO
			,#{dge}
			,#{rsltRprtHndlRsltCd}
			,#{frtUsrId}
	        ,#{fnlUsrId}
	        ,#{fnlDealDptCd}
	        ,#{fnlMnuId}
	        ,SYSDATE
	        ,SYSDATE
	FROM	TBCSPDCP105L S1
	WHERE	S1.REQ_DVSN_CD = #{reqDvsnCd}
	AND		S1.ACP_YR = #{acpYr}
	AND		S1.CVPT_ACP_NO = #{cvptAcpNo}

/* kr.go.bai.eip.csp.dcp.dao.CSPDCP513Mapper.CSPDCP513E405DspInsert */

	INSERT INTO	TBCSPDCP405L
			(	REQ_DVSN_CD
				,ACP_YR
				,CVPT_ACP_NO
				,DGE
				,SRNO
				,DSP_ORG_CD
				,DSP_ORG_NM
				,DSP_RQ_KND_CD
				,NOP_CNT
				,PNL_DRIN_PSVT_AM
				,FRT_USR_ID
		        ,FNL_USR_ID
		        ,FNL_DEAL_DPT_CD
		        ,FNL_MNU_ID
		        ,FRT_REGI_DH
		        ,FNL_MDF_DH
			)
	VALUES	(	#{reqDvsnCd}
				,#{acpYr}
				,#{cvptAcpNo}
				,#{dge}
				,(	SELECT	NVL(MAX(SRNO)+1,1)
					FROM	TBCSPDCP405L
					WHERE	REQ_DVSN_CD = #{reqDvsnCd}
					AND		ACP_YR = #{acpYr}
					AND		CVPT_ACP_NO = #{cvptAcpNo}
					AND		DGE = #{dge}
				)
				,#{dspOrgCd}
				,#{dspOrgNm}
				,#{dspRqKndCd}
				,#{nopCnt}
				,#{pnlDrinPsvtAm}	
				,#{frtUsrId}
		        ,#{fnlUsrId}
		        ,#{fnlDealDptCd}
		        ,#{fnlMnuId}
		        ,SYSDATE
		        ,SYSDATE
		       )

mapper.update:
/* kr.go.bai.eip.csp.dcp.dao.CSPDCP513Mapper.CSPDCP513E405DspUpdate */

	UPDATE	TBCSPDCP405L
	SET		DSP_ORG_CD = #{dspOrgCd}
			,DSP_ORG_NM = #{dspOrgNm}
			,DSP_RQ_KND_CD = #{dspRqKndCd}
			,NOP_CNT = #{nopCnt}
			,PNL_DRIN_PSVT_AM = #{pnlDrinPsvtAm}
			,FNL_USR_ID = #{fnlUsrId}
			,FNL_DEAL_DPT_CD = #{fnlDealDptCd}
			,FNL_MNU_ID = #{fnlMnuId}
			,FNL_MDF_DH = SYSDATE
	WHERE	REQ_DVSN_CD = #{reqDvsnCd}
	AND		ACP_YR = #{acpYr}
	AND		CVPT_ACP_NO = #{cvptAcpNo}
	AND		DGE = #{dge}
	AND		SRNO = #{srno}

mapper.delete:
/* kr.go.bai.eip.csp.dcp.dao.CSPDCP513Mapper.CSPDCP513E405DspDelete */

	DELETE
	FROM	TBCSPDCP405L
	WHERE	REQ_DVSN_CD = #{reqDvsnCd}
	AND		ACP_YR = #{acpYr}
	AND		CVPT_ACP_NO = #{cvptAcpNo}
	AND		DGE = #{dge}
	AND		SRNO = #{srno}

/* kr.go.bai.eip.csp.dcp.dao.CSPDCP513Mapper.CSPDCP513EDeleteDgeAll405L */

	DELETE
	FROM	TBCSPDCP405L
	WHERE	REQ_DVSN_CD = #{reqDvsnCd}
	AND		ACP_YR = #{acpYr}
	AND		CVPT_ACP_NO = #{cvptAcpNo}
	AND		DGE = #{dge}
