		/* kr.go.bai.eip.csp.dcp.dao.CSPDCP514Mapper.CSPDCP514P203Merge */

        	MERGE INTO 	TBCSPDCP203M A
        	USING 	(	SELECT 	#{reqDvsnCd} AS REQ_DVSN_CD 
        						,#{acpYr} AS ACP_YR
        						,#{cvptAcpNo} AS CVPT_ACP_NO
        						,'B1' AS CVPT_DOC_DVSN_CD
        						,#{dge} AS DGE
        				FROM DUAL
        			) B
        	ON		(	A.REQ_DVSN_CD = B.REQ_DVSN_CD
        				AND	A.ACP_YR = B.ACP_YR
        				AND A.CVPT_ACP_NO = B.CVPT_ACP_NO
        				AND A.CVPT_DOC_DVSN_CD = B.CVPT_DOC_DVSN_CD
        				AND A.DGE = B.DGE
        			)
        	WHEN MATCHED THEN
        			UPDATE	
        			SET		CVPT_DOC_TXT = #{b1CvptDocTxt}
        					,FNL_USR_ID = #{fnlUsrId}
        					,FNL_DEAL_DPT_CD = #{fnlDealDptCd}
        					,FNL_MNU_ID = #{fnlMnuId}
        					,FNL_MDF_DH = SYSDATE
        	WHEN NOT MATCHED THEN
		        	INSERT 		(	REQ_DVSN_CD
		        					,ACP_YR
		        					,CVPT_ACP_NO
		        					,DOC_SRNO
		        					,CVPT_DOC_DVSN_CD
		        					,CVPT_DOC_TXT
		        					,DGE
		        					,FRT_USR_ID
		        					,FNL_USR_ID
		        					,FNL_DEAL_DPT_CD
		        					,FNL_MNU_ID
		        					,FRT_REGI_DH
		        					,FNL_MDF_DH
		        				)
		        		VALUES	(	#{reqDvsnCd}
									,#{acpYr}
									,#{cvptAcpNo}
									,(	SELECT	NVL(MAX(DOC_SRNO),0)+1
										FROM	TBCSPDCP203M
										WHERE	REQ_DVSN_CD = #{reqDvsnCd}
										AND		ACP_YR = #{acpYr}
										AND		CVPT_ACP_NO = #{cvptAcpNo}
									)
		        					,'B1'
		        					,#{b1CvptDocTxt}
		        					,#{dge}
									,#{frtUsrId}
									,#{fnlUsrId}
									,#{fnlDealDptCd}
									,#{fnlMnuId}
									,SYSDATE
									,SYSDATE
								)

