 AND A.CVPT_ACP_NO = S2.CVPT_ACP_NO AND S2.SUC_REQ_DVSN_CD = S3.REQ_DVSN_CD AND S2.SUC_ACP_YR = S3.ACP_YR AND S2.SUC_CVPT_ACP_NO = S3.CVPT_ACP_NO AND ROWNUM = 1) || ' 외 ' || (SELECT COUNT(*)-1 FROM TBCSPDCP953L S2, TBCSPDCP101M S3 WHERE A.REQ_DVSN_CD = S2.REQ_DVSN_CD AND A.ACP_YR = S2.ACP_YR AND A.CVPT_ACP_NO = S2.CVPT_ACP_NO AND S2.SUC_REQ_DVSN_CD = S3.REQ_DVSN_CD AND S2.SUC_ACP_YR = S3.ACP_YR AND S2.SUC_CVPT_ACP_NO = S3.CVPT_ACP_NO ) ||'건 )' WHEN ( SELECT COUNT(*) FROM TBCSPDCP953L S1 WHERE S1.REQ_DVSN_CD = A.REQ_DVSN_CD AND S1.ACP_YR = A.ACP_YR AND S1.CVPT_ACP_NO = A.CVPT_ACP_NO) = 1 THEN '(합건(처리과) : '|| ( SELECT S3.ACP_YR||'-'|| F_CODE_NM('1000949', S3.RM_ACP_KND_CD) ||'-'||S3.RM_CVPT_ACP_NO FROM TBCSPDCP953L S2, TBCSPDCP101M S3 WHERE A.REQ_DVSN_CD = S2.REQ_DVSN_CD AND A.ACP_YR = S2.ACP_YR AND A.CVPT_ACP_NO = S2.CVPT_ACP_NO AND S2.SUC_REQ_DVSN_CD = S3.REQ_DVSN_CD AND S2.SUC_ACP_YR = S3.ACP_YR AND S2.SUC_CVPT_ACP_NO = S3.CVPT_ACP_NO ) || ')' ELSE ' ' END AS SUB_TIT_NM ,D.CVPT_SBT ,A.CVPTR_NM ,F_DPT_INFO(C.TRNF_DPT_CD, '1') AS TRNF_DPT_NM ,F_CODE_NM('1000755' ,E.RSLT_RPRT_HNDL_RSLT_CD) AS RSLT_RPRT_HNDL_RSLT_NM ,E.RSLT_RPRT_HNDL_RSLT_CD ,(SELECT COUNT(CVPT_ACP_NO) FROM TBCSPDCP601M WHERE ACP_YR = A.ACP_YR AND REQ_DVSN_CD = A.REQ_DVSN_CD AND CVPT_ACP_NO = A.CVPT_ACP_NO AND ACP_STEP_CD LIKE '%B2%') AS PRC_CNT ,TO_CHAR(A.ACP_DH + 30 + DECODE(( SELECT NVL(SUM(DECODE(NVL(Z3.APV_STA_CD,'0'), '30', '1', '0')),0) FROM TBCSPDCP603M Z3 WHERE Z3.ACP_YR = A.ACP_YR AND Z3.REQ_DVSN_CD = A.REQ_DVSN_CD AND Z3.CVPT_ACP_NO = A.CVPT_ACP_NO AND Z3.RPRT_CD = 'B10200100'),0,0,10),'YYYY-MM-DD') AS HNDL_LIMIT_DT_PRCS , TO_CHAR(B.FNL_HNDL_DH,'YYYY-MM-DD') AS FNL_HNDL_DH ,NVL(F.HNDL_RSLT_NTF_YN,'N') AS HNDL_RSLT_NTF_YN , F_CODE_NM('1000894', A.DOC_WRIT_KND_CD) AS DOC_TYPE /* 편집기 구분 */ FROM TBCSPDCP101M A ,TBCSPDCP201M B ,(SELECT REQ_DVSN_CD, ACP_YR, CVPT_ACP_NO, TRNF_DPT_CD, SRNO FROM TBCSPDCP205L WHERE 1=1 AND REQ_DVSN_CD = '1' AND CVPT_HNDL_DVSN_CD IN ('70','100') ) C ,TBCSPDCP102M D ,TBCSPDCP402L E , TBCSPDCP401H F WHERE 1=1 AND A.REQ_DVSN_CD = B.REQ_DVSN_CD AND A.ACP_YR = B.ACP_YR AND A.CVPT_ACP_NO = B.CVPT_ACP_NO AND A.REQ_DVSN_CD = C.REQ_DVSN_CD AND A.ACP_YR = C.ACP_YR AND A.CVPT_ACP_NO = C.CVPT_ACP_NO AND A.REQ_DVSN_CD = D.REQ_DVSN_CD AND A.ACP_YR = D.ACP_YR AND A.CVPT_ACP_NO = D.CVPT_ACP_NO AND A.REQ_DVSN_CD = '1' AND A.CVPT_HNDL_STA_CD IN ('60') AND C.REQ_DVSN_CD = E.REQ_DVSN_CD(+) AND C.ACP_YR = E.ACP_YR(+) AND C.CVPT_ACP_NO = E.CVPT_ACP_NO(+) AND C.SRNO = E.DGE(+) AND C.REQ_DVSN_CD = F.REQ_DVSN_CD(+) AND C.ACP_YR = F.ACP_YR(+) AND C.CVPT_ACP_NO = F.CVPT_ACP_NO(+) AND C.SRNO = F.DGE(+) AND (A.REQ_DVSN_CD, A.ACP_YR, A.CVPT_ACP_NO) NOT IN ( SELECT SUC_REQ_DVSN_CD,SUC_ACP_YR,SUC_CVPT_ACP_NO FROM TBCSPDCP105L ) AND (A.REQ_DVSN_CD, A.ACP_YR, A.CVPT_ACP_NO) NOT IN ( SELECT SUC_REQ_DVSN_CD,SUC_ACP_YR,SUC_CVPT_ACP_NO FROM TBCSPDCP953L )
