mapper.update:
		/* kr.go.bai.eip.csp.dcp.dao.CSPDCP816Mapper.CSPDCP816E101DtpUpdate */

			UPDATE	TBCSPDCP811M
			SET		HNDL_STA_CD = CASE WHEN HNDL_STA_CD = '2' THEN '3' ELSE HNDL_STA_CD END
					,FNL_USR_ID = #{fnlUsrId}
   					,FNL_DEAL_DPT_CD = #{fnlDealDptCd}
   					,FNL_MNU_ID = #{fnlMnuId}
   					,FNL_MDF_DH=SYSDATE
			WHERE	STTM_DVSN_CD = #{sttmDvsnCd}
			AND		ACP_YR = #{acpYr}
			AND		STTM_ACP_NO = #{sttmAcpNo}

		/* kr.go.bai.eip.csp.dcp.dao.CSPDCP816Mapper.CSPDCP816E813DtpbMerge */

			MERGE INTO 	TBCSPDCP813M A
        	USING 	(	SELECT 	#{sttmDvsnCd} AS STTM_DVSN_CD
        						,#{acpYr} AS ACP_YR
        						,#{sttmAcpNo} AS STTM_ACP_NO
        				FROM DUAL
        			) B
        	ON		(	A.STTM_DVSN_CD = B.STTM_DVSN_CD
        				AND	A.ACP_YR = B.ACP_YR
        				AND A.STTM_ACP_NO = B.STTM_ACP_NO
        			)
        	WHEN MATCHED THEN
        			UPDATE
        			SET		FNL_DTB_DPT_CD = #{fnlDealDptCd}
        					,FNL_DTB_PEN_ID = #{fnlUsrId}
        					,FNL_DTB_DH = SYSDATE
        					,HNDL_DPT_CD = #{hndlDptCd}
        					,HNDL_CHGR_ID = #{hndlChgrId}
        					,FNL_USR_ID = #{fnlUsrId}
        					,FNL_DEAL_DPT_CD = #{fnlDealDptCd}
        					,FNL_MNU_ID = #{fnlMnuId}
        					,FNL_MDF_DH = SYSDATE
        	WHEN NOT MATCHED THEN
		        	INSERT 		(	STTM_DVSN_CD
		        					,ACP_YR
		        					,STTM_ACP_NO
		        					,FNL_DTB_DPT_CD
		        					,FNL_DTB_PEN_ID
		        					,FNL_DTB_DH
		        					,HNDL_DPT_CD
		        					,HNDL_CHGR_ID
		        					,FRT_USR_ID
		        					,FNL_USR_ID
		        					,FNL_DEAL_DPT_CD
		        					,FNL_MNU_ID
		        					,FRT_REGI_DH
		        					,FNL_MDF_DH
		        				)
		        		VALUES	(	#{sttmDvsnCd}
									,#{acpYr}
									,#{sttmAcpNo}
									,#{fnlDealDptCd}
		        					,#{fnlUsrId}
		        					,SYSDATE
		        					,#{hndlDptCd}
		        					,#{hndlChgrId}
									,#{fnlUsrId}
									,#{fnlUsrId}
									,#{fnlDealDptCd}
									,#{fnlMnuId}
									,SYSDATE
									,SYSDATE
								)

mapper.insert:
		/* kr.go.bai.eip.csp.dcp.dao.CSPDCP816Mapper.CSPDCP816E814HInsert */

			INSERT INTO	TBCSPDCP814H
					(	STTM_DVSN_CD
						,ACP_YR
						,STTM_ACP_NO
						,SRNO
						,DTB_DPT_CD
						,DTB_PEN_ID
						,HNDL_DPT_CD
       					,HNDL_CHGR_ID
						,PET_TXT
						,FRT_USR_ID
						,FNL_USR_ID
						,FNL_DEAL_DPT_CD
						,FNL_MNU_ID
						,FRT_REGI_DH
						,FNL_MDF_DH
					)
				VALUES	(	#{sttmDvsnCd}
							,#{acpYr}
							,#{sttmAcpNo}
							,(	SELECT	NVL(MAX(SRNO),0)+1
								FROM	TBCSPDCP814H
								WHERE	STTM_DVSN_CD = #{sttmDvsnCd}
								AND		ACP_YR = #{acpYr}
								AND		STTM_ACP_NO = #{sttmAcpNo}
							)
							,#{fnlDealDptCd}
							,#{fnlUsrId}
							,#{hndlDptCd}
        					,#{hndlChgrId}
							,#{petTxt}
							,#{fnlUsrId}
							,#{fnlUsrId}
							,#{fnlDealDptCd}
							,#{fnlMnuId}
							,SYSDATE
							,SYSDATE
						)
