mapper.select:
/* kr.go.bai.eip.csp.dcp.dao.CSPDCP817Mapper.CSPDCP817QMainSelect */
SELECT T1.STTM_DVSN_CD ,T1.ACP_YR ,T1.STTM_ACP_NO ,DECODE(T1.RM_STTM_ACP_NO,NULL,NULL,T1.ACP_YR||'-'||'청금-'||T1.RM_STTM_ACP_NO) AS ACP_NO ,T1.HMP_APL_NO ,T1.STTM_KND_CD ,F_CODE_NM('1000758',T1.STTM_KND_CD) AS STTM_KND_NM ,T1.STTM_KND_DTL_CD ,F_CODE_NM('1000759',T1.STTM_KND_DTL_CD) AS STTM_KND_DTL_NM ,T1.NAM ,T1.TIT_NM ,TO_CHAR(T1.ACP_DH,'YYYY.MM.DD HH24:MI') AS ACP_DH ,TO_CHAR(T1.FRT_REGI_DH,'YYYY.MM.DD HH24:MI') AS FRT_REGI_DH ,T1.ACP_RUT_CD ,F_CODE_NM('1000761',T1.ACP_RUT_CD) AS ACP_RUT_NM ,T1.HNDL_STA_CD ,F_CODE_NM('1000760',T1.HNDL_STA_CD) AS HNDL_STA_NM ,T1.RM_STTM_ACP_NO ,TO_CHAR(T2.FNL_HNDL_DH,'YYYY.MM.DD HH24:MI') AS FNL_HNDL_DH ,TO_CHAR(TO_DATE(TO_CHAR(T1.ACP_DH, 'YYYYMMDD'), 'YYYYMMDD') + 30, 'YYYY.MM.DD') AS HNDL_LIMIT__DT ,TO_CHAR(T1.DELETE_DH,'YYYY.MM.DD HH24:MI') AS DELETE_DH ,(SELECT COUNT(CVPT_ACP_NO) FROM TBCSPDCP601M WHERE ACP_YR = T1.ACP_YR AND REQ_DVSN_CD = '4' AND CVPT_ACP_NO = T1.STTM_ACP_NO) AS PRC_CNT FROM TBCSPDCP811M T1 ,TBCSPDCP813M T2 WHERE T1.STTM_DVSN_CD = '1' AND T1.STTM_DVSN_CD = T2.STTM_DVSN_CD AND T1.ACP_YR = T2.ACP_YR AND T1.STTM_ACP_NO = T2.STTM_ACP_NO
AND T2.HNDL_DPT_CD = #{dptCd} AND T2.HNDL_CHGR_ID = #{usrId}
AND T1.ACP_YR = #{acpYr}
AND T1.RM_STTM_ACP_NO = #{rmSttmAcpNo}
AND TO_CHAR(T1.ACP_DH,'YYYYMMDD') >= #{acpDhs}
AND TO_CHAR(T1.ACP_DH,'YYYYMMDD') <= #{acpDhe}
AND T1.STTM_KND_CD = #{sttmKndCd}
AND T1.STTM_KND_DTL_CD = #{sttmKndDtlCd}
AND T1.HNDL_STA_CD = #{hndlStaCd}
AND T1.TIT_NM LIKE '%'||#{titNm}||'%'
AND T1.NAM LIKE '%'||#{nam}||'%'
AND T1.HNDL_STA_CD IN ('9','10')
AND T1.HNDL_STA_CD NOT IN ('9','10')
ORDER BY T1.ACP_YR DESC,TO_NUMBER(T1.RM_STTM_ACP_NO) DESC
