mapper.insert:
		/* kr.go.bai.eip.csp.dcp.dao.CSPDCP818Mapper.CSPDCP818ETBCSPDCP816L6Insert */

			INSERT INTO	TBCSPDCP816L
						(	STTM_DVSN_CD
        					,ACP_YR
        					,STTM_ACP_NO
        					,HNDL_ORG_SRNO
        					,SRNO
