        						AND		STTM_ACP_NO = #{sttmAcpNo}
        					)
        					,#{srno}
        					,#{orgCd}
        					,#{pubDvsnCd}
        					,#{coptSgCd}
        					,#{fnlUsrId}
        					,#{fnlUsrId}
        					,#{fnlDealDptCd}
        					,#{fnlMnuId}
        					,SYSDATE
        					,SYSDATE
        				)

