			,ANS_TXT = #{ansTxt}
	WHERE	STTM_DVSN_CD = #{sttmDvsnCd}
	AND		ACP_YR = #{acpYr}
	AND		STTM_ACP_NO = #{sttmAcpNo}

mapper.insert:
		/* kr.go.bai.eip.csp.dcp.dao.CSPDCP818Mapper.CSPDCP818ETBCSPDCP818MOnnaraMerge */

        	MERGE INTO 	TBCSPDCP818M A
        	USING 	(	SELECT 	#{sttmDvsnCd} AS STTM_DVSN_CD
        						,#{acpYr} AS ACP_YR
        						,#{sttmAcpNo} AS STTM_ACP_NO
        						,#{opmSrno} AS OPM_SRNO
        				FROM DUAL
        			) B
        	ON		(	A.STTM_DVSN_CD = B.STTM_DVSN_CD
        				AND	A.ACP_YR = B.ACP_YR
        				AND A.STTM_ACP_NO = B.STTM_ACP_NO
        				AND A.OPM_SRNO = B.OPM_SRNO
        			)
        	WHEN MATCHED THEN
        			UPDATE
        			SET		ONR_DOC_MNG_CRD_ID = #{onrDocMngCrdId}
        					,ADMN_INF_HNDL_NO = #{admnInfHndlNo}
        					,FNL_USR_ID = #{fnlUsrId}
        					,FNL_DEAL_DPT_CD = #{fnlDealDptCd}
        					,FNL_MNU_ID = #{fnlMnuId}
        					,FNL_MDF_DH = SYSDATE
        	WHEN NOT MATCHED THEN
		        	INSERT 		(	STTM_DVSN_CD
		        					,ACP_YR
		        					,STTM_ACP_NO
		        					,OPM_SRNO
		        					,IFM_DVSN_CD
		        					,OPM_APV_HNDL_STA_CD
		        					,ADMN_INF_HNDL_NO
		        					,ONR_DOC_MNG_CRD_ID
		        					,FRT_USR_ID
		        					,FNL_USR_ID
		        					,FNL_DEAL_DPT_CD
		        					,FNL_MNU_ID
		        					,FRT_REGI_DH
		        					,FNL_MDF_DH
		        				)
		        		VALUES	(	#{sttmDvsnCd}
									,#{acpYr}
									,#{sttmAcpNo}
		        					,(SELECT 	NVL(MAX(TO_NUMBER(OPM_SRNO)),0)+1
										FROM 	TBCSPDCP818M
										WHERE 	STTM_DVSN_CD = #{sttmDvsnCd}
										AND		ACP_YR = #{acpYr}
										AND		STTM_ACP_NO = #{sttmAcpNo})
		        					,#{ifmDvsnCd}
		        					,'10'
		        					,#{admnInfHndlNo}
		        					,#{onrDocMngCrdId}
									,#{frtUsrId}
									,#{fnlUsrId}
									,#{fnlDealDptCd}
									,#{fnlMnuId}
									,SYSDATE
									,SYSDATE
								)

mapper.update:
/* kr.go.bai.eip.csp.dcp.dao.CSPDCP818Mapper.CSPDCP818ETBCSPDCP813MNtfUpdate */

	UPDATE	/*CSPDCP818ETBCSPDCP813MNtfUpdate*/ TBCSPDCP813M D1
