/* kr.go.bai.eip.csp.ear.dao.CSPEAR101Mapper.CSPEAR101EMainSelect */
SELECT CASE WHEN REQ_DVSN_CD = '3' THEN '공익' WHEN REQ_DVSN_CD = '2' THEN '국민' ELSE '' END AS REQ_DVSN_CD ,ACP_YR ,ACP_NO ,ACP_YR || '-' || F_CODE_NM('1000740',REQ_DVSN_CD) || '-' || ACP_NO AS ACP_NO2 ,TIT ,REQR_RPST ,CHR_DPT_NM ,CHGR_NM ,ACP_DY FROM TBCSPEAR101M WHERE 1=1 AND REQ_DVSN_CD = #{reqDvsnCd}
AND ACP_DY >= #{acpDhs}
AND ACP_DY <= #{acpDhe}
AND TIT LIKE '%'||#{titNm}||'%'
AND ACP_YR = #{acpYr}
AND ACP_NO = #{cvptAcpNo}
ORDER BY ACP_YR DESC, ACP_NO DESC

