mapper.select:
/*kr.go.bai.eip.dcm.acm.dao.DCMACM017EMapper.selectList601B*/ SELECT REQ_DVSN_CD, ACP_STEP_CD , ACP_STEP_NM , NECES_YN, STAD_DT, TSK_DESC, LINK_URL, SORT_SNO, (SELECT COUNT(REQ_DVSN_CD) FROM TBCORCOR602B B WHERE REQ_DVSN_CD = #{reqDvsnCd} AND A.ACP_STEP_CD = B.ACP_STEP_CD) COUNT, (SELECT COUNT(REQ_DVSN_CD) FROM TBCORCOR601H C WHERE REQ_DVSN_CD = #{reqDvsnCd} AND A.ACP_STEP_CD = C.TSK_STEP_CD) COUNT2
FROM TBCORCOR601B A WHERE 1=1 AND REQ_DVSN_CD = #{reqDvsnCd}
UNION ALL
SELECT REQ_DVSN_CD, ACP_STEP_CD , ACP_STEP_NM , NECES_YN, STAD_DT, TSK_DESC, LINK_URL, SORT_SNO, (SELECT COUNT(REQ_DVSN_CD) FROM TBDCPDCP602B B WHERE REQ_DVSN_CD = #{reqDvsnCd} AND A.ACP_STEP_CD = B.ACP_STEP_CD) COUNT, (SELECT COUNT(REQ_DVSN_CD) FROM TBDCPDCP601H C WHERE REQ_DVSN_CD = #{reqDvsnCd} AND A.ACP_STEP_CD = C.TSK_STEP_CD) COUNT2 FROM TBDCPDCP601B A WHERE 1=1 AND REQ_DVSN_CD = #{reqDvsnCd} ORDER BY ACP_STEP_CD ASC;

/*kr.go.bai.eip.dcm.acm.dao.DCMACM017EMapper.selectList602B*/ SELECT REQ_DVSN_CD, ACP_STEP_CD, ACP_MAIN_STEP_ID, ACP_MAIN_STEP_SNO, ACP_MAIN_SEQ_W, ACP_MAIN_SEQ_H, ACP_MAIN_STEP_NM, ACP_DOC_CD, CMPT_YN, LINK_TYPE , LINK_URL, (SELECT COUNT(REQ_DVSN_CD) FROM TBCORCOR602H B WHERE REQ_DVSN_CD = #{reqDvsnCd} AND A.ACP_STEP_CD = B.TSK_STEP_CD AND A.ACP_MAIN_STEP_ID = B.TSK_PRCS_CD) COUNT2 FROM TBCORCOR602B A WHERE 1=1 AND REQ_DVSN_CD = #{reqDvsnCd} AND ACP_STEP_CD = #{acpStepCd}
UNION ALL
SELECT REQ_DVSN_CD, ACP_STEP_CD, ACP_MAIN_STEP_ID, ACP_MAIN_STEP_SNO, ACP_MAIN_SEQ_W, ACP_MAIN_SEQ_H, ACP_MAIN_STEP_NM, ACP_DOC_CD, CMPT_YN, LINK_TYPE , LINK_URL, (SELECT COUNT(REQ_DVSN_CD) FROM TBDCPDCP602H B WHERE REQ_DVSN_CD = #{reqDvsnCd} AND A.ACP_STEP_CD = B.TSK_STEP_CD AND A.ACP_MAIN_STEP_ID = B.TSK_PRCS_CD) COUNT2 FROM TBDCPDCP602B A WHERE 1=1 AND REQ_DVSN_CD = #{reqDvsnCd} AND ACP_STEP_CD = #{acpStepCd} ORDER BY ACP_MAIN_SEQ_W

/*kr.go.bai.eip.dcm.acm.dao.DCMACM017EMapper.selectHisList601B*/ SELECT TSK_DVSN_CD, ESSNTL_YN, STAD_DT, TSK_DES, LNK_URL, SRT_SNO, TO_CHAR(HST_DH, 'YYYY-MM-DD') AS HST_DT, TO_CHAR(HST_DH, 'HH24:MI:SS') AS HST_TM, TSK_STEP_CD, TSK_STEP_NM, ESSNTL_YN, DECODE(HST_DVSN, 'C', '삽입', 'D', '삭제', 'U', '수정') AS HST_DVSN, FRT_USR_ID, FNL_USR_ID, FNL_DEAL_DPT_CD, FNL_MNU_ID, TO_CHAR(FRT_REGI_DH, 'YYYY-MM-DD') AS FRT_REGI_DT, TO_CHAR(FRT_REGI_DH, 'HH24:MI:SS') AS FRT_REGI_TM, TO_CHAR(FNL_MDF_DH, 'YYYY-MM-DD') AS FNL_MDF_DT, TO_CHAR(FNL_MDF_DH, 'HH24:MI:SS') AS FNL_MDF_TM FROM TBCORCOR601H WHERE 1=1 AND TSK_DVSN_CD = #{reqDvsnCd} AND TSK_STEP_CD = #{acpStepCd}
UNION ALL
SELECT TSK_DVSN_CD, ESSNTL_YN, STAD_DT, TSK_DES, LNK_URL, SRT_SNO, TO_CHAR(HST_DH, 'YYYY-MM-DD') AS HST_DT, TO_CHAR(HST_DH, 'HH24:MI:SS') AS HST_TM, TSK_STEP_CD, TSK_STEP_NM, ESSNTL_YN, DECODE(HST_DVSN, 'C', '삽입', 'D', '삭제', 'U', '수정') AS HST_DVSN, FRT_USR_ID, FNL_USR_ID, FNL_DEAL_DPT_CD, FNL_MNU_ID, TO_CHAR(FRT_REGI_DH, 'YYYY-MM-DD') AS FRT_REGI_DT, TO_CHAR(FRT_REGI_DH, 'HH24:MI:SS') AS FRT_REGI_TM, TO_CHAR(FNL_MDF_DH, 'YYYY-MM-DD') AS FNL_MDF_DT, TO_CHAR(FNL_MDF_DH, 'HH24:MI:SS') AS FNL_MDF_TM FROM TBDCPDCP601H WHERE 1=1 AND TSK_DVSN_CD = #{reqDvsnCd} AND TSK_STEP_CD = #{acpStepCd} ORDER BY HST_DT DESC, HST_TM DESC;

