/*kr.go.bai.eip.dcm.acm.dao.DCMACM048EMapper.selectMainList*/
SELECT T1.ACP_YR || '-' || DECODE(T1.ACP_DVSN_CD,'1','심사','2','재심','3','재심판정') || '-' || DECODE(T1.ORG_ACP_SRNO, null, T1.ACP_SRNO, T1.ORG_ACP_SRNO) || ' ' || (CASE WHEN T1.MG_ACP_YR IS NULL THEN '' WHEN T1.MG_DIV_CD = '10' THEN '병합(' || (SELECT COUNT(MG_ACP_YR) FROM TBBADFRE012M WHERE MG_ACP_YR = T1.MG_ACP_YR AND MG_ACP_DVSN_CD = T1.MG_ACP_DVSN_CD AND MG_ACP_SRNO = T1.MG_ACP_SRNO) || ')' WHEN T1.MG_DIV_CD = '20' THEN '병행(' || (SELECT COUNT(MG_ACP_YR) FROM TBBADFRE012M WHERE MG_ACP_YR = T1.MG_ACP_YR AND MG_ACP_DVSN_CD = T1.MG_ACP_DVSN_CD AND MG_ACP_SRNO = T1.MG_ACP_SRNO) || ')' ELSE '병합(' || (SELECT COUNT(T4.MG_ACP_YR) FROM TBBADFRE012M T4 WHERE T4.MG_ACP_YR = T1.MG_ACP_YR AND T4.MG_ACP_DVSN_CD = T1.MG_ACP_DVSN_CD AND T4.MG_ACP_SRNO = T1.MG_ACP_SRNO AND T4.MG_DPT_CD = T1.MG_DPT_CD) || ')' END) AS ACP_YR_NO , TIT_NM , T1.ACP_HNDL_STA_CD /* 진행단계코드 */ , F_CODE_NM('1000807', T1.ACP_HNDL_STA_CD) AS ACP_STEP_NM /* 진행단계명 */ , (CASE WHEN T1.MG_ACP_YR IS NULL THEN '' WHEN T1.MG_DIV_CD = '10' THEN '병합(' || (SELECT COUNT(MG_ACP_YR) FROM TBBADFRE012M WHERE MG_ACP_YR = T1.MG_ACP_YR AND MG_ACP_DVSN_CD = T1.MG_ACP_DVSN_CD AND MG_ACP_SRNO = T1.MG_ACP_SRNO) || ')' WHEN T1.MG_DIV_CD = '20' THEN '병행(' || (SELECT COUNT(MG_ACP_YR) FROM TBBADFRE012M WHERE MG_ACP_YR = T1.MG_ACP_YR AND MG_ACP_DVSN_CD = T1.MG_ACP_DVSN_CD AND MG_ACP_SRNO = T1.MG_ACP_SRNO) || ')' ELSE '병합(' || (SELECT COUNT(T4.MG_ACP_YR) FROM TBBADFRE012M T4 WHERE T4.MG_ACP_YR = T1.MG_ACP_YR AND T4.MG_ACP_DVSN_CD = T1.MG_ACP_DVSN_CD AND T4.MG_ACP_SRNO = T1.MG_ACP_SRNO AND T4.MG_DPT_CD = T1.MG_DPT_CD) || ')' END) AS MG_COUNT , (SELECT DPT_NM FROM TBDCMACM002M WHERE DPT_CD = T1.DPT_CD) AS DPT_NM , T1.HNDL_CHGR_NM , T1.ACP_YR , T1.ACP_DVSN_CD , T1.ACP_SRNO FROM TBBADFRE012M T1 WHERE 1=1 AND T1.ACP_YR = #{schAudYr}
AND T1.ACP_DVSN_CD = #{acpDvsnCd}
AND (NVL(T1.ORG_ACP_SRNO,T1.ACP_SRNO) = #{schAudGrnNo})
AND T1.DPT_CD = #{dptCd}
AND T1.HNDL_CHGR_NM =#{hndlChgrNm}
AND T1.TIT_NM LIKE '%'||#{schAudSbjNm}||'%'
ORDER BY T1.ACP_YR, T1.ACP_SRNO DESC

/* kr.go.bai.eip.dcm.acm.dao.DCMACM041EMapper.selectExcelExport*/ SELECT ROWNUM ,AUD_YR_NO ,AUD_SBJ_NM ,DPT_NM ,AUD_STEP_NM ,RPRT_KND_NM ,RPRT_NM ,APV_STA_NM ,DOC_WRT_NM ,REGI_DH ,CURR_APV_USR_NM ,FNL_APV_USR_NM ,EDT_AUTH_NM FROM ( SELECT F_MNG_KND_AUDYRNO(T1.AUD_YR, T1.AUD_NO, T1.AUD_KND_CD, '-') AS AUD_YR_NO , AUD_SBJ_NM , (SELECT MAX(CASE WHEN AUD_STEP_CD = '10' THEN '자료수집/확인출장' ELSE AUD_STEP_NM END) AS CD_NM FROM TBBADDED101B WHERE AUD_STEP_CD = T2.AUD_STEP_CD) AS AUD_STEP_NM , (SELECT DECODE(AUD_DOC_CD, 'A10600100', '개별처분요구안',AGGR_CONCAT(DTIL_AUD_DOC_NM,',')) AS CD_NM FROM ( SELECT AUD_DOC_CD , DECODE(INSTR(DTIL_AUD_DOC_NM,'('),0,DTIL_AUD_DOC_NM,SUBSTR(DTIL_AUD_DOC_NM,0,INSTR(DTIL_AUD_DOC_NM,'(')-1)) AS DTIL_AUD_DOC_NM FROM TBFDMADM002M WHERE SUBSTR(AUD_DOC_CD,0,1) = 'A' GROUP BY AUD_DOC_CD, DECODE(INSTR(DTIL_AUD_DOC_NM,'('),0,DTIL_AUD_DOC_NM,SUBSTR(DTIL_AUD_DOC_NM,0,INSTR(DTIL_AUD_DOC_NM,'(')-1)) UNION ALL SELECT 'A10800500' , '공개문' FROM DUAL UNION ALL SELECT 'A10700700' , '부의안요약문-보고자용' FROM DUAL UNION ALL SELECT 'A10700800' , '부의안요약문-원장님용' FROM DUAL ) WHERE AUD_DOC_CD = T2.RPRT_CD GROUP BY AUD_DOC_CD) AS RPRT_KND_NM , T2.RPRT_NM , T2.RPRT_NM AS RPRT_REAL_NM , F_CODE_NM('1000773', T2.APV_STA_CD) AS APV_STA_NM , F_USR_INFO_BY_ID(DOC_WRT_ID,'2') AS DOC_WRT_NM , TO_CHAR(T2.FRT_REGI_DH,'YYYY"-"MM"-"DD" "HH24:MI') AS REGI_DH , DECODE(NVL(EDT_AUTH,'N'),'N','편집불가','편집가능') AS EDT_AUTH_NM , NVL(EDT_AUTH,'N') AS EDT_AUTH , T2.AUD_YR , T2.AUD_NO , T2.AUD_STEP_CD , T2.DOC_ID , T2.APV_STA_CD , T2.APV_RQS_ID , F_DPT_INFO(T1.MNG_DPT_CD,'1') AS DPT_NM , CURR_APV.APVR_NM AS CURR_APV_USR_NM , FNL_APV.APVR_NM AS FNL_APV_USR_NM FROM TBBADDED001M T1 INNER JOIN TBBADDED103M T2 ON (T1.AUD_YR = T2.AUD_YR AND T1.AUD_NO = T2.AUD_NO) LEFT OUTER JOIN ( SELECT M.APV_DOC_NO, M.APV_DVSN_CD, M.APVR_PST_CD , DECODE(M.APVR_PST_CD, '','','(' || F_CODE_NM('1000176', M.APVR_PST_CD) || ')') || ' ' || F_USR_INFO(M.APVR_CNB,2) AS APVR_NM FROM TBDCMACM023L M WHERE M.APV_DVSN_CD = 'fieldAudit' AND SUBSTR(M.APVR_STA_CD,1,1) = '1' AND M.APVR_KND_CD <> '6' GROUP BY M.APV_DOC_NO, M.APV_DVSN_CD, M.APVR_PST_CD, M.APVR_CNB) CURR_APV ON (T2.APV_RQS_ID = CURR_APV.APV_DOC_NO) LEFT OUTER JOIN ( SELECT M.APV_DOC_NO, M.APV_DVSN_CD, M.APVR_PST_CD ,DECODE(M.APVR_PST_CD, '','','(' || F_CODE_NM('1000176', M.APVR_PST_CD) || ')') || ' ' || F_USR_INFO(M.APVR_CNB,2) AS APVR_NM FROM TBDCMACM023L M ,( SELECT APV_DOC_NO, APV_DVSN_CD, MAX(APVR_SEQ) AS MAX_APVR_SEQ FROM TBDCMACM023L GROUP BY APV_DOC_NO, APV_DVSN_CD ) SUB WHERE M.APV_DOC_NO = SUB.APV_DOC_NO AND M.APV_DVSN_CD = SUB.APV_DVSN_CD AND M.APVR_SEQ = SUB.MAX_APVR_SEQ AND M.APV_DVSN_CD = 'fieldAudit' ) FNL_APV ON (T2.APV_RQS_ID = FNL_APV.APV_DOC_NO) WHERE 1=1 AND T1.AUD_YR = #{schAudYr}
AND T1.AUD_KND_CD = #{schAudYrNoKndCd}
AND T1.AUD_GRN_NO = #{schAudGrnNo}
AND T1.AUD_SBJ_NM LIKE '%'||#{schAudSbjNm}||'%'
AND T1.MNG_DPT_CD = #{schDeptCd}
AND (SELECT HIRK_DPT_CD FROM TBDCMACM002M WHERE DPT_CD = T1.MNG_DPT_CD) =#{schSpvBrdvCd}
AND T2.AUD_STEP_CD = #{schPrssStepCd}
AND SUBSTR(T2.RPRT_CD,0,5) = #{schPrssStepCd}
AND T2.RPRT_NM LIKE '%'||#{schRprtNm}||'%'
AND T2.RPRT_CD = #{schRprtCd}
AND T2.APV_STA_CD = #{schApvStaCd}
AND NVL(T2.EDT_AUTH,'N') = #{schEdtAuth}
AND CURR_APV.APVR_PST_CD = '0013600' /*최종결재자가 과장 단계*/
AND CURR_APV.APVR_PST_CD IN ('0015800','0035100','0058100') /*최종결재자가 국장 단계*/
AND CURR_APV.APVR_PST_CD IN ('0055600','0045900') /*최종결재자가 차장 단계*/
AND CURR_APV.APVR_PST_CD = '0055700' /*최종결재자가 총장 단계*/
AND CURR_APV.APVR_PST_CD = '0010600' /*최종결재자가 위원 단계*/
AND CURR_APV.APVR_PST_CD = '0067400' /*최종결재자가 원장 단계*/
AND FNL_APV.APVR_PST_CD = '0013600' /*최종결재자가 과장 단계*/
AND FNL_APV.APVR_PST_CD IN ('0015800','0035100','0058100') /*최종결재자가 국장 단계*/
AND FNL_APV.APVR_PST_CD IN ('0055600','0045900') /*최종결재자가 차장 단계*/
AND FNL_APV.APVR_PST_CD = '0055700' /*최종결재자가 총장 단계*/
AND FNL_APV.APVR_PST_CD = '0010600' /*최종결재자가 위원 단계*/
AND FNL_APV.APVR_PST_CD = '0067400' /*최종결재자가 원장 단계*/
ORDER BY T1.AUD_YR, T1.AUD_KND_CD, T1.AUD_GRN_NO DESC, T2.AUD_STEP_CD, T2.RPRT_CD )
