mapper.select:
/* kr.go.bai.eip.dcp.dcp.dao.DCP002001EMapper.DCP002001EMainSelect */
SELECT A.REQ_DVSN_CD ,A.ACP_YR ,A.CVPT_ACP_NO ,A.RM_CVPT_ACP_NO ,CASE WHEN A.RM_CVPT_ACP_NO IS NULL THEN NULL WHEN A.REQ_DVSN_CD = '9' THEN A.ACP_YR||'-이해-'||A.RM_CVPT_ACP_NO ELSE A.ACP_YR||'-청금-'||A.RM_CVPT_ACP_NO END AS ACP_NO ,A.CVPT_KND_CD ,F_CODE_NM('1000940', A.CVPT_KND_CD) AS CVPT_KND_NM ,F_CODE_NM('1000944', A.CVPT_KND_DTL_CD) AS CVPT_KND_DTL_NM ,A.TIT_NM ,A.REQR_KND_CD ,A.ACP_RUT_CD ,F_CODE_NM('1000927', A.ACP_RUT_CD) AS ACP_RUT_NM ,DECODE(A.REQR_KND_CD,'1',A.CVPTR_NM,'2',A.RPST_PEN_NM,'3',A.RPST_PEN_NM,'4',A.CVPTR_NM) AS CVPTR_NM ,TO_CHAR(A.ACP_DH,'YYYY-MM-DD HH24:MI') AS ACP_DH ,A.CVPT_HNDL_STA_CD ,F_CODE_NM('1000934', A.CVPT_HNDL_STA_CD) AS CVPT_HNDL_STA_NM ,A.HPN ,TO_CHAR(A.FRT_REGI_DH,'YYYY-MM-DD') AS ACT_DT ,( SELECT S1.DPT_NM FROM TBDCMACM002M S1 WHERE S1.DPT_CD = A.ACP_DPT_CD ) AS ACP_DPT_NM ,( SELECT S1.DPT_NM FROM TBDCMACM002M S1 WHERE S1.DPT_CD = C.HNDL_DPT_CD ) AS HNDL_DPT_NM ,F_USR_INFO_BY_ID(C.HNDL_CHGR_ID,'2') AS HNDL_CHGR_NM ,TO_CHAR(A.CVPT_WDW_DH,'YYYY-MM-DD HH24:MI') AS CVPT_WDW_DH ,( SELECT F_CODE_NM('1000943', S3.CVPT_HNDL_DVSN_CD) FROM TBDCPDCP205L S3 WHERE (S3.REQ_DVSN_CD, S3.ACP_YR, S3.CVPT_ACP_NO, S3.SRNO) IN ( SELECT S4.REQ_DVSN_CD,S4.ACP_YR,S4.CVPT_ACP_NO,MIN(S4.SRNO) FROM TBDCPDCP205L S4 WHERE A.REQ_DVSN_CD = S4.REQ_DVSN_CD AND A.ACP_YR = S4.ACP_YR AND A.CVPT_ACP_NO = S4.CVPT_ACP_NO GROUP BY S4.REQ_DVSN_CD,S4.ACP_YR,S4.CVPT_ACP_NO ) ) AS CVPT_HNLD_DVSN_NM ,TO_CHAR(D.ONR_APV_CMPT_DH,'YYYY-MM-DD') AS ONR_APV_CMPT_DH ,( SELECT TO_CHAR(TO_DATE(TO_CHAR(S2.ONR_APV_CMPT_DH, 'YYYYMMDD'), 'YYYYMMDD') + NVL( S1.USR_DFN_TXT_2, 30), 'YYYY-MM-DD') FROM TBDCMACM013C S1, TBDCPDCP205L D3, TBDCPDCP301M S2 WHERE S1.CMM_CD_DVSN_CD = '1000943' AND S1.CD <> '000000000'
AND S1.CD = D3.CVPT_HNDL_DVSN_CD AND A.REQ_DVSN_CD = D3.REQ_DVSN_CD AND A.ACP_YR = D3.ACP_YR AND A.CVPT_ACP_NO = D3.CVPT_ACP_NO AND A.REQ_DVSN_CD = S2.REQ_DVSN_CD AND A.ACP_YR = S2.ACP_YR AND A.CVPT_ACP_NO = S2.CVPT_ACP_NO AND S2.CVPT_IFM_DVSN_CD = 'C' AND S2.CVPT_OPM_APV_HNDL_STA_CD = '30' AND (D3.REQ_DVSN_CD, D3.CVPT_ACP_NO, D3.ACP_YR, D3.SRNO ) IN ( SELECT Z.REQ_DVSN_CD, Z.CVPT_ACP_NO, Z.ACP_YR, MIN(Z.SRNO) FROM TBDCPDCP205L Z WHERE A.REQ_DVSN_CD = Z.REQ_DVSN_CD AND A.CVPT_ACP_NO = Z.CVPT_ACP_NO AND A.ACP_YR = Z.ACP_YR GROUP BY Z.REQ_DVSN_CD, Z.CVPT_ACP_NO, Z.ACP_YR )) AS HNDL_LIMIT__DT ,TO_CHAR(A.ACP_DH + 30 + DECODE(( SELECT NVL(SUM(DECODE(NVL(Z3.APV_STA_CD,'0'), '30', '1', '0')),0) FROM TBDCPDCP603M Z3 WHERE Z3.ACP_YR = A.ACP_YR AND Z3.REQ_DVSN_CD = A.REQ_DVSN_CD AND Z3.CVPT_ACP_NO = A.CVPT_ACP_NO AND Z3.RPRT_CD = 'G10200100'),0,0,10),'YYYY-MM-DD') AS HNDL_LIMIT_DT_PRCS , F_CODE_NM('1000599',C.CVPT_KND_DVSN_CD) AS CVPT_KND_DVSN_NM FROM TBDCPDCP101M A ,TBDCPDCP102M B ,TBDCPDCP201M C ,TBDCPDCP301M D WHERE 1=1 AND A.REQ_DVSN_CD IN ('6','9') AND A.REQ_DVSN_CD = B.REQ_DVSN_CD AND A.ACP_YR = B.ACP_YR AND A.CVPT_ACP_NO = B.CVPT_ACP_NO AND A.REQ_DVSN_CD = C.REQ_DVSN_CD(+) AND A.ACP_YR = C.ACP_YR(+) AND A.CVPT_ACP_NO = C.CVPT_ACP_NO(+) AND A.REQ_DVSN_CD = D.REQ_DVSN_CD(+) AND A.ACP_YR = D.ACP_YR(+) AND A.CVPT_ACP_NO = D.CVPT_ACP_NO(+) AND D.CVPT_IFM_DVSN_CD(+) = 'A'
AND TO_CHAR(A.FRT_REGI_DH,'YYYYMMDD') >= #{acpDhs}
AND TO_CHAR(A.FRT_REGI_DH,'YYYYMMDD') <= #{acpDhe}
AND A.CVPT_HNDL_STA_CD = #{cvptHndlStaCd}
AND A.TIT_NM LIKE '%'||#{titNm}||'%'
AND A.HMP_APL_NO = #{hmpAplNo}
AND A.ACP_YR = #{acpYr}
AND A.RM_CVPT_ACP_NO = #{rmCvptAcpNo}
AND A.SMG_CVPT_APL_NO = #{smgCvptAplNo}
AND (A.CVPTR_NM LIKE '%'||#{cvptrNm}||'%' OR A.RPST_PEN_NM LIKE '%'||#{cvptrNm}||'%')
AND A.REQR_KND_CD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) merge_col FROM DUAL A CROSS JOIN(SELECT ','|| #{reqrKndCd} ||',' AS CODE FROM DUAL) B CONNECT BY LEVEL<=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 )
AND A.CVPT_KND_CD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) merge_col FROM DUAL A CROSS JOIN(SELECT ','|| #{cvptKndCd} ||',' AS CODE FROM DUAL) B CONNECT BY LEVEL<=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 )
AND A.CVPT_KND_DTL_CD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) merge_col FROM DUAL A CROSS JOIN(SELECT ','|| #{cvptKndDtlCd} ||',' AS CODE FROM DUAL) B CONNECT BY LEVEL<=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 )
AND A.ACP_RUT_CD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) merge_col FROM DUAL A CROSS JOIN(SELECT ','|| #{acpRutCd} ||',' AS CODE FROM DUAL) B CONNECT BY LEVEL<=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 )
AND A.MNG_TYP_CD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) merge_col FROM DUAL A CROSS JOIN(SELECT ','|| #{mngTypCd} ||',' AS CODE FROM DUAL) B CONNECT BY LEVEL<=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 )
ORDER BY A.ACP_YR DESC , A.RM_CVPT_ACP_NO DESC

/* kr.go.bai.eip.dcp.dcp.dao.DCP002001EMapper.DCP002001ECheckSelect */

	SELECT  CVPT_HNDL_STA_CD	/*진행상태*/
	         , ACP_YR
	         , REQ_DVSN_CD
	         , CVPT_ACP_NO
	FROM	TBDCPDCP101M
	WHERE	REQ_DVSN_CD = #{reqDvsnCd}
	AND		ACP_YR = #{acpYr}
	AND		CVPT_ACP_NO = #{cvptAcpNo}

