					,FNL_USR_ID = #{fnlUsrId}
   					,FNL_DEAL_DPT_CD = #{fnlDealDptCd}
   					,FNL_MNU_ID = #{fnlMnuId}
   					,FNL_MDF_DH = SYSDATE
   					,HNDL_DDLN_DT = F_GET_NEXT_WORK_DT(TO_CHAR(SYSDATE,'YYYYMMDD'),30)
   					,SCEN_CFM_EXTN_DCN =0
        			,NTF_EXTN_DCN =0



				,ACP_DH = SYSDATE
				,RM_CVPT_ACP_NO = ( SELECT
				TO_CHAR(NVL(MAX(TO_NUMBER(RM_CVPT_ACP_NO)),0)+1)
				FROM TBDCPDCP101M
				WHERE REQ_DVSN_CD = #{reqDvsnCd}
				AND ACP_YR = #{acpYr})




				,ACP_DH = SYSDATE
				,RM_CVPT_ACP_NO = ( SELECT
				TO_CHAR(NVL(MAX(TO_NUMBER(RM_CVPT_ACP_NO)),0)+1)
				FROM TBDCPDCP101M
				WHERE REQ_DVSN_CD = #{reqDvsnCd}
				AND ACP_YR = #{acpYr})



			WHERE	REQ_DVSN_CD = #{reqDvsnCd}
			AND		ACP_YR = #{acpYr}
			AND		CVPT_ACP_NO = #{cvptAcpNo}

