/* kr.go.bai.eip.dcp.dcp.dao.DCP002001EMapper.DCP002001EExcelExportSelect */ SELECT ROWNUM, C.* FROM (
SELECT DECODE(A.RM_CVPT_ACP_NO,NULL,'',A.ACP_YR||'-청금-'||A.CVPT_ACP_NO) AS ACP_NO ,TO_CHAR(A.FRT_REGI_DH,'YYYY-MM-DD') AS ACT_DH ,TO_CHAR(A.ACP_DH,'YYYY-MM-DD') AS ACP_DH ,A.TIT_NM ,DECODE(A.REQR_KND_CD,'1',A.CVPTR_NM,'2',A.RPST_PEN_NM,'3',A.RPST_PEN_NM,'4',A.CVPTR_NM) AS CVPTR_NM ,( SELECT S1.DPT_NM FROM TBDCMACM002M S1 WHERE S1.DPT_CD = A.ACP_DPT_CD ) AS ACP_DPT_NM ,F_USR_INFO_BY_ID(A.RCEP_ID,'2') AS RCEP_NM ,F_CODE_NM('1000927',A.ACP_RUT_CD) AS ACP_RUT_NM FROM TBDCPDCP101M A ,TBDCPDCP102M B WHERE 1=1 AND A.REQ_DVSN_CD = '6' AND A.REQ_DVSN_CD = B.REQ_DVSN_CD AND A.ACP_YR = B.ACP_YR AND A.CVPT_ACP_NO = B.CVPT_ACP_NO
AND TO_CHAR(A.FRT_REGI_DH,'YYYYMMDD') >= #{acpDhs}
AND TO_CHAR(A.FRT_REGI_DH,'YYYYMMDD') <= #{acpDhe}
AND A.CVPT_HNDL_STA_CD = #{cvptHndlStaCd}
AND A.TIT_NM LIKE '%'||#{titNm}||'%'
AND A.ACP_YR = #{acpYr}
AND A.CVPT_ACP_NO = #{cvptAcpNo}
AND A.SMG_CVPT_APL_NO = #{smgCvptAplNo}
AND (A.CVPTR_NM LIKE '%'||#{cvptrNm}||'%' OR A.RPST_PEN_NM LIKE '%'||#{cvptrNm}||'%')
AND A.REQR_KND_CD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) merge_col FROM DUAL A CROSS JOIN(SELECT ','|| #{reqrKndCd} ||',' AS CODE FROM DUAL) B CONNECT BY LEVEL<=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 )
AND A.CVPT_KND_CD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) merge_col FROM DUAL A CROSS JOIN(SELECT ','|| #{cvptKndCd} ||',' AS CODE FROM DUAL) B CONNECT BY LEVEL<=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 )
AND A.CVPT_KND_DTL_CD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) merge_col FROM DUAL A CROSS JOIN(SELECT ','|| #{cvptKndDtlCd} ||',' AS CODE FROM DUAL) B CONNECT BY LEVEL<=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 )
AND A.ACP_RUT_CD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) merge_col FROM DUAL A CROSS JOIN(SELECT ','|| #{acpRutCd} ||',' AS CODE FROM DUAL) B CONNECT BY LEVEL<=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 )
AND A.MNG_TYP_CD IN (SELECT SUBSTR(CODE,INSTR(CODE,',',1,LEVEL)+1,INSTR(CODE,',',1,LEVEL+1)-INSTR(CODE,',',1,LEVEL) -1) merge_col FROM DUAL A CROSS JOIN(SELECT ','|| #{mngTypCd} ||',' AS CODE FROM DUAL) B CONNECT BY LEVEL<=LENGTH(CODE)-LENGTH(REPLACE(CODE,','))-1 )
ORDER BY ACP_NO DESC
) C

