/* kr.go.bai.eip.dcp.dcp.dao.DCP002002EMapper.DCP002002EMainSelect */
SELECT A.REQ_DVSN_CD ,A.ACP_YR ,A.CVPT_ACP_NO ,DECODE(A.RM_CVPT_ACP_NO,NULL,NULL,A.ACP_YR||'-'||F_CODE_NM('1000740',A.REQ_DVSN_CD)||'-'||A.RM_CVPT_ACP_NO) AS ACP_NO ,A.RM_CVPT_ACP_NO ,A.CVPT_KND_CD ,A.CVPT_KND_DTL_CD ,A.REQR_KND_CD ,A.ACP_RUT_CD ,A.ACP_RUT_DTL_CD ,A.CVPTR_NM , SUBSTR(DGUARD.DECRYPT('TB_ENC','EIN',A.EIN),0,6) AS EIN1 , DECODE(A.EIN, NULL, ' ' , SUBSTR(DGUARD.DECRYPT('TB_ENC','EIN',A.EIN),7,1)||'******' ) AS EIN2
,A.JB_NM ,A.RPST_PEN_NM ,A.BZN ,A.CPNM_NM ,A.ZCD ,A.AD ,A.DTL_AD ,CASE WHEN A.TNB like ('%-%') THEN DECODE(A.TNB, NULL, '', SUBSTR(A.TNB, 1, INSTR(A.TNB, '-', 1, 1)-1)) ELSE SUBSTR(A.TNB, 0 , 3) END AS TNB1 , CASE WHEN LENGTH(A.TNB) = '10' THEN CASE WHEN A.TNB like ('%-%') THEN DECODE(A.TNB, NULL, '', SUBSTR(A.TNB, INSTR(A.TNB, '-', 1, 1)+1, INSTR(A.TNB, '-', 1, 2)-INSTR(A.TNB, '-', 1, 1)-1)) ELSE SUBSTR(A.TNB, 4 , 3) END ELSE CASE WHEN A.TNB like ('%-%') THEN DECODE(A.TNB, NULL, '', SUBSTR(A.TNB, INSTR(A.TNB, '-', 1, 1)+1, INSTR(A.TNB, '-', 1, 2)-INSTR(A.TNB, '-', 1, 1)-1)) ELSE SUBSTR(A.TNB, 4 , 4) END END AS TNB2, CASE WHEN LENGTH(A.TNB) = '10' THEN CASE WHEN A.TNB like ('%-%') THEN DECODE(A.TNB, NULL, '', SUBSTR(A.TNB, INSTR(A.TNB, '-', 1, 2)+1)) ELSE SUBSTR(A.TNB, 7) END ELSE CASE WHEN A.TNB like ('%-%') THEN DECODE(A.TNB, NULL, '', SUBSTR(A.TNB, INSTR(A.TNB, '-', 1, 2)+1)) ELSE SUBSTR(A.TNB, 8) END END AS TNB3 , CASE WHEN A.HPN like ('%-%') THEN DECODE(A.HPN, NULL, '', SUBSTR(A.HPN, 1, INSTR(A.HPN, '-', 1, 1)-1)) ELSE SUBSTR(A.HPN, 0 , 3) END AS HPN1 , CASE WHEN LENGTH(A.HPN) = '10' THEN CASE WHEN A.HPN like ('%-%') THEN DECODE(A.HPN, NULL, '', SUBSTR(A.HPN, INSTR(A.HPN, '-', 1, 1)+1, INSTR(A.HPN, '-', 1, 2)-INSTR(A.HPN, '-', 1, 1)-1)) ELSE SUBSTR(A.HPN, 4 , 3) END ELSE CASE WHEN A.HPN like ('%-%') THEN DECODE(A.HPN, NULL, '', SUBSTR(A.HPN, INSTR(A.HPN, '-', 1, 1)+1, INSTR(A.HPN, '-', 1, 2)-INSTR(A.HPN, '-', 1, 1)-1)) ELSE SUBSTR(A.HPN, 4 , 4) END END AS HPN2 , CASE WHEN LENGTH(A.HPN) = '10' THEN CASE WHEN A.HPN like ('%-%') THEN DECODE(A.HPN, NULL, '', SUBSTR(A.HPN, INSTR(A.HPN, '-', 1, 2)+1)) ELSE SUBSTR(A.HPN, 7) END ELSE CASE WHEN A.HPN like ('%-%') THEN DECODE(A.HPN, NULL, '', SUBSTR(A.HPN, INSTR(A.HPN, '-', 1, 2)+1)) ELSE SUBSTR(A.HPN, 8) END END AS HPN3
,A.TNB ,A.HPN ,A.EML_ID ,A.EML_DMI_NM ,A.OPEN_YN ,A.MNG_TYP_CD ,A.RSLT_NTF_WAY_CD ,A.TIT_NM ,A.ACP_DPT_CD ,( SELECT S1.DPT_NM FROM TBDCMACM002M S1 WHERE S1.DPT_CD = A.ACP_DPT_CD ) AS ACP_DPT_NM ,A.RCEP_ID ,F_USR_INFO_BY_ID(A.RCEP_ID,'2') AS RCEP_NM ,TO_CHAR(A.ACP_DH,'YYYY-MM-DD.MM.DD HH24:MI') AS ACP_DH ,A.CVPT_HNDL_STA_CD ,A.AUD_INF_TG_YN ,A.SMG_CVPT_APL_NO ,A.SMG_CVPT_ACP_NO ,A.SMG_FNL_HNDL_STA_CD ,A.SMG_CVPT_APL_DH ,A.SMG_FRT_CVPT_APL_GCC_ORG_CD ,A.SMG_CAT_HST_SRNO ,TO_CHAR(A.CVPT_WDW_DH,'YYYY-MM-DD.MM.DD HH24:MI') AS CVPT_WDW_DH ,A.CVPT_WDW_RSN ,B.CVPT_TXT ,B.CVPT_SBT ,C.CLM_NM ,C.WRK_PLAC_NM ,CASE WHEN C.TNB like ('%-%') THEN DECODE(C.TNB, NULL, '', SUBSTR(C.TNB, 1, INSTR(C.TNB, '-', 1, 1)-1)) ELSE SUBSTR(C.TNB, 0 , 3) END AS CLM_TNB1 , CASE WHEN LENGTH(C.TNB) = '10' THEN CASE WHEN C.TNB like ('%-%') THEN DECODE(C.TNB, NULL, '', SUBSTR(C.TNB, INSTR(C.TNB, '-', 1, 1)+1, INSTR(C.TNB, '-', 1, 2)-INSTR(C.TNB, '-', 1, 1)-1)) ELSE SUBSTR(C.TNB, 4 , 3) END ELSE CASE WHEN C.TNB like ('%-%') THEN DECODE(C.TNB, NULL, '', SUBSTR(C.TNB, INSTR(C.TNB, '-', 1, 1)+1, INSTR(C.TNB, '-', 1, 2)-INSTR(C.TNB, '-', 1, 1)-1)) ELSE SUBSTR(C.TNB, 4 , 4) END END AS CLM_TNB2, CASE WHEN LENGTH(C.TNB) = '10' THEN CASE WHEN C.TNB like ('%-%') THEN DECODE(C.TNB, NULL, '', SUBSTR(C.TNB, INSTR(C.TNB, '-', 1, 2)+1)) ELSE SUBSTR(C.TNB, 7) END ELSE CASE WHEN C.TNB like ('%-%') THEN DECODE(C.TNB, NULL, '', SUBSTR(C.TNB, INSTR(C.TNB, '-', 1, 2)+1)) ELSE SUBSTR(C.TNB, 8) END END AS CLM_TNB3 ,C.ZCD AS CLM_ZCD ,C.AD AS CLM_AD ,C.DTL_AD AS CLM_DTL_AD ,A.DOC_ID ,F_CODE_NM('1000934',A.CVPT_HNDL_STA_CD) AS CVPT_HNDL_STA_NM ,A.HMP_APL_NO ,NVL(A.HMP_APL_NO,A.SMG_CVPT_APL_NO) AS HMP_SMG_APL_NO ,TO_CHAR(A.CVPT_WDW_DH,'YYYY-MM-DD.MM.DD HH24:MI') AS CVPT_WDW_DH ,NVL(A.INNER_YN,'N') AS INNER_YN ,A.INNER_ORG_NM ,B.RTRN_WAY_TXT ,B.RMK_TXT ,B.OTR_ORG_NM ,B.INSPC_AGR_YN ,B.OTR_INSPC_AGR_YN
FROM TBDCPDCP101M A ,TBDCPDCP102M B ,TBDCPDCP103M C WHERE 1=1 AND A.REQ_DVSN_CD = #{reqDvsnCd} AND A.ACP_YR = #{acpYr} AND A.REQ_DVSN_CD = B.REQ_DVSN_CD AND A.ACP_YR = B.ACP_YR AND A.CVPT_ACP_NO = B.CVPT_ACP_NO AND A.REQ_DVSN_CD = C.REQ_DVSN_CD(+) AND A.ACP_YR = C.ACP_YR(+) AND A.CVPT_ACP_NO = C.CVPT_ACP_NO(+)
AND A.CVPT_ACP_NO = #{cvptAcpNo}
AND A.RM_CVPT_ACP_NO = #{rmCvptAcpNo}

