mapper.insert:
		/* kr.go.bai.eip.dcp.dcp.dao.DCP002002EMapper.DCP002002EDCP103Insert */

        	MERGE INTO 	TBDCPDCP103M A
        	USING 	(	SELECT 	#{reqDvsnCd} AS REQ_DVSN_CD
        						,#{acpYr} AS ACP_YR
        						,#{cvptAcpNo} AS CVPT_ACP_NO
        				FROM DUAL
        			) B
        	ON		(	A.REQ_DVSN_CD = B.REQ_DVSN_CD
        				AND	A.ACP_YR = B.ACP_YR
        				AND A.CVPT_ACP_NO = B.CVPT_ACP_NO
        			)
        	WHEN MATCHED THEN
        			UPDATE
        			SET		CLM_NM = #{clmNm}
        					,WRK_PLAC_NM = #{wrkPlacNm}
        					,TNB = DECODE(#{clmTnb1},NULL,'',#{clmTnb1}||'-'||#{clmTnb2}||'-'||#{clmTnb3})
        					,ZCD = #{clmZcd}
        					,AD = #{clmAd}
        					,DTL_AD = #{clmDtlAd}
        					,FNL_USR_ID = #{fnlUsrId}
        					,FNL_DEAL_DPT_CD = #{fnlDealDptCd}
        					,FNL_MNU_ID = #{fnlMnuId}
        					,FNL_MDF_DH = SYSDATE
        	WHEN NOT MATCHED THEN
		        	INSERT 		(	REQ_DVSN_CD
		        					,ACP_YR
		        					,CVPT_ACP_NO
		        					,CLM_NM
		        					,WRK_PLAC_NM
		        					,TNB
		        					,ZCD
		        					,AD
		        					,DTL_AD
		        					,FRT_USR_ID
		        					,FNL_USR_ID
		        					,FNL_DEAL_DPT_CD
		        					,FNL_MNU_ID
		        					,FRT_REGI_DH
		        					,FNL_MDF_DH
		        				)
		        		VALUES	(	#{reqDvsnCd}
									,#{acpYr}
									,#{cvptAcpNo}
									,#{clmNm}
		        					,#{wrkPlacNm}
		        					,DECODE(#{clmTnb1},NULL,'',#{clmTnb1}||'-'||#{clmTnb2}||'-'||#{clmTnb3})
		        					,#{clmZcd}
		        					,#{clmAd}
		        					,#{clmDtlAd}
									,NVL(#{frtUsrId},#{fnlUsrId})
									,#{fnlUsrId}
									,#{fnlDealDptCd}
									,#{fnlMnuId}
									,SYSDATE
									,SYSDATE
								)

