NO, D3.ACP_YR, D3.SRNO ) IN ( SELECT Z.REQ_DVSN_CD, Z.CVPT_ACP_NO, Z.ACP_YR, MIN(Z.SRNO) FROM TBDCPDCP205L Z WHERE A.REQ_DVSN_CD = Z.REQ_DVSN_CD AND A.CVPT_ACP_NO = Z.CVPT_ACP_NO AND A.ACP_YR = Z.ACP_YR GROUP BY Z.REQ_DVSN_CD, Z.CVPT_ACP_NO, Z.ACP_YR )) AS HNDL_LIMIT__DT ,TO_CHAR(A.ACP_DH + 30 + DECODE(( SELECT NVL(SUM(DECODE(NVL(Z3.APV_STA_CD,'0'), '30', '1', '0')),0) FROM TBDCPDCP603M Z3 WHERE Z3.ACP_YR = A.ACP_YR AND Z3.REQ_DVSN_CD = A.REQ_DVSN_CD AND Z3.CVPT_ACP_NO = A.CVPT_ACP_NO AND Z3.RPRT_CD = 'G10200100'),0,0,10),'YYYY-MM-DD') AS HNDL_LIMIT_DT_PRCS ,F_USR_INFO_BY_ID(C.BAI_CHGR_ID,'2') AS BAI_CHGR_NM ,CASE WHEN (SELECT COUNT(AUD_STEP_SNO) FROM TBDCPDCP207L WHERE ACP_YR = A.ACP_YR AND REQ_DVSN_CD = A.REQ_DVSN_CD AND CVPT_ACP_NO = A.CVPT_ACP_NO) > 0 THEN (SELECT COUNT(AUD_STEP_SNO) FROM TBDCPDCP207L WHERE ACP_YR = A.ACP_YR AND REQ_DVSN_CD = A.REQ_DVSN_CD AND CVPT_ACP_NO = A.CVPT_ACP_NO) || '회' END AS BZT_CNT FROM TBDCPDCP101M A ,TBDCPDCP102M B ,TBDCPDCP201M C WHERE 1=1 AND A.REQ_DVSN_CD IN ('6','9') AND A.REQ_DVSN_CD = B.REQ_DVSN_CD AND A.ACP_YR = B.ACP_YR AND A.CVPT_ACP_NO = B.CVPT_ACP_NO AND A.REQ_DVSN_CD = C.REQ_DVSN_CD(+) AND A.ACP_YR = C.ACP_YR(+) AND A.CVPT_ACP_NO = C.CVPT_ACP_NO(+)
AND A.CVPT_HNDL_STA_CD NOT IN ('10','21')
AND (A.ACP_DPT_CD = #{dptCd} OR C.HNDL_DPT_CD = #{dptCd})
AND TO_CHAR(A.ACP_DH,'YYYYMMDD') >= #{acpDhs}
AND TO_CHAR(A.ACP_DH,'YYYYMMDD') <= #{acpDhe}
AND A.CVPT_HNDL_STA_CD = #{cvptHndlStaCd}
AND A.TIT_NM LIKE '%'||#{titNm}||'%'
AND A.ACP_YR = #{acpYr}
AND A.RM_CVPT_ACP_NO = #{rmCvptAcpNo}
AND (A.CVPTR_NM LIKE '%'||#{cvptrNm}||'%' OR A.RPST_PEN_NM LIKE '%'||#{cvptrNm}||'%')
AND ( (C.HNDL_DPT_CD IS NOT NULL AND C.HNDL_DPT_CD <> #{dptCd}) OR (C.HNDL_DPT_CD = #{dptCd} AND C.HNDL_CHGR_ID IS NOT NULL) OR (A.CVPT_HNDL_STA_CD IN ('70','80')))
AND (C.HNDL_DPT_CD IS NULL OR (C.HNDL_DPT_CD = #{dptCd} AND C.HNDL_CHGR_ID IS NULL)) AND A.CVPT_HNDL_STA_CD NOT IN('70','80')
AND A.SMG_CVPT_APL_NO = #{smgCvptAplNo}
AND TO_CHAR(C.FNL_DTB_DH, 'YYYYMMDD') = #{dipDt}
ORDER BY A.ACP_YR DESC,A.RM_CVPT_ACP_NO DESC

