/* AND A.CVPTR_NM = C.CVPTR_NM AND A.HPN = C.HPN */ AND A.CVPTR_NM = C.CVPTR_NM AND (DGUARD.DECRYPT('TB_ENC','EIN',A.EIN)) = (DGUARD.DECRYPT('TB_ENC','EIN',C.EIN))
/* AND A.RPST_PEN_NM = C.RPST_PEN_NM AND A.HPN = C.HPN */ A.RPST_PEN_NM = C.RPST_PEN_NM AND (DGUARD.DECRYPT('TB_ENC','EIN',A.EIN)) = (DGUARD.DECRYPT('TB_ENC','EIN',C.EIN))
