/* AND A.CVPTR_NM = C.CVPTR_NM AND A.HPN = C.HPN */ A.CVPTR_NM = C.CVPTR_NM AND (DGUARD.DECRYPT('TB_ENC','EIN',A.EIN)) = (DGUARD.DECRYPT('TB_ENC','EIN',C.EIN))

