mapper.insert:
		/* kr.go.bai.eip.dcp.dcp.dao.DCP002004EMapper.DCP002005E201DtbMerge */

        	MERGE INTO 	TBDCPDCP201M A
        	USING 	(	SELECT 	#{reqDvsnCd} AS REQ_DVSN_CD
        						,#{acpYr} AS ACP_YR
        						,#{cvptAcpNo} AS CVPT_ACP_NO
        				FROM DUAL
        			) B
        	ON		(	A.REQ_DVSN_CD = B.REQ_DVSN_CD
        				AND	A.ACP_YR = B.ACP_YR
        				AND A.CVPT_ACP_NO = B.CVPT_ACP_NO
        			)
        	WHEN MATCHED THEN
        			UPDATE
        			SET		FNL_DTB_DPT_CD = F_USR_INFO(#{sCnb},'5')
        					,FNL_DTB_PEN_ID = F_USR_INFO(#{sCnb},'1')
        					,FNL_DTB_DH = SYSDATE
        					,HNDL_DPT_CD = #{hndlDptCd}
        					,HNDL_CHGR_ID = #{hndlChgrId}
        					,BAI_CHGR_ID = #{baiChgrId}
        					,FNL_USR_ID = #{fnlUsrId}
        					,FNL_DEAL_DPT_CD = #{fnlDealDptCd}
        					,FNL_MNU_ID = #{fnlMnuId}
        					,FNL_MDF_DH = SYSDATE
        	WHEN NOT MATCHED THEN
		        	INSERT 		(	REQ_DVSN_CD
		        					,ACP_YR
		        					,CVPT_ACP_NO
		        					,FNL_DTB_DPT_CD
		        					,FNL_DTB_PEN_ID
		        					,FNL_DTB_DH
		        					,HNDL_DPT_CD
		        					,HNDL_CHGR_ID
		        					,FRT_USR_ID
		        					,FNL_USR_ID
		        					,FNL_DEAL_DPT_CD
		        					,FNL_MNU_ID
		        					,FRT_REGI_DH
		        					,FNL_MDF_DH
		        					,BAI_CHGR_ID
		        				)
		        		VALUES	(	#{reqDvsnCd}
									,#{acpYr}
									,#{cvptAcpNo}
									,F_USR_INFO(#{sCnb},'5')
		        					,F_USR_INFO(#{sCnb},'1')
		        					,SYSDATE
		        					,#{hndlDptCd}
		        					,#{hndlChgrId}
									,F_USR_INFO(#{sCnb},'1')
									,#{fnlUsrId}
									,#{fnlDealDptCd}
									,#{fnlMnuId}
									,SYSDATE
									,SYSDATE
									,#{baiChgrId}
								)

