AND S1.DTB_DPT_CD = #{dptCd}
GROUP BY S1.REQ_DVSN_CD, S1.ACP_YR, S1.CVPT_ACP_NO) ORDER BY D1.CVPT_ACP_NO DESC

/* kr.go.bai.eip.dcp.dcp.dao.CSPDCP311Mapper.CSPDCP315PMainSelect */

	SELECT	'파일다운' AS FILE_DOWN
			,D1.ATT_FL_URL_AD
	FROM	TBDCPDCP106L D1
	WHERE	D1.REQ_DVSN_CD = #{reqDvsnCd}
	AND		D1.ACP_YR = #{acpYr}
	AND		D1.CVPT_ACP_NO = #{cvptAcpNo}
	AND		TRIM(D1.ATT_FL_URL_AD) IS NOT NULL

/* kr.go.bai.eip.dcp.dcp.dao.DCP002004EMapper.DCP002004ESelectHpn */

	SELECT	D1.HPN , F_DPT_INFO(#{hndlDptCd},'1') AS DPT_NM
	FROM	TBDCPDCP101M D1
	WHERE	D1.REQ_DVSN_CD = #{reqDvsnCd}
	AND		D1.ACP_YR = #{acpYr}
	AND		D1.CVPT_ACP_NO = #{cvptAcpNo}
