mapper.select:
/* kr.go.bai.eip.dcp.dcp.dao.DCP003013PMapper.DCP003013PMainSelect */
SELECT D1.ANS_DVSN_CD ,D1.SRNO ,D1.TIT_NM ,D1.ANS_TXT ,D2.USR_NAM ,D2.USR_ID FROM TBDCPDCP001M D1 ,TBDCMACM001M D2 WHERE 1=1 AND D1.FRT_USR_ID = D2.USR_ID(+) AND D1.ANS_DVSN_CD = #{ansDvsnCd}
AND D1.TIT_NM || D1.ANS_TXT LIKE '%'||#{sTitNm}||'%'
ORDER BY D1.SRNO


		/* kr.go.bai.eip.dcp.dcp.dao.DCP003013PMapper.DCP003013PMaxSrno */

		SELECT NVL(MAX(SRNO), 0)+1  AS MAX_SRNO
          FROM TBDCPDCP001M
         WHERE 1=1
           AND ANS_DVSN_CD = #{ansDvsnCd}

mapper.update:
	/* kr.go.bai.eip.dcp.dcp.dao.DCP003013PMapper.DCP003013PUpdate */

	UPDATE TBDCPDCP001M
       SET FNL_USR_ID         = #{fnlUsrId}
    	  ,FNL_DEAL_DPT_CD    = #{fnlDealDptCd}
    	  ,FNL_MNU_ID         = #{fnlMnuId}
          ,FNL_MDF_DH         = SYSDATE
          ,TIT_NM = #{titNm}
          ,ANS_TXT= #{ansTxt}
     WHERE ANS_DVSN_CD = #{ansDvsnCd}
       AND SRNO        = #{srno}

mapper.delete:
	/* kr.go.bai.eip.dcp.dcp.dao.DCP003013PMapper.DCP003013PDelete */


		      DELETE 
                FROM TBDCPDCP001M
               WHERE 1=1
                 AND ANS_DVSN_CD = #{ansDvsnCd}
                 AND SRNO        = #{srno}
