mapper.select:
/* kr.go.bai.eip.faa.cav.dao.FAACAV011QMapper.selectLoadSttcList */
SELECT AUD_YR ,STTC_TIT1 ,ROW_KND_CD ,VIEW_YN ,STTC_COL1 ,STTC_COL2 ,STTC_COL3 ,STTC_COL4 ,STTC_COL5 ,STTC_COL6 ,STTC_COL7 ,STTC_COL8 ,STTC_COL9 ,STTC_COL10 ,STTC_COL11 ,STTC_COL12 ,STTC_COL13 ,STTC_COL14 ,STTC_COL15 ,STTC_COL16 ,STTC_COL17 ,STTC_COL18 ,STTC_COL19 ,STTC_COL20 ,STTC_COL21 ,STTC_COL22 ,STTC_COL23 ,STTC_COL24 ,STTC_COL25 FROM ( SELECT ENF_YR AS AUD_YR, CASE WHEN KIKGU IS NULL THEN ENF_YR || '년 합계' WHEN KIKGU = '1' THEN '회계사항' WHEN KIKGU = '2' THEN '감찰사항' END AS STTC_TIT1, NVL(KIKGU, '0') AS ROW_KND_CD, CASE WHEN ENF_YR IS NULL THEN 'Y' WHEN SUBSTR(ADD_MONTHS(#{proStadDt}, -1), 0, 4) - 1 > ENF_YR THEN 'N' ELSE 'Y' END VIEW_YN, SUM(CASE WHEN CHBCD IN ('110','210','310','320','390','391','395','396','410','490','495','510','520','530','611','610','630','620','621','623','625','710','720','730') THEN NVL(GUNSU,0) ELSE 0 END) AS STTC_COL1, /*합계_건수*/ TRUNC(SUM(CASE WHEN CHBCD IN ('110','310','320','390','391','395','396','625') THEN NVL(AM1,0) ELSE 0 END)/1000000) AS STTC_COL2, /*합계_금액_위*/ TRUNC(SUM(CASE WHEN CHBCD IN ('110','310','320','390','391','395','396','625') THEN NVL(AM2,0) + NVL(AM3,0) ELSE 0 END)/1000000) AS STTC_COL3, /*합계_금액_아래 */ SUM(CASE WHEN CHBCD IN ('210','620','621','623','710','720','730') THEN NVL(MAN,0) ELSE 0 END) AS STTC_COL4, /*합계_인원 */ SUM(CASE WHEN CHBCD = '110' THEN NVL(GUNSU,0) ELSE 0 END) AS STTC_COL5, /*변상판정_건수*/ TRUNC(SUM(CASE WHEN CHBCD = '110' THEN NVL(AM1,0) ELSE 0 END)/1000000) AS STTC_COL6, /*변상판정_금액_위*/ TRUNC(SUM(CASE WHEN CHBCD = '110' THEN NVL(AM2,0) + NVL(AM3,0) ELSE 0 END)/1000000) AS STTC_COL7, /*변상판정_금액_아래*/ SUM(CASE WHEN CHBCD = '210' THEN NVL(GUNSU,0) ELSE 0 END) AS STTC_COL8, /*징계문책_건수*/ SUM(CASE WHEN CHBCD = '210' THEN NVL(MAN,0) ELSE 0 END) AS STTC_COL9, /*징계문책_인원*/ SUM(CASE WHEN CHBCD IN ('310','320','390','391','395','396') THEN NVL(GUNSU,0) ELSE 0 END) AS STTC_COL10, /*시정_건수*/ TRUNC(SUM(CASE WHEN CHBCD IN ('310','320','390','391','395','396') THEN NVL(AM1,0) ELSE 0 END)/1000000) AS STTC_COL11, /*시정_금액_위*/ TRUNC(SUM(CASE WHEN CHBCD IN ('310','320','390','391','395','396') THEN NVL(AM2,0) + NVL(AM3,0) ELSE 0 END)/1000000) AS STTC_COL12, /*시정_금액_아래*/ SUM(CASE WHEN CHBCD IN ('410','490','495') THEN NVL(GUNSU,0) ELSE 0 END) AS STTC_COL13, /*주의_건수*/ SUM(CASE WHEN CHBCD IN ('510','520','530') THEN NVL(GUNSU,0) ELSE 0 END) AS STTC_COL14, /*개선_건수*/ SUM(CASE WHEN CHBCD = '611' THEN NVL(GUNSU,0) ELSE 0 END) AS STTC_COL15, /*권고_건수*/ SUM(CASE WHEN CHBCD IN ('610','630') THEN NVL(GUNSU,0) ELSE 0 END) AS STTC_COL16, /*통보_건수 */ SUM(CASE WHEN CHBCD IN ('620','621') THEN NVL(GUNSU,0) ELSE 0 END) AS STTC_COL17, /*"통보(인사자료)_건수"*/ SUM(CASE WHEN CHBCD IN ('620','621') THEN NVL(MAN,0) ELSE 0 END) AS STTC_COL18, /*"통보(인사자료)_인원"*/ SUM(CASE WHEN CHBCD = '623' THEN NVL(GUNSU,0) ELSE 0 END) AS STTC_COL19, /*"통보(비위)_건수"*/ SUM(CASE WHEN CHBCD = '623' THEN NVL(MAN,0) ELSE 0 END) AS STTC_COL20, /*"통보(비위)_인원"*/ SUM(CASE WHEN CHBCD = '625' THEN NVL(GUNSU,0) ELSE 0 END) AS STTC_COL21, /*"통보(시정완료)_건수"*/ TRUNC(SUM(CASE WHEN CHBCD = '625' THEN NVL(AM1,0) ELSE 0 END)/1000000) AS STTC_COL22, /*"통보(시정완료)_금액_위"*/ TRUNC(SUM(CASE WHEN CHBCD = '625' THEN NVL(AM2,0) + NVL(AM3,0) ELSE 0 END)/1000000) AS STTC_COL23, /*"통보(시정완료)_금액_아래"*/ SUM(CASE WHEN CHBCD IN ('710','720','730') THEN NVL(GUNSU,0) ELSE 0 END) AS STTC_COL24, /*"고발,수사요청_건수"*/ SUM(CASE WHEN CHBCD IN ('710','720','730') THEN NVL(MAN,0) ELSE 0 END) AS STTC_COL25 /*"고발,수사요청_인원"*/ FROM (SELECT ENF_YR, KIKGU, SOKIK, (CASE WHEN CHB_CD < '930' AND CHB_CD NOT IN('390','391','800','810','820','830','840','850') THEN CHB_CD WHEN CHB_CD < '800' AND CHB_CD IN ('390','391') AND (NVL(CAM1,0) + NVL(CAM2,0) + NVL(CAM3,0)) > 0 THEN '390' WHEN CHB_CD < '800' AND CHB_CD IN ('390','391') AND (NVL(CAM1,0) + NVL(CAM2,0) + NVL(CAM3,0)) = 0 THEN '391' WHEN CHB_CD < '800' AND CHB_CD IN ('395','396') AND (NVL(CAM1,0) + NVL(CAM2,0) + NVL(CAM3,0)) > 0 THEN '395' WHEN CHB_CD < '800' AND CHB_CD IN ('395','396') AND (NVL(CAM1,0) + NVL(CAM2,0) + NVL(CAM3,0)) = 0 THEN '396' END) AS CHBCD, SUM(CASE WHEN CHB_CD IN('110','310','320','390','391','395','396','410','490','495','510','520','530','610','611','630','910','911','912','913','914','915','916','625') THEN 0 ELSE CHB_SU END) AS MAN, SUM(CASE WHEN CHB_CD IN('210','620','621', '623','710','720','730', '800','810','820','830','840','850','320','410','490','510','520','530', '610','611','630','910','911','912','913','914','915','916') THEN 0 ELSE CAM1 END) AS AM1, SUM(CASE WHEN CHB_CD IN('210','620','621', '623','710','720','730', '800','810','820','830','840','850','320','410','490','510','520','530', '610','611','630','910','911','912','913','914','915','916') THEN 0 ELSE CAM2 END) AS AM2, SUM(CASE WHEN CHB_CD IN('210','620','621', '623','710','720','730', '800','810','820','830','840','850','320','410','490','510','520','530', '610','611','630','910','911','912','913','914','915','916') THEN 0 ELSE CAM3 END) AS AM3, (NVL(SUM(CASE WHEN CHB_CD < '930' AND CHB_CD NOT IN('390','391','395','396','800','810','820','830','840','850') THEN 1 END), 0) + NVL(SUM(CASE WHEN CHB_CD < '800' AND CHB_CD IN ('390','391','395','396') AND (NVL(CAM1,0) + NVL(CAM2,0) + NVL(CAM3,0)) > 0 THEN 1 END),0) + NVL(SUM(CASE WHEN CHB_CD < '800' AND CHB_CD IN ('390','391','395','396') AND (NVL(CAM1,0) + NVL(CAM2,0) + NVL(CAM3,0)) = 0 THEN 1 END),0)) AS GUNSU
FROM (SELECT SUBSTR(DETAIL.ENF_DT, 1, 4) AS ENF_YR, SUBSTR(DETAIL.WRDO_TYP_CD, 1, 1) AS KIKGU, (SELECT MAX(RLTN_ORG_CD) FROM TBBADEAR008M SO WHERE SO.AUD_YR = DETAIL.AUD_YR AND SO.AUD_NO = DETAIL.AUD_NO AND SO.DSP_RQ_SNO = DETAIL.DSP_RQ_SNO) AS SOKIK, MASTER.DSP_RQ_KND_CD AS CHB_CD, NVL(DETAIL.NOP_CNT, 0) AS CHB_SU, NVL(DETAIL.PNL_DRIN_PSVT_AM, 0) AS CAM1 , NVL(DETAIL.RSR_AM, 0) AS CAM2 , NVL(DETAIL.SPLP_AM, 0) AS CAM3 FROM TBBADEAR001M MASTER, TBBADEAR002D DETAIL WHERE MASTER.AUD_YR = DETAIL.AUD_YR AND MASTER.AUD_NO = DETAIL.AUD_NO AND MASTER.DSP_RQ_SNO = DETAIL.DSP_RQ_SNO AND DETAIL.ENF_DT IS NOT NULL AND DETAIL.ENF_DT BETWEEN TO_DATE(TO_CHAR(ADD_MONTHS(#{proStadDt}, CASE WHEN SUBSTR(ADD_MONTHS(#{proStadDt}, -1),0,4) <> (SELECT MAX(T2.AUD_YR) FROM TWFAACAV200D T2 WHERE T2.STTC_CLSF_CD = '011' AND T2.STTC_TYP_SEQ = (SELECT MAX(T3.STTC_TYP_SEQ) FROM TWFAACAV200M T3 WHERE T3.STTC_CLSF_CD = '011' AND T3.STTC_DTM_DY IS NOT NULL)) THEN '-13' ELSE '-1' END), 'YYYY')||'-01-01') AND LAST_DAY(ADD_MONTHS(#{proStadDt}, -1)) AND DETAIL.DTM_STA_CD != '0' AND DETAIL.WRDO_TYP_CD IS NOT NULL ) A GROUP BY ENF_YR, KIKGU, SOKIK, (CASE WHEN CHB_CD < '930' AND CHB_CD NOT IN('390','391','800','810','820','830','840','850') THEN CHB_CD WHEN CHB_CD < '800' AND CHB_CD IN ('390','391') AND (NVL(CAM1,0) + NVL(CAM2,0) + NVL(CAM3,0)) > 0 THEN '390' WHEN CHB_CD < '800' AND CHB_CD IN ('390','391') AND (NVL(CAM1,0) + NVL(CAM2,0) + NVL(CAM3,0)) = 0 THEN '391' WHEN CHB_CD < '800' AND CHB_CD IN ('395','396') AND (NVL(CAM1,0) + NVL(CAM2,0) + NVL(CAM3,0)) > 0 THEN '395' WHEN CHB_CD < '800' AND CHB_CD IN ('395','396') AND (NVL(CAM1,0) + NVL(CAM2,0) + NVL(CAM3,0)) = 0 THEN '396' END)
) T1 GROUP BY ROLLUP(ENF_YR ,KIKGU) HAVING 2 - (GROUPING(ENF_YR) + GROUPING(KIKGU)) <> 0 ORDER BY ENF_YR DESC , CASE WHEN ENF_YR IS NULL THEN 1 WHEN KIKGU IS NULL THEN 2 WHEN KIKGU = '1' THEN 3 WHEN KIKGU = '2' THEN 4 END ) UNION ALL SELECT AUD_YR ,STTC_TIT1 ,ROW_KND_CD ,CASE WHEN SUBSTR(ADD_MONTHS(#{proStadDt}, -1),6,2) = '01' AND T2.AUD_YR = (SELECT TO_CHAR(MAX(T2.AUD_YR)-1) FROM TWFAACAV200D T2 WHERE T2.STTC_CLSF_CD = '011' AND T2.STTC_TYP_SEQ = (SELECT MAX(T3.STTC_TYP_SEQ) FROM TWFAACAV200M T3 WHERE T3.STTC_CLSF_CD = '011' AND T3.STTC_DTM_DY IS NOT NULL)) THEN 'N' WHEN T1.STTC_YR > T2.AUD_YR THEN 'N' ELSE 'Y' END VIEW_YN ,STTC_COL1 ,STTC_COL2 ,STTC_COL3 ,STTC_COL4 ,STTC_COL5 ,STTC_COL6 ,STTC_COL7 ,STTC_COL8 ,STTC_COL9 ,STTC_COL10 ,STTC_COL11 ,STTC_COL12 ,STTC_COL13 ,STTC_COL14 ,STTC_COL15 ,STTC_COL16 ,STTC_COL17 ,STTC_COL18 ,STTC_COL19 ,STTC_COL20 ,STTC_COL21 ,STTC_COL22 ,STTC_COL23 ,STTC_COL24 ,STTC_COL25 FROM TWFAACAV200M T1 INNER JOIN TWFAACAV200D T2 ON ( T1.STTC_TYP_CD = T2.STTC_TYP_CD AND T1.STTC_CLSF_CD = T2.STTC_CLSF_CD AND T1.STTC_TYP_SEQ = T2.STTC_TYP_SEQ ) WHERE T1.STTC_CLSF_CD = '011' AND T2.STTC_TYP_SEQ = (SELECT MAX(T3.STTC_TYP_SEQ) FROM TWFAACAV200M T3 WHERE T3.STTC_CLSF_CD = '011' AND T3.STTC_DTM_DY IS NOT NULL) AND T2.AUD_YR BETWEEN SUBSTR(ADD_MONTHS(#{proStadDt}, -49),0,4) AND SUBSTR(ADD_MONTHS(#{proStadDt},-13),0,4) ORDER BY AUD_YR DESC, ROW_KND_CD

/* kr.go.bai.eip.aep.arp.dao.FAACAV011QMapper.selectAffComboList */ SELECT STTC_TYP_SEQ AS CD, STTC_YR || '년 ' || STTC_STR_MON || '월' || DECODE(STTC_END_MON, '01', '', ' ~ ' || STTC_END_MON || '월') AS CD_NM FROM TWFAACAV200M WHERE STTC_TYP_CD = '10' AND STTC_CLSF_CD = '011'
AND STTC_DTM_DY IS NOT NULL
ORDER BY STTC_TYP_SEQ DESC

